INSERT INTO `creature_text` VALUES (82238, 9, 0, 'Пришло время для финальной схватки! Роакк Фанатичный против претендента!', 14, 0, 100, 0, 0, 43598, 84980, 0, 0, 0, 'Йорн Безумец to Газраг Малютка');
INSERT INTO `creature_text` VALUES (82238, 10, 0, 'Невероятно! В Круге Испытаний новый чемпион! Что скажешь, Токтар?', 14, 0, 100, 0, 0, 47783, 84982, 0, 0, 0, 'Йорн Безумец to Player');
INSERT INTO `creature_text` VALUES (82239, 0, 0, 'Молотобой победит без труда! ХА-ХА-ХА!', 14, 0, 100, 0, 0, 45944, 84983, 0, 0, 0, 'Токтар Железный Череп to Газраг Малютка');
INSERT INTO `creature_text` VALUES (82239, 1, 0, 'Очевидно, это просто случайность! Я в этом уверен, Йорн!', 14, 0, 100, 0, 0, 45945, 84984, 0, 0, 0, 'Токтар Железный Череп to Player');
INSERT INTO `creature_text` VALUES (82239, 2, 0, 'Она сражается как львица, когда голодна, а мы ее неделю не кормили! Ха-ха!', 14, 0, 100, 0, 0, 45946, 84985, 0, 0, 0, 'Токтар Железный Череп to Газраг Малютка');
INSERT INTO `creature_text` VALUES (82239, 3, 0, 'Да, слаба! Но следующий бой будет интереснее!', 14, 0, 100, 0, 0, 45947, 84986, 0, 0, 0, 'Токтар Железный Череп to Player');
INSERT INTO `creature_text` VALUES (82239, 4, 0, 'Хе\'йилаа убил предыдущего соперника еще до удара гонга!', 14, 0, 100, 0, 0, 45948, 84987, 0, 0, 0, 'Токтар Железный Череп to Газраг Малютка');
INSERT INTO `creature_text` VALUES (82239, 5, 0, 'После боя с участием капитана Бумшмяка зрителей ждет отличное барбекю!', 14, 0, 100, 0, 0, 45950, 84989, 0, 0, 0, 'Токтар Железный Череп to Газраг Малютка');
INSERT INTO `creature_text` VALUES (82239, 6, 0, 'Опа! Похоже, барбекю сделали из капитана Бумшмяка! Оставьте мне кусочек!', 14, 0, 100, 0, 0, 45951, 84990, 0, 0, 0, 'Токтар Железный Череп to Player');
INSERT INTO `creature_text` VALUES (82239, 7, 0, 'Вул\'гор – единственный, кто может победить Роакка! Этот бой закончится, еще не начавшись!', 14, 0, 100, 0, 0, 45952, 84991, 0, 0, 0, 'Токтар Железный Череп to Газраг Малютка');
INSERT INTO `creature_text` VALUES (82239, 8, 0, 'Ой, подумаешь. Посмотрим, чем закончится следующий бой!', 14, 0, 100, 0, 0, 45949, 84988, 0, 0, 0, 'Токтар Железный Череп to Газраг Малютка');
INSERT INTO `creature_text` VALUES (82239, 9, 0, 'Подумаешь! Новому чемпиону Круга все равно не совладать с Вул\'гором!', 14, 0, 100, 0, 0, 45953, 84993, 0, 0, 0, 'Токтар Железный Череп to Player');
INSERT INTO `creature_text` VALUES (82240, 2, 0, 'My spirit... is free...', 12, 0, 100, 0, 0, 0, 91243, 0, 0, 0, 'Coerced Villager to Player');
INSERT INTO `creature_text` VALUES (82240, 2, 0, 'Мой дух... свободен...', 12, 0, 100, 0, 0, 0, 91243, 0, 0, 0, 'Пленный житель to Player');
INSERT INTO `creature_text` VALUES (82240, 2, 0, 'Странно... никакой боли...', 12, 0, 100, 0, 0, 0, 91242, 0, 0, 0, 'Пленный житель to Player');
INSERT INTO `creature_text` VALUES (82240, 2, 0, 'Так... холодно...', 12, 0, 100, 0, 0, 0, 115884, 0, 0, 0, 'Пленный житель to Player');
INSERT INTO `creature_text` VALUES (82240, 3, 0, 'Предатели!', 12, 0, 100, 0, 0, 0, 119767, 426000, 426000, 0, 'Пленный житель');
INSERT INTO `creature_text` VALUES (82240, 4, 0, 'I ... rest in peace ...', 12, 0, 100, 0, 0, 0, 91244, 7000, 7000, 0, 'Пленный житель to Player');
INSERT INTO `creature_text` VALUES (82244, 1, 0, 'Нужно сжигать трупы, пока есть такая возможность!', 12, 0, 100, 0, 0, 0, 84101, 0, 0, 0, 'Доральд Килнхарт to Player');
INSERT INTO `creature_text` VALUES (82244, 3, 0, 'Пепел к пеплу.', 12, 0, 100, 0, 0, 0, 90453, 569000, 569000, 165487, 'Доральд Килнхарт to Player');
INSERT INTO `creature_text` VALUES (82244, 4, 0, 'Жги все, пока еще есть время!', 12, 0, 100, 0, 0, 0, 84099, 13000, 732000, 0, 'Доральд Килнхарт to Player');
INSERT INTO `creature_text` VALUES (82247, 1, 0, 'Еще одна темная душа для эшафота.', 12, 0, 100, 0, 0, 0, 84102, 0, 0, 0, 'Наз Данберлин to Player');
INSERT INTO `creature_text` VALUES (82247, 1, 0, 'Непослушание карается смертью.', 12, 0, 100, 0, 0, 0, 84104, 0, 0, 0, 'Наз Данберлин to Player');
INSERT INTO `creature_text` VALUES (82247, 3, 0, 'Будет больно, но недолго.', 12, 0, 100, 0, 0, 0, 84103, 9000, 9000, 0, 'Наз Данберлин to Player');
INSERT INTO `creature_text` VALUES (82248, 0, 0, 'This is the final resting place of some of my companions who were killed by ogron.', 12, 0, 100, 0, 0, 0, 84072, 0, 0, 0, 'Rexxar to Player');
INSERT INTO `creature_text` VALUES (82248, 1, 0, 'Visha, newly born and not yet ready for combat.', 12, 0, 100, 1, 0, 0, 84073, 0, 0, 0, 'Rexxar to Player');
INSERT INTO `creature_text` VALUES (82248, 2, 0, 'Qisha, still young but showing great promise.', 12, 0, 100, 274, 0, 0, 84074, 0, 0, 0, 'Rexxar to Player');
INSERT INTO `creature_text` VALUES (82248, 3, 0, 'And finally, the dearest of them all, Latisha.', 12, 0, 100, 113, 0, 0, 84075, 0, 0, 0, 'Rexxar to Player');
INSERT INTO `creature_text` VALUES (82248, 4, 0, 'Were it not for you, this would be Nisha\'s resting place as well.', 12, 0, 100, 0, 0, 0, 85982, 0, 0, 0, 'Rexxar to Player');
INSERT INTO `creature_text` VALUES (82248, 5, 0, 'Nisha is willing to take you to the den of the ogron if you are in need.', 12, 0, 100, 0, 0, 0, 87596, 0, 0, 0, 'Rexxar to Player');
INSERT INTO `creature_text` VALUES (82263, 3, 0, 'Мы сражаемся за Азерот!', 12, 0, 100, 0, 0, 0, 84094, 79491, 79491, 0, 'Агиос Светоносец');
INSERT INTO `creature_text` VALUES (82263, 4, 0, 'Нужно... продолжать... бой...', 12, 0, 100, 0, 0, 44860, 84092, 6000, 6000, 0, 'Агиос Светоносец');
INSERT INTO `creature_text` VALUES (82264, 3, 0, 'Нужно... продолжать... бой...', 12, 0, 100, 0, 0, 0, 84092, 489000, 489000, 0, 'Джонни Ошимо');
INSERT INTO `creature_text` VALUES (82270, 0, 0, 'Нам не выиграть это сражение, если мы не выведем из строя боевые машины Железной Орды.', 12, 0, 100, 0, 0, 0, 84772, 0, 0, 0, 'Воздаятель Мараад to Player');
INSERT INTO `creature_text` VALUES (82270, 3, 0, 'We cannot win this battle if the enemy is always one step ahead of us.', 12, 0, 100, 0, 0, 0, 84774, 726578, 726578, 0, 'Vindicator Maraad to Player');
INSERT INTO `creature_text` VALUES (82270, 3, 0, 'We cannot win this battle if those Iron Horde war machines remain operational.', 12, 0, 100, 0, 0, 0, 84772, 20015, 20015, 0, 'Vindicator Maraad to Player');
INSERT INTO `creature_text` VALUES (82270, 3, 0, 'With those scouts dead we have a greater chance of catching the Iron Horde by surprise. Excellent work, $n.', 12, 0, 100, 0, 0, 0, 84776, 879875, 879875, 0, 'Vindicator Maraad to Player');
INSERT INTO `creature_text` VALUES (82270, 3, 0, 'Лишив Железную Орду разведчиков, мы сможем застать ее врасплох. Отличная работа, $n.', 12, 0, 100, 0, 0, 0, 84776, 76813, 76813, 0, 'Воздаятель Мараад to Player');
INSERT INTO `creature_text` VALUES (82270, 3, 0, 'Мы не сможем одержать победу, если противник все время будет на шаг впереди нас.', 12, 0, 100, 0, 0, 0, 84774, 14016, 14016, 0, 'Воздаятель Мараад to Player');
INSERT INTO `creature_text` VALUES (82274, 0, 0, 'Я больше не могу убивать этих ботани в одиночку. Я иду с тобой!', 12, 0, 100, 5, 0, 0, 84097, 0, 0, 0, 'Резатель to Player');
INSERT INTO `creature_text` VALUES (82275, 0, 0, 'Назад! Поищем жертв для Нер\'зула где-нибудь еще.', 12, 0, 100, 0, 0, 45848, 84098, 0, 0, 0, 'Темный ревнитель из клана Призрачной Луны to Player');
INSERT INTO `creature_text` VALUES (82275, 3, 0, 'Pull back! We must find Ner\'zhul his sacrifices elsewhere.', 12, 0, 100, 0, 0, 45848, 84098, 94, 94, 0, 'Shadowmoon Dark Zealot to Player');
INSERT INTO `creature_text` VALUES (82277, 0, 0, 'We need to help Taylor!', 12, 0, 100, 396, 0, 0, 84113, 0, 0, 0, 'Sir Harris to Player');
INSERT INTO `creature_text` VALUES (82278, 0, 0, 'I... I remember...', 12, 0, 100, 396, 0, 0, 84112, 0, 0, 0, 'Lady Claudia to Player');
INSERT INTO `creature_text` VALUES (82287, 0, 0, 'What is going on here? The entire mushroom forest is riled up.', 12, 0, 100, 5, 0, 45892, 84142, 0, 0, 0, 'Exarch Naielle to Player');
INSERT INTO `creature_text` VALUES (82287, 1, 0, 'Ты что, опять погрузил кристаллы-резонаторы в землю? От этого вся живность с ума сходит!', 12, 0, 100, 5, 0, 45893, 84143, 0, 0, 0, 'Exarch Naielle to Player');
INSERT INTO `creature_text` VALUES (82287, 2, 0, 'Когда ты поймешь, что этот мир не похож на Аргус?', 12, 0, 100, 5, 0, 45894, 84144, 0, 0, 0, 'Экзарх Найель to Player');
INSERT INTO `creature_text` VALUES (82288, 0, 0, 'Stop that thing! Bring it down!', 12, 0, 100, 5, 0, 9953, 84135, 0, 0, 0, 'Exarch Hataaru to Player');
INSERT INTO `creature_text` VALUES (82288, 1, 0, 'Stay in your swamp, Rangari. I will handle this.', 12, 0, 100, 1, 0, 46754, 84136, 0, 0, 0, 'Exarch Hataaru to Player');
INSERT INTO `creature_text` VALUES (82296, 3, 0, 'I\'ll take care of everything, $p!', 12, 0, 100, 0, 0, 0, 86761, 2000, 2000, 0, 'Рожебой to Player');
INSERT INTO `creature_text` VALUES (82302, 3, 0, 'Rest Dionor, I am sorry it took so long...', 12, 0, 100, 0, 0, 0, 84195, 76843, 76843, 0, 'Birchus to Player');
INSERT INTO `creature_text` VALUES (82302, 3, 0, 'Спи спокойно, Дионор. Прости, что я не смог сделать этого раньше...', 12, 0, 100, 0, 0, 0, 84195, 38314, 38314, 0, 'Березус to Player');
INSERT INTO `creature_text` VALUES (82303, 3, 0, '*задыхается*... *кашляет* Спасибо, что $gвытащил:вытащила; меня оттуда!', 12, 0, 100, 5, 0, 0, 84133, 1000, 1000, 0, 'Низзикс Нетеряй to Player');
INSERT INTO `creature_text` VALUES (82303, 3, 0, 'Пока, $gдружище:подруга;!', 12, 0, 100, 70, 0, 0, 84140, 32000, 32000, 0, 'Низзикс Нетеряй to Player');
INSERT INTO `creature_text` VALUES (82303, 3, 0, 'Проклятый люк захлопнулся! Вот уж повезло так повезло: спастись от зараженных и застрять в сломанной спасательной капсуле.', 12, 0, 100, 396, 0, 0, 84134, 6000, 6000, 0, 'Низзикс Нетеряй to Player');
INSERT INTO `creature_text` VALUES (82303, 3, 0, 'Слушай, я в долгу перед тобой. Прежде чем свалить из города, я прихватил вот эту штуку. Наверно, тебе она пригодится больше, чем мне.', 12, 0, 100, 396, 0, 0, 84139, 22000, 22000, 0, 'Низзикс Нетеряй to Player');
INSERT INTO `creature_text` VALUES (82303, 3, 0, 'Ты меня $gвытащил:вытащила;! БАЦ! Ох, убил... сам себя убил... ДВОЙНОЙ БАЦ! ...Потому что я застрял в спасательной капсуле... Ну, ты понимаешь.', 12, 0, 100, 396, 0, 0, 84137, 12000, 12000, 0, 'Низзикс Нетеряй to Player');
INSERT INTO `creature_text` VALUES (82314, 0, 0, 'I will tear you and your garrison to shreds.', 12, 0, 100, 0, 0, 0, 85974, 0, 0, 0, 'Soulscythe to Player');
INSERT INTO `creature_text` VALUES (82314, 1, 0, 'A soldier\'s duty... is never done.', 12, 0, 100, 0, 0, 0, 85975, 0, 0, 0, 'Soulscythe to 0');
INSERT INTO `creature_text` VALUES (82314, 2, 0, 'Your time... will come.', 12, 0, 100, 0, 0, 0, 85976, 0, 0, 0, 'Soulscythe to Player');
INSERT INTO `creature_text` VALUES (82317, 0, 0, 'I think I broke every bone in my body. I think I even broke some bones that we don\'t have names for!', 12, 0, 100, 0, 0, 0, 83221, 0, 0, 0, 'Injured Crewman to Player');
INSERT INTO `creature_text` VALUES (82317, 0, 1, 'Thanks for the patch-up! Since you saved me here\'s some advice, stay away from Pinchwhistle Point.', 12, 0, 100, 0, 0, 0, 83791, 0, 0, 0, 'Injured Crewman to Player');
INSERT INTO `creature_text` VALUES (82317, 0, 2, 'That was a REAAAAAALY nice explosion! Wish I wasn\'t in the middle of it.', 12, 0, 100, 0, 0, 0, 83220, 0, 0, 0, 'Injured Crewman to Player');
INSERT INTO `creature_text` VALUES (82317, 0, 3, 'Pinchwhistle Point is crawling with the infected, I... I think the town\'s a lost cause.', 12, 0, 100, 0, 0, 0, 83792, 0, 0, 0, 'Injured Crewman to Player');
INSERT INTO `creature_text` VALUES (82317, 0, 4, 'Pinchwhistle Point is completely lost. We only saw one guy who didn\'t look infected but we lost control of the zeppelin before we could make contact.', 12, 0, 100, 0, 0, 0, 83793, 0, 0, 0, 'Injured Crewman to Player');
INSERT INTO `creature_text` VALUES (82320, 0, 0, 'You have heart, Taylor. I\'ll give you that.', 12, 0, 100, 1, 0, 0, 84152, 0, 0, 0, 'Ephial to Player');
INSERT INTO `creature_text` VALUES (82320, 1, 0, 'Now, Admiral, it is time to finish what I started.', 12, 0, 100, 1, 0, 0, 84154, 0, 0, 0, 'Ephial to Player');
INSERT INTO `creature_text` VALUES (82320, 2, 0, 'Rise, and carry out my will!', 14, 0, 100, 0, 0, 31957, 84181, 0, 0, 0, 'Ephial to Player');
INSERT INTO `creature_text` VALUES (82320, 3, 0, 'Very well. I\'ll finish you myself.', 12, 0, 100, 1, 0, 0, 84180, 0, 0, 0, 'Ephial to Player');
INSERT INTO `creature_text` VALUES (82320, 4, 0, 'I turned the Admiral\'s \"followers\" against him. Perhaps I shall pay your garrison a visit next?', 12, 0, 100, 0, 0, 0, 85978, 0, 0, 0, 'Ephial to 0');
INSERT INTO `creature_text` VALUES (82320, 5, 0, 'The dark one promised me so much... if I succeeded...', 12, 0, 100, 0, 0, 0, 85979, 0, 0, 0, 'Ephial to Player');
INSERT INTO `creature_text` VALUES (82320, 6, 0, 'Полагаю, больше врать нет смысла.', 12, 0, 100, 0, 0, 0, 84153, 0, 0, 0, 'Ephial to Player');
INSERT INTO `creature_text` VALUES (82324, 0, 0, 'Генезотавр приближается. Приготовься!', 14, 0, 100, 0, 0, 0, 84155, 0, 0, 0, 'Резатель to Player');
INSERT INTO `creature_text` VALUES (82328, 0, 0, '%s is about to burst!', 16, 0, 100, 0, 0, 0, 84218, 0, 0, 0, 'Volatile Spore to 0');
INSERT INTO `creature_text` VALUES (82328, 9, 0, '%s скоро лопнут!', 16, 0, 100, 0, 0, 0, 84218, 0, 0, 0, 'Нестабильные споры to 0');
INSERT INTO `creature_text` VALUES (82337, 0, 0, 'Мы будем сражаться вместе.', 14, 0, 100, 0, 0, 44745, 86208, 0, 0, 0, 'Дрека to Ию');
INSERT INTO `creature_text` VALUES (82338, 4, 0, 'Да! Спали их… сестра.', 14, 0, 100, 0, 0, 46174, 86211, 39624, 39624, 165778, 'Каз Визгунья to Ию');
INSERT INTO `creature_text` VALUES (82339, 0, 0, 'Помоги Дреке и Каз! Дрека сказала, что я могу стрелять из большой пушки. С этой дворфской штукой я порву тварь в клочья.', 14, 0, 100, 0, 0, 0, 84156, 0, 0, 0, 'Резатель to Player');
INSERT INTO `creature_text` VALUES (82363, 3, 0, 'I will seek out Exarchs Akama and Maladaar. Good luck, Commander.', 12, 0, 100, 1, 0, 44808, 84167, 49094, 49094, 0, 'Vindicator Maraad to Player');
INSERT INTO `creature_text` VALUES (82363, 3, 0, 'Your laborers are hard at work, Commander. Soon your garrison will be well-stocked with food.', 12, 0, 100, 1, 0, 44807, 84166, 9047, 9047, 0, 'Vindicator Maraad to Player');
INSERT INTO `creature_text` VALUES (82363, 3, 0, 'Твои рабочие усердно трудятся. Скоро в гарнизоне будет достаточно еды.', 12, 0, 100, 1, 0, 44807, 84166, 9000, 9000, 0, 'Воздаятель Мараад to Player');
INSERT INTO `creature_text` VALUES (82363, 3, 0, 'Я найду экзархов Акаму и Маладаара. Удачи, командир.', 12, 0, 100, 1, 0, 44808, 84167, 62000, 62000, 0, 'Воздаятель Мараад to Player');
INSERT INTO `creature_text` VALUES (82372, 1, 0, 'Кто мешает нам собирать урожай?', 12, 0, 100, 0, 0, 0, 87801, 0, 0, 123964, 'Онтоген Жнец to Player');
INSERT INTO `creature_text` VALUES (82372, 3, 0, 'Who interrupts the harvest?', 12, 0, 100, 0, 0, 0, 87801, 3359, 3359, 0, 'Ontogen the Harvester to Player');
INSERT INTO `creature_text` VALUES (82374, 3, 0, 'Глупец.', 12, 0, 100, 0, 0, 0, 84192, 57000, 57000, 0, 'Раи\'вош to Player');
INSERT INTO `creature_text` VALUES (82374, 3, 0, 'Ты играешь с силами, которые находятся за пределами твоего понимания.', 12, 0, 100, 0, 0, 0, 84193, 60000, 60000, 0, 'Раи\'вош to Player');
INSERT INTO `creature_text` VALUES (82375, 0, 0, 'You\'ve done it. We are free!', 12, 0, 100, 0, 0, 0, 84182, 0, 0, 0, 'Admiral Taylor to Player');
INSERT INTO `creature_text` VALUES (82375, 1, 0, 'У тебя все получилось. Мы свободны!', 12, 0, 100, 0, 0, 0, 84182, 0, 0, 0, 'Адмирал Тейлор to Player');
INSERT INTO `creature_text` VALUES (82402, 0, 0, 'Все в порядке, командир?', 12, 0, 100, 6, 0, 0, 84198, 0, 0, 0, 'Темный охотник Укамбе to Player');
INSERT INTO `creature_text` VALUES (82402, 1, 0, 'I will help the admiral find the way to his. See you at the camp.', 12, 0, 100, 396, 0, 0, 84228, 0, 0, 0, 'Темный охотник Укамбе to Player');
INSERT INTO `creature_text` VALUES (82403, 0, 0, 'Commander, are you alright?', 12, 0, 100, 5, 0, 0, 84197, 0, 0, 0, 'Jasper Fel to Player');
INSERT INTO `creature_text` VALUES (82403, 1, 0, 'I\'ll guide Admiral Taylor back to camp. See you there.', 12, 0, 100, 396, 0, 0, 84227, 0, 0, 0, 'Jasper Fel to Player');
INSERT INTO `creature_text` VALUES (82404, 3, 0, 'Attack!', 14, 0, 100, 0, 0, 0, 139449, 47, 47, 0, 'Stormwind Marine to Player');
INSERT INTO `creature_text` VALUES (82404, 3, 0, 'For Nethergarde!', 14, 0, 100, 0, 0, 0, 84769, 47, 47, 0, 'Stormwind Marine to Player');
INSERT INTO `creature_text` VALUES (82404, 3, 0, 'For the Alliance!', 14, 0, 100, 0, 0, 37730, 146212, 110, 110, 0, 'Stormwind Marine to Player');
