INSERT INTO `creature_text` VALUES (82415, 1, 0, 'Шинри бросается в бегство при виде вас!', 41, 0, 100, 0, 0, 0, 86510, 0, 0, 167009, 'Шинри to Player');
INSERT INTO `creature_text` VALUES (82416, 3, 0, 'И зачем я надел красную рубашку...', 12, 0, 100, 0, 0, 0, 84290, 25000, 25000, 138767, 'Гоблин-землекоп to Player');
INSERT INTO `creature_text` VALUES (82416, 3, 0, 'Идет ответная реакция! Бластер не справляется!', 12, 0, 100, 0, 0, 0, 84289, 20000, 20000, 0, 'Гоблин-землекоп to Player');
INSERT INTO `creature_text` VALUES (82432, 3, 0, 'Get out of here! Only staff is allowed in the larder.', 12, 0, 100, 0, 0, 4415, 84225, 1034047, 1034047, 0, 'Miril Dumonde to Player');
INSERT INTO `creature_text` VALUES (82432, 3, 0, 'Get out of here! В кладовую допускается исключительно обслуга. ', 12, 0, 100, 0, 0, 0, 84225, 801000, 801000, 0, 'Мирил Дюмонд to Player');
INSERT INTO `creature_text` VALUES (82450, 3, 0, 'Бери стул, присаживайся! Бургеры почти готовы, босс!', 12, 0, 100, 1, 0, 0, 84246, 12000, 12000, 0, 'Пилот планера Хитрой Шестеренки to Player');
INSERT INTO `creature_text` VALUES (82459, 0, 0, 'Smuggling is my business.... and business is good.', 12, 0, 100, 0, 0, 0, 84257, 0, 0, 0, 'Honest Jim to Player');
INSERT INTO `creature_text` VALUES (82459, 0, 0, 'Ну конечно же! У меня найдется нечто особенное как раз для тебя.', 12, 0, 100, 0, 0, 0, 84259, 0, 0, 0, 'Честный Джим to Player');
INSERT INTO `creature_text` VALUES (82459, 0, 0, 'Я занимаюсь контрабандой... и дела пока идут неплохо.', 12, 0, 100, 0, 0, 0, 84257, 0, 0, 0, 'Честный Джим to Player');
INSERT INTO `creature_text` VALUES (82459, 3, 0, 'Редкие товары! Только у меня!', 12, 0, 100, 0, 0, 0, 84258, 109, 109, 0, 'Честный Джим to Player');
INSERT INTO `creature_text` VALUES (82460, 0, 0, 'This madness ends now, Gar\'mak!', 14, 0, 100, 0, 0, 0, 84766, 0, 0, 0, 'Vindicator Maraad to Gar\'mak Bladetwist');
INSERT INTO `creature_text` VALUES (82460, 0, 0, 'Это безумие закончится здесь и сейчас, Гар\'мак!', 14, 0, 100, 0, 0, 0, 84766, 0, 0, 0, 'Воздаятель Мараад to Гар\'мак Фехтовальщик');
INSERT INTO `creature_text` VALUES (82460, 4, 0, 'Gar\'mak is dead. You fought well today. Meet me back at camp.', 12, 0, 100, 396, 0, 0, 84765, 19797, 19797, 0, 'Vindicator Maraad');
INSERT INTO `creature_text` VALUES (82460, 4, 0, 'Гар\'мак мертв. Это была достойная битва. Увидимся в лагере.', 12, 0, 100, 396, 0, 0, 84765, 17000, 17000, 0, 'Воздаятель Мараад');
INSERT INTO `creature_text` VALUES (82461, 3, 0, 'Ah, traveler, did you expect more from a humble artist? A trinket perhaps? Coin, or a keepsake?', 12, 0, 100, 0, 0, 0, 84266, 3894578, 3894578, 0, 'Painter Mikkal to Player');
INSERT INTO `creature_text` VALUES (82461, 3, 0, 'Just look in front of you. The true treasure is the beauty of this moment.', 12, 0, 100, 0, 0, 0, 84267, 3898625, 3898625, 0, 'Painter Mikkal to Player');
INSERT INTO `creature_text` VALUES (82462, 3, 0, 'Босс! Кажется, пространство вокруг этого артефакта искажается!', 12, 0, 100, 0, 0, 0, 84287, 9000, 9000, 0, 'Гоблин-землекоп to Player');
INSERT INTO `creature_text` VALUES (82463, 3, 0, 'Аномалию нужно нейтрализовать. Перевести археологические бластеры в режим рассеивания!', 12, 0, 100, 0, 0, 0, 84288, 12000, 12000, 0, 'Газмольф Прохлаждайс to Player');
INSERT INTO `creature_text` VALUES (82463, 3, 0, 'Бластеры на полную мощность!', 12, 0, 100, 0, 0, 0, 84291, 25000, 25000, 0, 'Газмольф Прохлаждайс to Player');
INSERT INTO `creature_text` VALUES (82463, 3, 0, 'Вот ЭТО и есть настоящая археология!', 12, 0, 100, 0, 0, 0, 84449, 34000, 34000, 0, 'Газмольф Прохлаждайс to Player');
INSERT INTO `creature_text` VALUES (82463, 3, 0, 'Ну разумеется! Небольшая доза космической радиации еще никому не повредила!', 12, 0, 100, 0, 0, 0, 84457, 51000, 51000, 0, 'Газмольф Прохлаждайс to Player');
INSERT INTO `creature_text` VALUES (82463, 3, 0, 'Так, братва! Пора заняться археологией!', 12, 0, 100, 0, 0, 0, 84286, 2000, 2000, 0, 'Газмольф Прохлаждайс to Player');
INSERT INTO `creature_text` VALUES (82463, 3, 0, 'Уходим! Встретимся в лагере, $n.', 12, 0, 100, 1, 0, 0, 84454, 43000, 43000, 0, 'Газмольф Прохлаждайс to Player');
INSERT INTO `creature_text` VALUES (82465, 3, 0, 'Эй, босс, а эта штука точно не опасна?', 12, 0, 100, 0, 0, 0, 84450, 47000, 47000, 0, 'Гоблин-землекоп to Player');
INSERT INTO `creature_text` VALUES (82476, 3, 0, '<coughs> What foul stench!  I don\'t think I got a full breath of it though...', 12, 0, 100, 15, 0, 0, 84495, 203, 203, 0, 'Khaano to Player');
INSERT INTO `creature_text` VALUES (82476, 3, 0, '<кашляет> Ну и вонь! Надеюсь, я не задохнусь до смерти...', 12, 0, 100, 15, 0, 0, 84495, 1000, 1000, 0, 'Хаано to Player');
INSERT INTO `creature_text` VALUES (82486, 1, 0, 'Не-е-ет! Только не \"Вибсон\"! Это все, что у меня осталось!', 12, 0, 100, 0, 0, 0, 84322, 0, 0, 0, 'Исследователь Ноззанд to Player');
INSERT INTO `creature_text` VALUES (82486, 2, 0, 'Отомсти за меня, \"Вибсон\". Отомсти за меня...', 12, 0, 100, 0, 0, 0, 84323, 0, 0, 174896, 'Исследователь Ноззанд to Player');
INSERT INTO `creature_text` VALUES (82486, 4, 0, '\"Вибсон\", давай свое огненное дыхание!', 12, 0, 100, 0, 0, 0, 89390, 32000, 50000, 174908, 'Исследователь Ноззанд to Player');
INSERT INTO `creature_text` VALUES (82486, 4, 0, '\"Вибсон\", мощный удар, живо!', 12, 0, 100, 0, 0, 0, 89391, 39000, 39000, 0, 'Исследователь Ноззанд to Player');
INSERT INTO `creature_text` VALUES (82486, 4, 0, '\"Вибсон\", огонь!', 12, 0, 100, 0, 0, 0, 89386, 11000, 11000, 0, 'Исследователь Ноззанд to Player');
INSERT INTO `creature_text` VALUES (82486, 4, 0, 'Сами напросились... \"ВИБСОН\"! ПОКАЖИ ИМ!', 14, 0, 100, 0, 0, 0, 89388, 23000, 23000, 0, 'Исследователь Ноззанд to Player');
INSERT INTO `creature_text` VALUES (82486, 4, 0, 'Спаси меня, \"Вибсон\"!', 12, 0, 100, 0, 0, 0, 89387, 16000, 16000, 174895, 'Исследователь Ноззанд');
INSERT INTO `creature_text` VALUES (82487, 0, 0, 'Мой клинок к твоим услугам.', 12, 0, 100, 0, 0, 0, 87956, 0, 0, 0, 'Шелли Хэмби to Player');
INSERT INTO `creature_text` VALUES (82487, 3, 0, 'My blade is at your call.', 12, 0, 100, 0, 0, 0, 87956, 102344, 440172, 0, 'Shelly Hamby to Player');
INSERT INTO `creature_text` VALUES (82487, 3, 0, 'Командир, убить кого-нибудь нужно?', 12, 0, 100, 0, 0, 0, 87954, 3194000, 3194000, 0, 'Шелли Хэмби to Player');
INSERT INTO `creature_text` VALUES (82487, 3, 0, 'Отправь меня на фронт, командир.', 12, 0, 100, 0, 0, 0, 87955, 39000, 39000, 0, 'Шелли Хэмби to Player');
INSERT INTO `creature_text` VALUES (82489, 3, 0, 'Твоим соратникам бы снаряжение получше не помешало. Сделаю, что смогу.', 12, 0, 100, 0, 0, 48543, 91019, 184687, 184687, 0, 'Ученик механолога Андрен to Player');
INSERT INTO `creature_text` VALUES (82491, 3, 0, 'I comb the perimeter on a night shift.', 12, 0, 100, 0, 0, 0, 90997, 368000, 368000, 0, 'Рангари Шел to Player');
INSERT INTO `creature_text` VALUES (82491, 3, 0, 'Предлагаю организовать несколько снайперских засад на деревьях вокруг нашей крепости.', 12, 0, 100, 0, 0, 42439, 90995, 67000, 67000, 0, 'Рангари Шел to Player');
INSERT INTO `creature_text` VALUES (82491, 3, 0, 'Рангари к твоим услугам.', 12, 0, 100, 0, 0, 0, 90994, 338000, 338000, 0, 'Рангари Шел to Player');
INSERT INTO `creature_text` VALUES (82491, 3, 0, 'Эти деревья – отличная маскировка, но они могут сыграть против нас, скрыв наступающих. Нужно быть начеку.', 12, 0, 100, 0, 0, 0, 90998, 178000, 178000, 0, 'Рангари Шел to Player');
INSERT INTO `creature_text` VALUES (82492, 0, 0, 'В хаосе войны твой бастион – единственный оплот спокойствия. Спасибо, командир!', 12, 0, 100, 0, 0, 0, 91049, 0, 0, 0, 'Воздаятельница Онаала to Player');
INSERT INTO `creature_text` VALUES (82493, 3, 0, 'Sappy\'s feeder?', 12, 0, 100, 0, 0, 0, 84324, 5047, 5047, 0, 'Sappy to Player');
INSERT INTO `creature_text` VALUES (82493, 3, 0, 'Ты кормить Росточек?', 12, 0, 100, 0, 0, 0, 84324, 8426000, 8426000, 0, 'Росточек to Player');
INSERT INTO `creature_text` VALUES (82495, 0, 0, 'Чем я могу помочь тебе сегодня?', 12, 0, 100, 0, 0, 0, 87965, 0, 0, 0, 'Рулкан to Player');
INSERT INTO `creature_text` VALUES (82495, 3, 0, 'Honor to you, commander.', 12, 0, 100, 0, 0, 0, 87964, 171203, 171203, 0, 'Rulkan to Player');
INSERT INTO `creature_text` VALUES (82495, 3, 0, 'Совсем другое дело.', 12, 0, 100, 0, 0, 0, 79172, 346000, 346000, 0, 'Рулкан to 230298');
INSERT INTO `creature_text` VALUES (82495, 3, 0, 'Стихии зовут.', 12, 0, 100, 0, 0, 0, 111444, 95000, 95000, 0, 'Рулкан to Player');
INSERT INTO `creature_text` VALUES (82495, 3, 0, 'Честь тебе, командир.', 12, 0, 100, 0, 0, 42439, 87964, 57594, 57594, 0, 'Рулкан to Player');
INSERT INTO `creature_text` VALUES (82498, 3, 0, 'А когда папа придет?', 12, 0, 100, 0, 0, 0, 90570, 7000, 145000, 0, 'Лумиель');
INSERT INTO `creature_text` VALUES (82498, 3, 0, 'Папа все еще на охоте?', 12, 0, 100, 0, 0, 0, 90577, 958000, 958000, 0, 'Лумиель');
INSERT INTO `creature_text` VALUES (82504, 3, 0, 'Run!', 12, 0, 100, 0, 0, 0, 118743, 18265, 18265, 0, 'Nethergarde Prisoner to Ironmarch Executioner');
INSERT INTO `creature_text` VALUES (82504, 3, 0, 'She\'s dead! I\'m out of here!', 12, 0, 100, 0, 0, 0, 84723, 269906, 269906, 0, 'Nethergarde Prisoner to Ironmarch Executioner');
INSERT INTO `creature_text` VALUES (82504, 3, 0, 'Thank you!', 12, 0, 100, 0, 0, 0, 131553, 99735, 99735, 0, 'Nethergarde Prisoner to Ironmarch Executioner');
INSERT INTO `creature_text` VALUES (82504, 3, 0, 'Бегите!', 12, 0, 100, 0, 0, 0, 118743, 4000, 4000, 0, 'Пленник из Крепости Стражей Пустоты to Палач Железного Марша');
INSERT INTO `creature_text` VALUES (82504, 3, 0, 'Она мертва! С меня хватит! Я ухожу!', 12, 0, 100, 0, 0, 0, 84723, 317625, 317625, 0, 'Пленник из Крепости Стражей Пустоты to Палач Железного Марша');
INSERT INTO `creature_text` VALUES (82504, 3, 0, 'Спасибо!', 12, 0, 100, 0, 0, 0, 131553, 210641, 210641, 0, 'Пленник из Крепости Стражей Пустоты to Палач Железного Марша');
INSERT INTO `creature_text` VALUES (82504, 3, 0, 'Это мой шанс!', 12, 0, 100, 0, 0, 0, 84721, 9000, 9000, 0, 'Пленник из Крепости Стражей Пустоты to Палач Железного Марша');
INSERT INTO `creature_text` VALUES (82523, 3, 0, 'Закрыть волчьи логова!', 14, 0, 100, 0, 0, 46816, 84398, 2000, 2000, 170002, 'Маг цитадели to Player');
INSERT INTO `creature_text` VALUES (82524, 3, 0, 'Нужно двигаться дальше.', 14, 0, 100, 0, 0, 46817, 84396, 3000, 3000, 0, 'Меткий стрелок из племени Черного Копья');
INSERT INTO `creature_text` VALUES (82526, 3, 0, 'Нужно двигаться дальше.', 14, 0, 100, 0, 0, 44689, 84396, 3000, 3000, 0, 'Рангари - меткий стрелок');
INSERT INTO `creature_text` VALUES (82527, 3, 0, 'Закрыть волчьи логова!', 14, 0, 100, 397, 0, 46818, 84398, 3000, 3000, 168937, 'Чародей Похитителей Солнца to Player');
INSERT INTO `creature_text` VALUES (82543, 0, 0, 'Begone, exarch. This one, and all he knows, belongs to us now.', 12, 0, 100, 0, 0, 45523, 84460, 0, 0, 0, 'Shrakun to Player');
INSERT INTO `creature_text` VALUES (82543, 1, 0, '%s is about to vomit ectoplasm!', 41, 0, 100, 0, 0, 0, 84502, 0, 0, 0, 'Shrakun to Tracking Bunny');
INSERT INTO `creature_text` VALUES (82545, 0, 0, 'Auchindoun... they wanted...', 12, 0, 100, 0, 0, 46756, 84490, 0, 0, 0, 'Spirit of Hataaru to Player');
INSERT INTO `creature_text` VALUES (82545, 1, 0, '...the Exarch\'s orders...', 12, 0, 100, 0, 0, 46757, 84493, 0, 0, 0, 'Spirit of Hataaru to Player');
INSERT INTO `creature_text` VALUES (82545, 2, 0, '%s points at Soulbinder Nyami.', 16, 0, 100, 0, 0, 0, 84497, 0, 0, 0, 'Spirit of Hataaru to Player');
INSERT INTO `creature_text` VALUES (82545, 3, 0, '...Soulbinder...', 12, 0, 100, 0, 0, 46758, 84496, 0, 0, 0, 'Spirit of Hataaru to Player');
INSERT INTO `creature_text` VALUES (82554, 3, 0, 'Привет и добро пожаловать в гриль-бар \"Ржавый Цилиндр\"!', 12, 0, 100, 70, 0, 0, 84458, 316000, 316000, 0, 'Зазывала \"Ржавого Цилиндра\" to Player');
INSERT INTO `creature_text` VALUES (82557, 3, 0, 'Планер готов к отправке! Дай нам знать, если найдешь что-нибудь интересное, ага?', 12, 0, 100, 1, 0, 0, 84466, 452000, 1496000, 0, 'Исследователь Бисби to Player');
INSERT INTO `creature_text` VALUES (82561, 3, 0, 'Привет, $gприятель:сестрица;! Славный нынче денек для полетов, а?', 12, 0, 100, 1, 0, 0, 84470, 140000, 140000, 0, 'Исследователь Раззук to Player');
INSERT INTO `creature_text` VALUES (82562, 3, 0, 'Quickly now, I have a bad feeling about all this...', 12, 0, 100, 0, 0, 0, 84469, 1390, 1390, 0, 'Khaano to Player');
INSERT INTO `creature_text` VALUES (82562, 3, 0, 'Давай быстрее... У меня какое-то дурное предчувствие...', 12, 0, 100, 0, 0, 0, 84469, 1000, 1000, 89180, 'Хаано to Player');
INSERT INTO `creature_text` VALUES (82563, 0, 0, 'Ah, such easy prey.', 12, 0, 100, 0, 0, 0, 84522, 0, 0, 0, 'Adherent Harrier to Player');
INSERT INTO `creature_text` VALUES (82563, 0, 0, 'Ваш жалкий бунт окончен.', 12, 0, 100, 0, 0, 0, 84519, 0, 0, 0, 'Приверженец-налетчик to Player');
INSERT INTO `creature_text` VALUES (82563, 0, 0, 'Попался, Решад!', 12, 0, 100, 0, 0, 0, 84523, 0, 0, 0, 'Приверженец-налетчик to Player');
INSERT INTO `creature_text` VALUES (82563, 3, 0, 'Грязная, жалкая тварь… Я избавлю тебя от страданий.', 12, 0, 100, 0, 0, 0, 84521, 1000, 1000, 0, 'Приверженец-налетчик to Player');
INSERT INTO `creature_text` VALUES (82563, 3, 0, 'Думаешь, лес тебя защитит? Рухмар видит все!', 12, 0, 100, 0, 0, 0, 84520, 1000, 1000, 0, 'Приверженец-налетчик to Player');
INSERT INTO `creature_text` VALUES (82563, 3, 0, 'Какая легкая добыча!', 12, 0, 100, 0, 0, 0, 84522, 0, 0, 0, 'Приверженец-налетчик to Player');
INSERT INTO `creature_text` VALUES (82563, 3, 0, 'Твой клюв будет хорошим подарком Высшему мудрецу!', 12, 0, 100, 0, 0, 0, 84518, 0, 0, 0, 'Приверженец-налетчик to Player');
INSERT INTO `creature_text` VALUES (82563, 3, 0, 'Тебе не скрыться от нас, хранитель свитков!', 12, 0, 100, 0, 0, 0, 84517, 110, 110, 0, 'Приверженец-налетчик to Player');
INSERT INTO `creature_text` VALUES (82563, 4, 0, 'Мои глаза! Что творится?!', 12, 0, 100, 0, 0, 0, 84526, 3000, 3000, 0, 'Приверженец-налетчик to Player');
INSERT INTO `creature_text` VALUES (82563, 4, 0, 'Обманка?!', 12, 0, 100, 0, 0, 0, 84525, 2000, 2000, 0, 'Приверженец-налетчик to Player');
INSERT INTO `creature_text` VALUES (82563, 4, 0, 'Ре… ша-а-ад…', 12, 0, 100, 0, 0, 0, 84527, 3000, 3000, 0, 'Приверженец-налетчик to Player');
INSERT INTO `creature_text` VALUES (82563, 4, 0, 'Руа-каааар! Это что?!', 12, 0, 100, 0, 0, 0, 84524, 7000, 7000, 0, 'Приверженец-налетчик to Player');
INSERT INTO `creature_text` VALUES (82563, 4, 0, 'У-а-а-к! Где... где я?', 12, 0, 100, 0, 0, 0, 84528, 3000, 3000, 0, 'Приверженец-налетчик to Player');
INSERT INTO `creature_text` VALUES (82565, 3, 0, 'Планер в твоем полном распоряжении. Я бы и сам им воспользовался, но преждевременная смерть пока не входит в мои планы.', 12, 0, 100, 0, 0, 0, 84477, 105000, 995000, 0, 'Исследователь Гарикс to Player');
INSERT INTO `creature_text` VALUES (82565, 3, 0, 'Ты все еще $gжив:жива;? Знаешь, в этом деле главное – уметь вовремя остановиться. Все равно больше ничего путного тут уже не найдешь.', 12, 0, 100, 0, 0, 0, 84478, 23000, 23000, 0, 'Исследователь Гарикс to Player');
INSERT INTO `creature_text` VALUES (82574, 0, 0, 'Руки вверх, каланча.', 12, 0, 100, 213, 0, 0, 86784, 0, 0, 0, 'Пенни Поппинстук to Player');
INSERT INTO `creature_text` VALUES (82574, 1, 0, 'Ты очень вовремя закончил ремонт, Талдрен!', 12, 0, 100, 603, 0, 0, 86601, 0, 0, 0, 'Пенни Поппинстук to Player');
INSERT INTO `creature_text` VALUES (82574, 2, 0, 'Командир, чтобы вернуться на аванпост, можешь воспользоваться буровой установкой.', 12, 0, 100, 1, 0, 0, 86915, 0, 0, 0, 'Пенни Поппинстук to Player');
INSERT INTO `creature_text` VALUES (82575, 0, 0, 'Fell off yer glider right into me lap!', 12, 0, 100, 213, 0, 0, 86785, 0, 0, 0, 'Glirin to Player');
INSERT INTO `creature_text` VALUES (82576, 3, 0, 'С этим нашествием элементалей нам стало катастрофически не хватать пилотов. Планеров-то полно – бери любой. Кстати, а почему бы тебе не полетать – вдруг найдешь что-нибудь дельное!', 12, 0, 100, 0, 0, 0, 84505, 671000, 1179000, 0, 'Исследователь Дез to Player');
INSERT INTO `creature_text` VALUES (82579, 3, 0, 'Мы почти дошли, капитан.', 12, 0, 100, 0, 0, 0, 84545, 1000, 1000, 0, 'Гром\'карский ясновидящий to Гром\'карский капитан');
INSERT INTO `creature_text` VALUES (82579, 3, 0, 'Она уже почти в поле зрения, капитан.', 12, 0, 100, 0, 0, 0, 84547, 7000, 7000, 0, 'Гром\'карский ясновидящий to Гром\'карский капитан');
INSERT INTO `creature_text` VALUES (82586, 3, 0, '%s проверяет список поставок.', 16, 0, 100, 25, 0, 42439, 86890, 3000, 3000, 0, 'Грузчик to 235292');
INSERT INTO `creature_text` VALUES (82597, 0, 0, 'Живее!', 12, 0, 100, 0, 0, 0, 84541, 0, 0, 0, 'Гром\'карский капитан');
INSERT INTO `creature_text` VALUES (82597, 3, 0, 'Clairvoyants, report the situation!', 12, 0, 100, 0, 0, 0, 84542, 18000, 18000, 0, 'Гром\'карский капитан');
INSERT INTO `creature_text` VALUES (82597, 3, 0, 'А что ты видишь?', 12, 0, 100, 0, 0, 0, 84544, 39000, 39000, 0, 'Гром\'карский капитан');
INSERT INTO `creature_text` VALUES (82597, 3, 0, 'Батрак, заряжай пушку. ЖИВО!', 12, 0, 100, 0, 0, 0, 84539, 19000, 19000, 0, 'Гром\'карский капитан');
INSERT INTO `creature_text` VALUES (82597, 3, 0, 'Держите портал открытым. Наши ясновидящие уже почти откалибровали пушку.', 12, 0, 100, 0, 0, 0, 84538, 33000, 33000, 0, 'Гром\'карский капитан');
INSERT INTO `creature_text` VALUES (82597, 3, 0, 'Не отвлекайся, маг. У нас не так много времени.', 12, 0, 100, 0, 0, 0, 84537, 41000, 41000, 0, 'Гром\'карский капитан');
INSERT INTO `creature_text` VALUES (82597, 3, 0, 'Ты чем занимаешься, червяк?! Ты должен был сделать все это еще вчера!', 12, 0, 100, 0, 0, 0, 84540, 13000, 13000, 0, 'Гром\'карский капитан');
INSERT INTO `creature_text` VALUES (82597, 3, 0, 'Хватит впустую тратить время, гоблин. Держи город в поле зрения.', 12, 0, 100, 0, 0, 0, 84536, 1000, 1000, 0, 'Гром\'карский капитан');
INSERT INTO `creature_text` VALUES (82597, 3, 0, 'Шаман, доложи обстановку.', 12, 0, 100, 0, 0, 0, 84543, 71000, 71000, 0, 'Гром\'карский капитан');
INSERT INTO `creature_text` VALUES (82598, 0, 0, 'We must hurry, $p! I have to get there now!', 12, 0, 100, 0, 0, 0, 87697, 0, 0, 0, 'Khaano to Player');
INSERT INTO `creature_text` VALUES (82611, 0, 0, 'Step aside, $p. Let us see if this fellow had anything worthwhile...', 12, 0, 100, 0, 0, 46010, 84553, 0, 0, 0, 'Reshad to Player');
INSERT INTO `creature_text` VALUES (82611, 1, 0, 'Mind the door, will you?', 12, 0, 100, 0, 0, 46011, 84554, 0, 0, 0, 'Reshad to Player');
INSERT INTO `creature_text` VALUES (82611, 2, 0, 'Hold off the Adherents.', 42, 0, 100, 0, 0, 0, 84559, 0, 0, 0, 'Reshad to Player');
INSERT INTO `creature_text` VALUES (82611, 3, 0, 'My, my... What a curious collection...', 12, 0, 100, 0, 0, 46012, 84560, 0, 0, 0, 'Reshad to Player');
INSERT INTO `creature_text` VALUES (82611, 4, 0, 'Percy, what\'s that you\'ve got there? Come, now, let\'s see it...', 12, 0, 100, 0, 0, 46013, 84561, 0, 0, 0, 'Reshad to Player');
INSERT INTO `creature_text` VALUES (82611, 5, 0, 'Fifty Layers of Shadow?! PERCIVAL, YOU PUT THAT BACK THIS INSTANT.', 12, 0, 100, 0, 0, 46014, 84562, 0, 0, 0, 'Reshad to Player');
INSERT INTO `creature_text` VALUES (82611, 6, 0, 'Nasty bird...', 12, 0, 100, 0, 0, 46015, 84565, 0, 0, 0, 'Reshad to Player');
INSERT INTO `creature_text` VALUES (82611, 7, 0, 'Ah, here\'s something! The Saga of Terokk.', 12, 0, 100, 0, 0, 46016, 84563, 0, 0, 0, 'Reshad to Player');
INSERT INTO `creature_text` VALUES (82611, 8, 0, 'And he\'s written notes inside. Here, have a look.', 12, 0, 100, 0, 0, 46017, 84564, 0, 0, 0, 'Reshad to Player');
INSERT INTO `creature_text` VALUES (82612, 0, 0, 'В добрый путь, командир.', 12, 0, 100, 66, 0, 0, 84572, 0, 0, 0, 'Хутоу Воздушное Перо to Player');
INSERT INTO `creature_text` VALUES (82612, 2, 0, 'Командир, я к твоим услугам.', 12, 0, 100, 66, 0, 0, 89176, 0, 0, 0, 'Хутоу Воздушное Перо to Player');
INSERT INTO `creature_text` VALUES (82619, 1, 0, 'I will smear you on the ground! Rrrrr!', 12, 0, 100, 0, 0, 0, 84596, 0, 0, 0, 'Археолог из клана Верховного Молота to Player');
INSERT INTO `creature_text` VALUES (82619, 1, 0, 'I\'ll smash your skull!', 12, 0, 100, 0, 0, 0, 84598, 0, 0, 0, 'Археолог из клана Верховного Молота to Player');
INSERT INTO `creature_text` VALUES (82619, 1, 0, 'Ар-р-р-р-р! Надеюсь, тебе нравится умирать!', 12, 0, 100, 0, 0, 0, 84594, 0, 0, 0, 'Археолог из клана Верховного Молота to Player');
INSERT INTO `creature_text` VALUES (82619, 1, 0, 'Вы с этими жалкими гоблинами за все заплатите!', 12, 0, 100, 0, 0, 0, 84600, 0, 0, 0, 'Археолог из клана Верховного Молота to Player');
INSERT INTO `creature_text` VALUES (82619, 1, 0, 'Ты $gхотел:хотела; украсть реликвию?! Я раздроблю тебе кости!', 12, 0, 100, 0, 0, 0, 84602, 0, 0, 0, 'Археолог из клана Верховного Молота to Player');
INSERT INTO `creature_text` VALUES (82619, 1, 0, 'Ты заплатишь за кражу! А-р-р-р!', 12, 0, 100, 0, 0, 0, 84599, 0, 0, 0, 'Археолог из клана Верховного Молота to Player');
INSERT INTO `creature_text` VALUES (82619, 1, 0, 'Это была реликвия для Верховного Молота! Ты умрешь!', 12, 0, 100, 0, 0, 0, 84601, 0, 0, 0, 'Археолог из клана Верховного Молота to Player');
INSERT INTO `creature_text` VALUES (82619, 3, 0, '$gНичтожный:Ничтожная:r; |3-6($r)! Я раздавлю тебя, как муху!', 12, 0, 100, 0, 0, 0, 84595, 9000, 9000, 0, 'Археолог из клана Верховного Молота to Player');
INSERT INTO `creature_text` VALUES (82619, 3, 0, 'Если будешь отлынивать – ответишь перед королем-чародеем!', 12, 0, 100, 0, 0, 0, 84588, 949000, 949000, 0, 'Археолог из клана Верховного Молота to Археолог из клана Верховного Молота');
INSERT INTO `creature_text` VALUES (82619, 3, 0, 'Идиоты, зачем вы разбили столько повозок? Теперь вам придется нести реликвии в земли Верховного Молота в руках!', 12, 0, 100, 0, 0, 0, 84587, 55000, 55000, 0, 'Археолог из клана Верховного Молота to Археолог из клана Верховного Молота');
INSERT INTO `creature_text` VALUES (82619, 3, 0, 'Копать быстрее! Королю-чародею нужно больше находок!', 12, 0, 100, 0, 0, 0, 84582, 36000, 36000, 0, 'Археолог из клана Верховного Молота to Археолог из клана Верховного Молота');
INSERT INTO `creature_text` VALUES (82619, 3, 0, 'Осторожнее с артефактами! Королю-чародею не нужны обломки!', 12, 0, 100, 0, 0, 0, 84584, 13000, 13000, 0, 'Археолог из клана Верховного Молота to Археолог из клана Верховного Молота');
INSERT INTO `creature_text` VALUES (82619, 3, 0, 'Первое правило работы с реликвиями – не говорить о реликвиях!', 12, 0, 100, 0, 0, 0, 84586, 5000, 5000, 0, 'Археолог из клана Верховного Молота to Археолог из клана Верховного Молота');
INSERT INTO `creature_text` VALUES (82619, 3, 0, 'Работайте быстрее! Ключ к реликвии должен быть где-то здесь!', 12, 0, 100, 0, 0, 0, 84583, 1957000, 1957000, 0, 'Археолог из клана Верховного Молота to Археолог из клана Верховного Молота');
INSERT INTO `creature_text` VALUES (82619, 3, 0, 'Сразу убивайте всех незваных гостей! Наша работа должна остаться тайной.', 12, 0, 100, 0, 0, 0, 84585, 5948976, 5948976, 0, 'Археолог из клана Верховного Молота to Археолог из клана Верховного Молота');
INSERT INTO `creature_text` VALUES (82623, 1, 0, 'That wretched outcast begged for death in the end - and so will you!', 12, 0, 100, 0, 0, 0, 84639, 0, 0, 0, 'Sun-Talon Oberyx to Player');
INSERT INTO `creature_text` VALUES (82623, 1, 0, 'Этот ничтожный изгой под конец молил о смерти, а теперь взмолишься и ты!', 12, 0, 100, 0, 0, 0, 84639, 0, 0, 0, 'Оберикс Солнечный Коготь to Player');
INSERT INTO `creature_text` VALUES (82623, 3, 0, 'Так вот что прятал воришка. Высший мудрец будет довольна.', 12, 0, 100, 0, 0, 0, 84641, 124000, 124000, 0, 'Оберикс Солнечный Коготь to Player');
INSERT INTO `creature_text` VALUES (82624, 0, 0, 'Остановить их!', 14, 0, 100, 0, 0, 45699, 103050, 0, 0, 0, 'Громмаш Адский Крик to Player');
INSERT INTO `creature_text` VALUES (82625, 0, 0, 'Хватит! Еще встретимся - на моих условиях.', 12, 0, 100, 1, 0, 43292, 84760, 0, 0, 0, 'Сокретар to 0');
INSERT INTO `creature_text` VALUES (82625, 1, 0, 'Скажи оставшимся экзархам, что их дни сочтены.', 12, 0, 100, 0, 0, 43293, 84761, 0, 0, 0, 'Сокретар to 0');
INSERT INTO `creature_text` VALUES (82650, 3, 0, 'Если соберешься полетать на планере над Громмашаром, будь осторожнее. Эти орки не шибко-то жалуют чужаков.', 12, 0, 100, 1, 0, 0, 84615, 11000, 11000, 0, 'Исследователь Тина to Player');
INSERT INTO `creature_text` VALUES (82651, 3, 0, 'Мой крик будет разить твоих врагов. Направляй меня, и мы сокрушим их!', 12, 0, 100, 0, 0, 46786, 84918, 17000, 17000, 0, 'Ка\'алу to Player');
INSERT INTO `creature_text` VALUES (82655, 0, 0, 'Back to the shadows for us, Percy.', 12, 0, 100, 1, 0, 46018, 84619, 0, 0, 0, 'Reshad to Player');
INSERT INTO `creature_text` VALUES (82656, 0, 0, 'I will interrupt your worthless life!', 12, 0, 100, 396, 0, 0, 88889, 0, 0, 0, 'Тормок');
INSERT INTO `creature_text` VALUES (82656, 0, 0, 'День моей смерти еще не настал.', 12, 0, 100, 396, 0, 0, 88895, 0, 0, 0, 'Тормок');
INSERT INTO `creature_text` VALUES (82656, 0, 0, 'Жалко тратить на тебя силы.', 12, 0, 100, 396, 0, 0, 88898, 0, 0, 0, 'Тормок to Фрагмент Бездны');
INSERT INTO `creature_text` VALUES (82656, 0, 0, 'И это, по-твоему, драка?', 12, 0, 100, 396, 0, 0, 88900, 0, 0, 0, 'Тормок to Воин из клана Призрачной Луны');
INSERT INTO `creature_text` VALUES (82656, 0, 0, 'Идите сюда, трусы!', 12, 0, 100, 396, 0, 0, 88894, 0, 0, 0, 'Тормок');
INSERT INTO `creature_text` VALUES (82656, 0, 0, 'Командир, тебе нужна сила истинного огра.', 12, 0, 100, 1, 0, 0, 88911, 0, 0, 0, 'Тормок to Player');
INSERT INTO `creature_text` VALUES (82656, 0, 0, 'Мар``гок еще пожалеет, что потерял такого силача, как я.', 12, 0, 100, 396, 0, 0, 0, 0, 0, 0, 'Тормок');
INSERT INTO `creature_text` VALUES (82656, 0, 0, 'О тебе быстро забудут...', 12, 0, 100, 397, 0, 0, 88897, 0, 0, 0, 'Тормок to Воин из клана Призрачной Луны');
INSERT INTO `creature_text` VALUES (82656, 0, 0, 'Таких, как ты, я убиваю сотнями!', 12, 0, 100, 396, 0, 0, 88886, 0, 0, 0, 'Тормок');
INSERT INTO `creature_text` VALUES (82656, 0, 0, 'Уверен, есть противники и посильнее.', 12, 0, 100, 396, 0, 0, 88903, 0, 0, 0, 'Тормок to Сущность Бездны');
INSERT INTO `creature_text` VALUES (82656, 1, 0, 'Ха! Да я одной левой таких клал.', 12, 0, 100, 396, 0, 0, 88892, 10000, 10000, 0, 'Тормок');
INSERT INTO `creature_text` VALUES (82656, 3, 0, 'Весь Дренор падет перед нами.', 12, 0, 100, 1, 0, 0, 88910, 294000, 294000, 0, 'Тормок to Player');
INSERT INTO `creature_text` VALUES (82656, 3, 0, 'Есть ли рядом достойный враг, которого можно сокрушить?', 12, 0, 100, 1, 0, 0, 88906, 246000, 246000, 0, 'Тормок to Player');
INSERT INTO `creature_text` VALUES (82656, 3, 0, 'Что прикажете, сэр?', 12, 0, 100, 1, 0, 0, 0, 2277000, 2277000, 0, 'Тормок to Player');
INSERT INTO `creature_text` VALUES (82656, 3, 0, 'Что прикажешь, командир?', 12, 0, 100, 1, 0, 0, 88905, 141045, 141045, 0, 'Тормок to Player');
INSERT INTO `creature_text` VALUES (82656, 3, 0, 'Это будет славная битва.', 12, 0, 100, 1, 0, 0, 88908, 1562000, 1562000, 0, 'Тормок to Player');
INSERT INTO `creature_text` VALUES (82656, 3, 0, 'Я в твоем распоряжении, повелитель.', 12, 0, 100, 1, 0, 0, 88912, 150000, 150000, 0, 'Тормок to Player');
INSERT INTO `creature_text` VALUES (82656, 4, 0, 'Глупец, сейчас ты узнаешь, что такое кулак империи!', 12, 0, 100, 396, 0, 0, 88893, 19000, 19000, 172870, 'Тормок');
INSERT INTO `creature_text` VALUES (82656, 4, 0, 'Как мне это нравится.', 12, 0, 100, 153, 0, 0, 88901, 13000, 13000, 172769, 'Тормок to Искусный воин из клана Пылающего Клинка');
INSERT INTO `creature_text` VALUES (82656, 4, 0, 'Твоя жалкая жизнь окончена.', 12, 0, 100, 397, 0, 0, 88896, 3000, 3000, 0, 'Тормок to Гром\'карский меткострел');
INSERT INTO `creature_text` VALUES (82656, 4, 0, 'Я даже чуть не вспотел.', 12, 0, 100, 153, 0, 0, 88899, 20000, 20000, 0, 'Тормок to Гром\'карский передовой боец');
INSERT INTO `creature_text` VALUES (82658, 0, 0, 'Если увидишь кого-то из моих разгильдяев, скажи, что если они хотят зарплату, то пусть начинают работать!', 12, 0, 100, 0, 0, 0, 82462, 0, 0, 0, 'Трикси Леру to Player');
INSERT INTO `creature_text` VALUES (82658, 1, 0, 'Слушайте, вы, идиоты! Раскопки будут идти дальше, с вами или без вас! Если хотите, чтобы вам заплатили, принимайтесь за работу!', 14, 0, 100, 0, 0, 0, 82331, 0, 0, 0, 'Трикси Леру to Player');
INSERT INTO `creature_text` VALUES (82660, 3, 0, 'Орда разрушила |c00FFFF00|Hspell:178535|h[Врата чернокнижников]|h|r!', 12, 0, 100, 0, 0, 8213, 84664, 2589000, 2589000, 0, 'Маркета to Player');
INSERT INTO `creature_text` VALUES (82660, 3, 0, 'Открылись |c00FFFF00|Hspell:178535|h[Врата чернокнижников]|h|r! Их местоположение отмечено на вашей карте.', 12, 0, 100, 0, 0, 8173, 84661, 1568000, 1568000, 0, 'Маркета to Player');
INSERT INTO `creature_text` VALUES (82664, 3, 0, 'Oh, my. We\'re definitely going to need more food.', 12, 0, 100, 0, 0, 0, 85621, 5435891, 5435891, 0, 'Quartermaster Jolie to Player');
INSERT INTO `creature_text` VALUES (82664, 3, 0, 'There are apples, there are helmets. There is a pretty quartermaster.', 12, 0, 100, 0, 0, 0, 85619, 221422, 221422, 0, 'Интендант Милага to Player');
INSERT INTO `creature_text` VALUES (82664, 3, 0, 'Нет-нет-нет, так не пойдет. У нас нет и никогда не было столько мечей.', 12, 0, 100, 0, 0, 0, 85620, 3849393, 3849393, 0, 'Интендант Милага to Player');
INSERT INTO `creature_text` VALUES (82664, 3, 0, 'Ну и ну, нам точно понадобится больше провианта.', 12, 0, 100, 0, 0, 0, 85621, 4747000, 4747000, 0, 'Интендант Милага to Player');
INSERT INTO `creature_text` VALUES (82682, 0, 0, 'No... You can\'t... It\'s... I can\'t FIGHT IT!', 14, 0, 100, 0, 0, 45481, 86811, 0, 0, 0, 'Верховный маг Сол to Одурманенный маг');
INSERT INTO `creature_text` VALUES (82682, 1, 0, 'Flee! The vines! They control us all!', 14, 0, 100, 0, 0, 45479, 86812, 0, 0, 0, 'Верховный маг Сол to 0');
INSERT INTO `creature_text` VALUES (82682, 2, 0, 'No! They will take you too!', 14, 0, 100, 0, 0, 45482, 86816, 0, 0, 0, 'Верховный маг Сол to Тотем заземления');
INSERT INTO `creature_text` VALUES (82682, 3, 0, 'Yes, fire! Fire will burn away the contagion.', 14, 0, 100, 0, 0, 45484, 86813, 0, 0, 0, 'Верховный маг Сол to 0');
INSERT INTO `creature_text` VALUES (82682, 4, 0, '|TInterface\\Icons\\Ability_Mage_MoltenArmor.blp:20|tВерховный маг Сол применяет заклинания магии льда!', 41, 0, 100, 0, 0, 0, 85959, 0, 0, 0, 'Верховный маг Сол to 0');
INSERT INTO `creature_text` VALUES (82682, 5, 0, 'Surely the cold will make them wither?', 14, 0, 100, 0, 0, 45485, 86814, 0, 0, 0, 'Верховный маг Сол to 0');
INSERT INTO `creature_text` VALUES (82682, 6, 0, '|TInterface\\Icons\\Ability_Mage_MoltenArmor.blp:20|tВерховный маг Сол применяет заклинания тайной магии!', 41, 0, 100, 0, 0, 0, 85960, 0, 0, 0, 'Верховный маг Сол to 0');
INSERT INTO `creature_text` VALUES (82682, 7, 0, 'Get away! GET AWAY!', 14, 0, 100, 0, 0, 45486, 86815, 0, 0, 0, 'Верховный маг Сол to 0');
INSERT INTO `creature_text` VALUES (82682, 8, 0, 'Burn my body, it\'s the only way to stop them.', 14, 0, 100, 0, 0, 45480, 86818, 0, 0, 0, 'Верховный маг Сол to Player');
INSERT INTO `creature_text` VALUES (82683, 0, 0, 'Ornekka, it is not safe here - the Adherents are out in force. Come with us to Veil Terokk.', 12, 0, 100, 1, 0, 46008, 84645, 0, 0, 0, 'Reshad to Player');
INSERT INTO `creature_text` VALUES (82683, 1, 0, 'Ну хорошо. Пошли, герой.', 12, 0, 100, 1, 0, 46009, 84651, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (82688, 3, 0, 'Моя семья должна быть отомщена.', 12, 0, 100, 18, 0, 0, 84682, 155000, 155000, 0, 'Ольгра to Player');
INSERT INTO `creature_text` VALUES (82702, 1, 0, 'А это кто еще? Друг семьи?', 12, 0, 100, 0, 0, 0, 84655, 62000, 325000, 0, 'Воргошкур Дикий to Player');
INSERT INTO `creature_text` VALUES (82704, 0, 0, 'Almost there. Come along, Percy!', 12, 0, 100, 0, 0, 46024, 84667, 0, 0, 0, 'Reshad to Player');
INSERT INTO `creature_text` VALUES (82704, 1, 0, 'Shadows gather...', 12, 0, 100, 0, 0, 46025, 84673, 0, 0, 0, 'Reshad to Player');
INSERT INTO `creature_text` VALUES (82704, 2, 0, 'Yes, just barely. Are we late?', 12, 0, 100, 0, 0, 46026, 84688, 0, 0, 0, 'Reshad to Player');
INSERT INTO `creature_text` VALUES (82704, 3, 0, 'Yes, yes. And something unexpected, too! We have much to discuss.', 12, 0, 100, 0, 0, 46027, 84676, 0, 0, 0, 'Reshad to Player');
INSERT INTO `creature_text` VALUES (82720, 0, 0, 'Raastok, I need a shroud at that dig site. Kura, back him up.', 12, 0, 100, 0, 0, 46312, 84672, 0, 0, 0, 'Shadow-Sage Iskar to Player');
INSERT INTO `creature_text` VALUES (82720, 1, 0, 'Reshad! You survived Skettis!', 12, 0, 100, 0, 0, 46313, 84687, 0, 0, 0, 'Shadow-Sage Iskar to Player');
INSERT INTO `creature_text` VALUES (82720, 2, 0, 'The Adherents grow bolder each day. It\'s all we can do to keep Veil Terokk shrouded from them.', 12, 0, 100, 0, 0, 46314, 84674, 0, 0, 0, 'Shadow-Sage Iskar to Player');
INSERT INTO `creature_text` VALUES (82720, 3, 0, 'Do you have the relics?', 12, 0, 100, 0, 0, 46315, 84675, 0, 0, 0, 'Shadow-Sage Iskar to Player');
INSERT INTO `creature_text` VALUES (82725, 0, 0, 'I only sell my goods to ogres and gladiators! And to all the rest I can only drop a hammer in the snout!', 12, 0, 100, 1, 0, 0, 84777, 0, 0, 0, 'Григрок to Player');
INSERT INTO `creature_text` VALUES (82727, 0, 0, 'Самое свежее мясо – у Мясобрюха! А кому не нравится – получит в рыло!', 12, 0, 100, 1, 0, 0, 84779, 0, 0, 0, 'Мясобрюх to Player');
INSERT INTO `creature_text` VALUES (82733, 3, 0, 'Еретики! Убить! Проглотить!', 14, 0, 100, 397, 0, 0, 84693, 302000, 302000, 0, 'Поддавшийся ночи Приверженец Тени to Player');
INSERT INTO `creature_text` VALUES (82754, 0, 0, 'Что, $gушиб:ушибла; на арене колено? Залечу любые травмы – по сходной цене!', 12, 0, 100, 1, 0, 0, 84783, 0, 0, 0, 'Сестра Лизз to Player');
INSERT INTO `creature_text` VALUES (82756, 0, 0, 'Мы – друзья до гроба.', 12, 0, 100, 0, 0, 0, 88037, 0, 0, 0, 'Лирой Дженкинс to Player');
INSERT INTO `creature_text` VALUES (82756, 1, 0, 'Время вышло. Вперед!', 12, 0, 100, 0, 0, 0, 88033, 0, 186000, 0, 'Лирой Дженкинс');
INSERT INTO `creature_text` VALUES (82756, 3, 0, 'Извини, ты что-то $gсказал:сказала;?', 12, 0, 100, 0, 0, 0, 88035, 67000, 67000, 0, 'Лирой Дженкинс to Player');
INSERT INTO `creature_text` VALUES (82756, 3, 0, 'Лироооой! Джееенкинс!', 12, 0, 100, 0, 0, 0, 88032, 24000, 254000, 0, 'Лирой Дженкинс');
INSERT INTO `creature_text` VALUES (82756, 3, 0, 'М-м, что ты $gсказал:сказала;?', 12, 0, 100, 0, 0, 0, 88036, 139000, 139000, 0, 'Лирой Дженкинс to Player');
