INSERT INTO `creature_text` VALUES (83026, 1, 0, 'Хватит красоваться.', 12, 0, 100, 153, 0, 46242, 85402, 0, 0, 0, 'Осадный мастер Олугар to 0');
INSERT INTO `creature_text` VALUES (83049, 3, 0, '$gЧужак:Чужестранка;! Я тебя прикончу!', 12, 0, 100, 0, 0, 0, 86819, 30000, 30000, 0, 'Вор\'гаш Вожак Стаи to Player');
INSERT INTO `creature_text` VALUES (83268, 0, 0, 'Прикончите его.', 14, 0, 100, 0, 0, 44758, 84871, 0, 0, 0, 'Император Мар\'гок to Каргат Острорук');
INSERT INTO `creature_text` VALUES (83385, 0, 0, 'Begin the Ritual!  I will become one with the Void!', 14, 0, 100, 0, 0, 0, 84915, 0, 0, 0, 'Voidseer Kalurg');
INSERT INTO `creature_text` VALUES (83385, 0, 0, 'Начинайте Ритуал! Я стану единым целым с Бездной!', 14, 0, 100, 0, 0, 0, 84915, 0, 0, 0, 'Пророк Бездны Калург');
INSERT INTO `creature_text` VALUES (83385, 0, 0, 'Ты смеешь прерывать мое восхождение!', 14, 0, 100, 0, 0, 0, 84913, 0, 0, 0, 'Пророк Бездны Калург to Чаротворец клана Призрачной Луны');
INSERT INTO `creature_text` VALUES (83385, 3, 0, 'You dare interrupt my ascendance!', 14, 0, 100, 0, 0, 0, 84913, 185500, 185500, 0, 'Voidseer Kalurg to Shadowmoon Channeller');
INSERT INTO `creature_text` VALUES (83385, 3, 0, 'You never should have interfered!', 14, 0, 100, 0, 0, 0, 84916, 302922, 302922, 0, 'Voidseer Kalurg to Player');
INSERT INTO `creature_text` VALUES (83385, 3, 0, 'Не надо было тебе вмешиваться!', 14, 0, 100, 0, 0, 0, 84916, 92000, 92000, 0, 'Пророк Бездны Калург to Player');
INSERT INTO `creature_text` VALUES (83398, 0, 0, 'Let me hook up the apparatus. Just a moment.', 12, 0, 100, 1, 0, 0, 84969, 0, 0, 0, 'Dilbert Tinkertoss to Player');
INSERT INTO `creature_text` VALUES (83398, 0, 0, 'Сейчас я подключу устройство. Секундочку.', 12, 0, 100, 1, 0, 0, 84969, 0, 0, 0, 'Дилберт Портач to Player');
INSERT INTO `creature_text` VALUES (83398, 3, 0, 'All systems are active. Fire!', 12, 0, 100, 1, 0, 0, 84970, 9704, 9704, 0, 'Dilbert Tinkertoss to Player');
INSERT INTO `creature_text` VALUES (83398, 3, 0, 'Все системы работают. Огонь!', 12, 0, 100, 1, 0, 0, 84970, 11546, 11546, 0, 'Дилберт Портач to Player');
INSERT INTO `creature_text` VALUES (83406, 3, 0, '...and the children. Who will care for them now?', 12, 0, 100, 6, 0, 0, 85255, 126719, 126719, 0, 'Karabor Citizen');
INSERT INTO `creature_text` VALUES (83407, 4, 0, 'Встань в круг Света Небес.', 14, 0, 100, 0, 0, 45579, 84348, 21000, 21000, 167167, 'Воздаятельница Ирель to Дарн Ненасытный');
INSERT INTO `creature_text` VALUES (83411, 3, 0, 'I know it hurts, but you must try and rest.', 12, 0, 100, 0, 0, 0, 85276, 51282, 51282, 0, 'Priestess of Karabor');
INSERT INTO `creature_text` VALUES (83416, 3, 0, 'Будешь моим подарком вождю Адскому Крику.', 12, 0, 100, 0, 0, 46349, 84966, 9000, 9000, 0, 'Азука Ярость Клинка to Player');
INSERT INTO `creature_text` VALUES (83416, 3, 0, 'Ну-ка, ну-ка, ну-ка… Надо же, кто попался в наши силки.', 12, 0, 100, 0, 0, 46348, 84965, 1000, 1000, 161927, 'Азука Ярость Клинка to Player');
INSERT INTO `creature_text` VALUES (83428, 1, 0, 'Тебя унесет буря!', 12, 0, 100, 0, 0, 0, 88498, 0, 0, 0, 'Призыватель ветров Кораст to Player');
INSERT INTO `creature_text` VALUES (83428, 4, 0, 'Я возвращаюсь к стихиям...', 12, 0, 100, 0, 0, 0, 88508, 34000, 34000, 0, 'Призыватель ветров Кораст to Player');
INSERT INTO `creature_text` VALUES (83452, 1, 0, 'Отвянь от меня!', 12, 0, 100, 0, 0, 0, 88751, 0, 0, 0, 'Малкор to Player');
INSERT INTO `creature_text` VALUES (83452, 3, 0, 'Leaf me alone!', 12, 0, 100, 0, 0, 0, 88751, 155281, 155281, 0, 'Malkor to Player');
INSERT INTO `creature_text` VALUES (83463, 3, 0, 'You bring this strange gift and helped those in Skettis... yes, I think we can come to an arrangement. The shadows cover much.', 12, 0, 100, 0, 0, 0, 84654, 9143062, 9143062, 166515, 'Dusk-Seer Irizzar to Player');
INSERT INTO `creature_text` VALUES (83463, 3, 0, 'Этот странный подарок и твоя помощь в Скеттисе… да, думаю, мы сможем договориться. Тени скрывают многое.', 12, 0, 100, 0, 0, 0, 84654, 605531, 605531, 166515, 'Темный ведун Ириззар to Player');
INSERT INTO `creature_text` VALUES (83465, 0, 0, 'Long ago, a Bloodmane pridelord commanded his hunters to bring him ten thousand arakkoa feathers.', 42, 0, 100, 0, 0, 46032, 84994, 0, 0, 0, 'Narrator Bunny \"The Talon King\" to Player');
INSERT INTO `creature_text` VALUES (83465, 1, 0, 'Eager for the glory of battle, the young chieftain had hoped to incite a war with the arakkoa.', 42, 0, 100, 0, 0, 46033, 85004, 0, 0, 0, 'Narrator Bunny \"The Talon King\" to Player');
INSERT INTO `creature_text` VALUES (83465, 2, 0, 'But the Talon King came alone, and what ensued was not a war. It was a slaughter.', 42, 0, 100, 0, 0, 46034, 84995, 0, 0, 0, 'Narrator Bunny \"The Talon King\" to Player');
INSERT INTO `creature_text` VALUES (83465, 3, 0, 'He moved with grace and power matched only by the wind.', 42, 0, 100, 0, 0, 46035, 84997, 0, 0, 0, 'Narrator Bunny \"The Talon King\" to Player');
INSERT INTO `creature_text` VALUES (83465, 4, 0, 'With the sweep of his wings, he cut down even the strongest of the Bloodmane like so many blades of grass.', 42, 0, 100, 0, 0, 46036, 84998, 0, 0, 0, 'Narrator Bunny \"The Talon King\" to Player');
INSERT INTO `creature_text` VALUES (83465, 5, 0, 'Their pridelord was fierce, but he was no match for Terokk.', 42, 0, 100, 0, 0, 46037, 85000, 0, 0, 0, 'Narrator Bunny \"The Talon King\" to Player');
INSERT INTO `creature_text` VALUES (83465, 6, 0, 'Satisfied that his message was clear, the Talon King left the Bloodmane to pick up the pieces of their broken tribe.', 42, 0, 100, 0, 0, 46038, 85002, 0, 0, 0, 'Narrator Bunny \"The Talon King\" to Player');
INSERT INTO `creature_text` VALUES (83465, 7, 0, 'Его крылья рассекали тела сильнейших воинов, словно лезвие — бумагу.', 42, 0, 100, 0, 0, 46036, 84998, 0, 0, 0, 'Narrator Bunny \"The Talon King\" to Player');
INSERT INTO `creature_text` VALUES (83465, 8, 0, 'Он двигался стремительно и плавно, как ветер.', 42, 0, 100, 0, 0, 46035, 84997, 0, 0, 0, 'Narrator Bunny \"The Talon King\" to Player');
INSERT INTO `creature_text` VALUES (83465, 9, 0, 'Вожак был свиреп, но не выстоял против Терокка.', 42, 0, 100, 0, 0, 46037, 85000, 0, 0, 0, 'Narrator Bunny \"The Talon King\" to Player');
INSERT INTO `creature_text` VALUES (83465, 10, 0, 'Король воронов преподал племени Кровавой Гривы урок, в живых осталась лишь жалкая горстка бойцов.', 42, 0, 100, 0, 0, 46038, 85002, 0, 0, 0, 'Narrator Bunny \"The Talon King\" to Player');
INSERT INTO `creature_text` VALUES (83476, 0, 0, 'Вот теперь я могу убивать кого хочу и когда захочу! Я буду ждать тебя в кургане огров. Подходи, когда закончишь – зададим им жару!', 14, 0, 100, 0, 0, 0, 85013, 0, 0, 0, 'Бруто to Player');
INSERT INTO `creature_text` VALUES (83485, 0, 0, 'My fists and gratitude are yours, stranger. How do you feel about killing an ogre chief before you go?', 12, 0, 100, 2, 0, 0, 85031, 0, 0, 0, 'Pitfighter Vaandaam to Player');
INSERT INTO `creature_text` VALUES (83485, 1, 0, 'Спасибо, $gнезнакомец:незнакомка;. Мои кулаки к твоим услугам. Ну что, ты $gготов:готова; убить вождя огров?', 12, 0, 100, 2, 0, 0, 85031, 0, 0, 0, 'Боец Ваандаам to Player');
INSERT INTO `creature_text` VALUES (83518, 3, 0, '...and the children. Who will care for them now?', 12, 0, 100, 6, 0, 0, 85255, 4219, 4219, 0, 'Karabor Citizen');
INSERT INTO `creature_text` VALUES (83538, 3, 0, 'Со мною мощь Железной Орды!', 12, 0, 100, 0, 0, 0, 90426, 20194, 20194, 0, 'Командир Песни Войны to Player');
INSERT INTO `creature_text` VALUES (83542, 2, 0, 'Наверное, это была плохая идея...', 12, 0, 100, 0, 0, 0, 88509, 0, 0, 0, 'Шон Белое Море to Player');
INSERT INTO `creature_text` VALUES (83550, 3, 0, '...и детки. Кто теперь о них позаботится?', 12, 0, 100, 274, 0, 0, 85255, 10000, 10000, 0, 'Караборский боевой жрец');
INSERT INTO `creature_text` VALUES (83550, 3, 0, 'How could this have happened...', 12, 0, 100, 274, 0, 0, 85254, 23766, 23766, 0, 'Karabor Battle-Priest');
INSERT INTO `creature_text` VALUES (83557, 0, 0, 'The Talon King\'s own sages betrayed him. Terokk and his allies were cast into the pools.', 42, 0, 100, 0, 0, 46039, 85069, 0, 0, 0, 'Narrator Bunny \"Terokk\'s Fall\" to Player');
INSERT INTO `creature_text` VALUES (83557, 1, 0, 'The accursed blood seared his flesh and tore at his mind.', 42, 0, 100, 0, 0, 46040, 85070, 0, 0, 0, 'Narrator Bunny \"Terokk\'s Fall\" to Player');
INSERT INTO `creature_text` VALUES (83557, 2, 0, 'As he withered, Terokk had but one concern: his daughter Lithic.', 42, 0, 100, 0, 0, 46041, 85071, 0, 0, 0, 'Narrator Bunny \"Terokk\'s Fall\" to Player');
INSERT INTO `creature_text` VALUES (83557, 3, 0, 'Seeing her broken body, Terokk despaired. He surrendered to the creeping darkness.', 42, 0, 100, 0, 0, 46042, 85072, 0, 0, 0, 'Narrator Bunny \"Terokk\'s Fall\" to Player');
INSERT INTO `creature_text` VALUES (83557, 4, 0, 'But in that darkness, something found him.', 42, 0, 100, 0, 0, 46043, 85073, 0, 0, 0, 'Narrator Bunny \"Terokk\'s Fall\" to Player');
INSERT INTO `creature_text` VALUES (83557, 5, 0, 'A strange power filled the Talon King. A presence urged him on.', 42, 0, 100, 0, 0, 46044, 85074, 0, 0, 0, 'Narrator Bunny \"Terokk\'s Fall\" to Player');
INSERT INTO `creature_text` VALUES (83557, 12, 0, 'The strongest of his guards clung to sanity. And when the Talon King called, they answered.', 42, 0, 100, 0, 0, 46046, 85177, 0, 0, 0, 'Narrator Bunny \"Terokk\'s Fall\" to Player');
INSERT INTO `creature_text` VALUES (83557, 13, 0, 'Wretches driven mad by the curse thrashed about in the pools in agony. But Terokk set them free.', 42, 0, 100, 0, 0, 46045, 85113, 0, 0, 0, 'Narrator Bunny \"Terokk\'s Fall\" to Player');
INSERT INTO `creature_text` VALUES (83557, 14, 0, 'A strange orb peeked out from the wastes. Gazing into it, Terokk met the being that had saved him.', 42, 0, 100, 0, 0, 46047, 85076, 0, 0, 0, 'Narrator Bunny \"Terokk\'s Fall\" to Player');
INSERT INTO `creature_text` VALUES (83557, 15, 0, 'The Raven God Anzu gifted Terokk and his guards with dark powers. In time, they would be known as Talonpriests.', 42, 0, 100, 0, 0, 46048, 85079, 0, 0, 0, 'Narrator Bunny \"Terokk\'s Fall\" to Player');
INSERT INTO `creature_text` VALUES (83557, 16, 0, 'Together they would build Skettis, and protect the unwanted children of Rukhmar.', 42, 0, 100, 0, 0, 46049, 85186, 0, 0, 0, 'Narrator Bunny \"Terokk\'s Fall\" to Player');
INSERT INTO `creature_text` VALUES (83567, 0, 0, 'Our ancestors will tell great tales of your deeds, commander.', 12, 0, 100, 2, 0, 0, 0, 0, 0, 0, 'Firn Swiftbreeze to Player');
INSERT INTO `creature_text` VALUES (83567, 1, 0, 'Доставлю куда угодно, командир. Ты только скажи.', 12, 0, 100, 2, 0, 0, 85609, 0, 0, 0, 'Фирн Быстрый Ветер to Player');
INSERT INTO `creature_text` VALUES (83567, 2, 0, 'Предки гордились бы тобой, командир.', 12, 0, 100, 2, 0, 0, 85608, 0, 0, 0, 'Фирн Быстрый Ветер to Player');
INSERT INTO `creature_text` VALUES (83569, 0, 0, 'Is this one of yours?', 12, 0, 100, 0, 0, 46556, 85080, 0, 0, 0, 'Rexxar to Player');
INSERT INTO `creature_text` VALUES (83575, 0, 0, 'Столько мускулов – а камни ломать не можешь? Бестолковый огрон!', 14, 0, 100, 0, 0, 0, 90306, 0, 0, 0, 'Забивальщик');
INSERT INTO `creature_text` VALUES (83577, 4, 0, 'Быстро за работу, ленивый огрон!', 12, 0, 100, 0, 0, 0, 90312, 13000, 13000, 175163, 'Заклинатель Земледрог to Player');
INSERT INTO `creature_text` VALUES (83577, 4, 0, 'Хватит болтаться без дела. Долби камень, тупой огрон!', 12, 0, 100, 0, 0, 0, 90314, 3000, 3000, 0, 'Заклинатель Земледрог to Player');
INSERT INTO `creature_text` VALUES (83578, 0, 0, 'CRUSH THEM!!!', 14, 0, 100, 480, 0, 0, 85904, 0, 0, 0, 'Ogron Laborer to Player');
INSERT INTO `creature_text` VALUES (83578, 1, 0, 'ДАВИ ИХ!', 14, 0, 100, 480, 0, 0, 100106, 0, 0, 0, 'Огрон-работник to Player');
INSERT INTO `creature_text` VALUES (83586, 0, 0, 'Встретимся там.', 12, 0, 100, 0, 0, 0, 96641, 0, 0, 0, 'Темный следопыт Велонара to Player');
INSERT INTO `creature_text` VALUES (83587, 3, 0, 'Встретимся там.', 12, 0, 100, 396, 0, 0, 96641, 13000, 13000, 0, 'Гульда Теневой Клинок to Player');
INSERT INTO `creature_text` VALUES (83606, 0, 0, 'А теперь можно зайти?', 12, 0, 100, 1, 0, 0, 81695, 0, 0, 0, 'Люси Расторгуйка to Player');
INSERT INTO `creature_text` VALUES (83606, 1, 0, 'Вы просто кучка жалких трусов. Из всех вас только $p не тряпка.', 12, 0, 100, 1, 0, 0, 85105, 0, 0, 0, 'Люси Расторгуйка to Player');
INSERT INTO `creature_text` VALUES (83607, 3, 0, 'Told you! Tell him who I am, commander. Tell him how I like bones and killing.', 12, 0, 100, 0, 0, 0, 85089, 36218, 36218, 0, 'Bony Xuk to Player');
INSERT INTO `creature_text` VALUES (83607, 3, 0, 'Говорил я тебе?! Командир, расскажи ему, кто я такой. Расскажи, как я люблю убивать и ломать кости.', 12, 0, 100, 0, 0, 0, 85089, 58000, 58000, 0, 'Костлявый Зак to Player');
INSERT INTO `creature_text` VALUES (83608, 0, 0, 'Да поможет нам Темная Госпожа. Мы оказались не единственными.', 12, 0, 100, 396, 0, 0, 85252, 0, 0, 0, 'Темный следопыт Велонара to Player');
INSERT INTO `creature_text` VALUES (83609, 0, 0, 'Ох, все еще хуже, чем я предполагала. Они нанесли удар и здесь.', 12, 0, 100, 396, 0, 0, 85251, 0, 0, 0, 'Гульда Теневой Клинок to Player');
INSERT INTO `creature_text` VALUES (83611, 3, 0, 'Каджасса всегда рядом. Я тебе помогу, если что.', 12, 0, 100, 0, 0, 0, 85098, 8000, 8000, 0, 'Темная охотница Каджасса to Player');
INSERT INTO `creature_text` VALUES (83612, 0, 0, '%s prepares a |cFFFF0404|Hspell:168148|h[Cannon Barrage]|h|r! Get behind cover to survive!', 41, 0, 100, 0, 0, 0, 90484, 0, 0, 0, 'Skulloc to Skulloc');
INSERT INTO `creature_text` VALUES (83612, 2, 0, '%s готовит |cFFFF0404|Hspell:168148|h[\"Обстрел\"]|h|r! Прячьтесь в укрытие!', 41, 0, 100, 0, 0, 0, 90484, 0, 0, 0, 'Черепон to Черепон');
INSERT INTO `creature_text` VALUES (83613, 0, 0, 'FIRE... FIRE! AIM, THEN FIRE!', 14, 0, 100, 6, 0, 43903, 85910, 0, 0, 0, 'Koramar to Player');
INSERT INTO `creature_text` VALUES (83613, 2, 0, 'ОГОНЬ! ОГОНЬ! И ЦЕЛЬСЯ СНАЧАЛА!', 14, 0, 100, 6, 0, 43903, 85910, 0, 0, 0, 'Корамар to Player');
INSERT INTO `creature_text` VALUES (83616, 0, 0, 'Koramar, no! I will avenge you!', 14, 0, 100, 0, 0, 44060, 85938, 0, 0, 0, 'Zoggosh to Koramar');
INSERT INTO `creature_text` VALUES (83616, 1, 0, 'Корамар, нет! Я отомщу за вас!', 14, 0, 100, 0, 0, 44060, 85938, 0, 0, 0, 'Зоггош to Корамар');
INSERT INTO `creature_text` VALUES (83623, 3, 0, 'А-А-А-А-А! Кругом огонь!', 14, 0, 100, 0, 0, 0, 78820, 2000, 2000, 0, 'Страж из клана Кровавого Молота to Player');
INSERT INTO `creature_text` VALUES (83634, 1, 0, 'Страшись топоров клана Песни Войны!', 12, 0, 100, 0, 0, 0, 88510, 0, 0, 0, 'Разведчик Покхар to Player');
INSERT INTO `creature_text` VALUES (83634, 4, 0, 'Да… нужно было… больше топоров…', 12, 0, 100, 0, 0, 0, 88511, 13000, 13000, 0, 'Разведчик Покхар to Player');
INSERT INTO `creature_text` VALUES (83643, 2, 0, 'I died in battle ... It is a worthy death ...', 12, 0, 100, 0, 0, 0, 85155, 0, 0, 0, 'Малрок Камнекрушитель to Player');
INSERT INTO `creature_text` VALUES (83643, 4, 0, 'Время меня не пощадило, но зубы старого волка еще крепки!', 12, 0, 100, 0, 0, 0, 85154, 11000, 19000, 0, 'Малрок Камнекрушитель to Player');
INSERT INTO `creature_text` VALUES (83649, 0, 0, 'В Фолианте Зыбучих Песков сказано: \"Время не ждет никого.\" Прародительница Воронов знала, что ритуал $gдолжен будет провести тот:должна будет провести та;, что не принадлежит к нашей стае! Воистину, ты $gблагословлен:благословлена; ею!', 12, 0, 100, 0, 0, 0, 85179, 0, 0, 0, 'Верховная Говорящая с воронами Крикка to Player');
INSERT INTO `creature_text` VALUES (83649, 1, 0, 'А сейчас пусти по ветру священные перья! Быстрее!', 12, 0, 100, 1, 0, 0, 85184, 0, 0, 0, 'Верховная Говорящая с воронами Крикка to Player');
INSERT INTO `creature_text` VALUES (83649, 2, 0, 'Все на местах? Отлично – начнем!', 12, 0, 100, 1, 0, 0, 85175, 0, 0, 0, 'Верховная Говорящая с воронами Крикка to Player');
INSERT INTO `creature_text` VALUES (83649, 3, 0, 'У нас нет времени, нужно начинать ритуал. Всем занять свои места, попытаемся снова!', 12, 0, 100, 1, 0, 0, 88668, 0, 0, 0, 'Верховная Говорящая с воронами Крикка to Player');
INSERT INTO `creature_text` VALUES (83649, 4, 0, 'Нам нужен сладкий запах. Кинь в огонь что-нибудь ароматное.', 12, 0, 100, 1, 0, 0, 85182, 0, 0, 0, 'Верховная Говорящая с воронами Крикка to Player');
INSERT INTO `creature_text` VALUES (83649, 5, 0, 'Так, теперь плесни в огонь благословенную воду!', 12, 0, 100, 0, 0, 0, 85185, 0, 0, 0, 'Верховная Говорящая с воронами Крикка to Player');
INSERT INTO `creature_text` VALUES (83649, 6, 0, 'Возможно, ты не $gбыл готов:была готова; к ритуалу. Ничего страшного, попробуем снова.', 12, 0, 100, 1, 0, 0, 88672, 0, 0, 0, 'Верховная Говорящая с воронами Крикка to Player');
INSERT INTO `creature_text` VALUES (83649, 6, 1, 'Во имя Прародительницы воронов... Ты вообще меня слушаешь? Давай еще раз.', 12, 0, 100, 1, 0, 0, 88215, 0, 0, 0, 'Верховная Говорящая с воронами Крикка to Player');
INSERT INTO `creature_text` VALUES (83649, 6, 2, 'Не так! Каждая ошибка ослабляет ритуал. Быстро делай, что я скажу, у нас еще может получиться.', 12, 0, 100, 1, 0, 0, 88214, 0, 0, 0, 'Верховная Говорящая с воронами Крикка to Player');
INSERT INTO `creature_text` VALUES (83649, 7, 0, 'Очень хорошо! Признайся, ты $Gрепетировал:репетировала;, пока я была занята?', 12, 0, 100, 0, 0, 0, 85984, 0, 0, 0, 'Верховная Говорящая с воронами Крикка to Player');
INSERT INTO `creature_text` VALUES (83649, 7, 1, 'Да, очень хорошо.', 12, 0, 100, 0, 0, 0, 85122, 0, 0, 0, 'Верховная Говорящая с воронами Крикка to Player');
INSERT INTO `creature_text` VALUES (83649, 7, 2, 'Молодец!', 12, 0, 100, 0, 0, 0, 85121, 0, 0, 0, 'Верховная Говорящая с воронами Крикка to Player');
INSERT INTO `creature_text` VALUES (83649, 7, 3, 'Верно. Ты все правильно делаешь.', 12, 0, 100, 0, 0, 0, 85985, 0, 0, 0, 'Верховная Говорящая с воронами Крикка to Player');
