INSERT INTO `creature_text` VALUES (83823, 3, 0, 'Апекситовые кристаллы часто встречаются в самых опасных местах Дренора. С их помощью можно усилить твое снаряжение.', 12, 0, 100, 1, 0, 44939, 86174, 4015000, 4015000, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (83823, 3, 0, 'Друг мой, думаю, что это начало очень плодотворного сотрудничества. Удачи. ', 12, 0, 100, 1, 0, 44926, 86176, 11000, 11000, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (83823, 3, 0, 'Мы найдем этого дракона во что бы то ни стало.', 12, 0, 100, 5, 0, 44966, 86354, 25000, 25000, 0, 'Верховный маг Кадгар to Player');
