INSERT INTO `creature_text` VALUES (84783, 3, 0, 'Ты $gуверен:уверена; в себе, $n? Не забывай, бой на арене покинуть уже нельзя, надо будет сражаться насмерть!', 12, 0, 100, 1, 0, 0, 86342, 53754000, 53754000, 81882, 'Гаргток to Player');
