INSERT INTO `creature_text` VALUES (84800, 3, 0, 'Да будет так. Кацуо, покажи \"гладиатору\", какой мощью обладает Пылающий Клинок.', 14, 0, 100, 396, 0, 0, 86125, 14000, 14000, 0, 'Азука Ярость Клинка to Player');
