INSERT INTO `creature_text` VALUES (84831, 0, 0, 'Follow da rules. Only brawl in da ring. Uh... and don\'t brawl not in da ring.', 12, 0, 100, 396, 0, 0, 86472, 0, 0, 0, 'Ring Guard to Player');
