INSERT INTO `creature_text` VALUES (84831, 3, 0, 'Если хочешь сражаться, поговори с мелким зеленым существом рядом с Могором.', 12, 0, 100, 396, 0, 0, 86473, 107000, 107000, 0, 'Страж Кольца to Player');
INSERT INTO `creature_text` VALUES (84831, 3, 0, 'Могор запретил тут бузить. Сражаться можно только на ринге.', 12, 0, 100, 396, 0, 0, 86470, 175000, 175000, 0, 'Страж Кольца to Player');
INSERT INTO `creature_text` VALUES (84831, 3, 0, 'Моя убивать тебя, если ты драться не на ринге. Так сказал Могор.', 12, 0, 100, 396, 0, 0, 86474, 53793000, 53793000, 0, 'Страж Кольца to Player');
