INSERT INTO `creature_text` VALUES (84861, 3, 0, 'Быстрее, Огурч! Скажи мне, что ты сейчас чувствуешь!', 12, 0, 100, 1, 0, 0, 86562, 637969, 637969, 0, 'Доктор Хэдли Рикард to Player');
INSERT INTO `creature_text` VALUES (84861, 3, 0, 'Видимо, мне нужен новый ассистент...', 12, 0, 100, 579, 0, 0, 86564, 655125, 655125, 0, 'Доктор Хэдли Рикард to Player');
INSERT INTO `creature_text` VALUES (84861, 3, 0, 'Говорят, Болотень обосновался в подводной пещере... До чего примитивно!', 12, 0, 100, 0, 0, 0, 86559, 411547, 411547, 0, 'Доктор Хэдли Рикард to Player');
INSERT INTO `creature_text` VALUES (84861, 3, 0, 'Как только данные исследования и материалы будут найдены, мы с Огурчом продолжим эксперименты!', 12, 0, 100, 0, 0, 0, 86558, 16157, 16157, 0, 'Доктор Хэдли Рикард to Player');
