INSERT INTO `creature_text` VALUES (84861, 3, 0, 'Подожди, Огурч! Нужно записать результаты эксперимента!', 12, 0, 100, 20, 0, 0, 86563, 647829, 647829, 0, 'Доктор Хэдли Рикард to Player');
INSERT INTO `creature_text` VALUES (84861, 3, 0, 'Чтобы правильно произвести вскрытие, нужна твердая рука и точное...', 12, 0, 100, 0, 0, 0, 86560, 626422, 626422, 0, 'Доктор Хэдли Рикард to Player');
