INSERT INTO `creature_text` VALUES (84868, 4, 0, 'Радуйся победе, захватчик. При следующей нашей встрече ты умрешь в муках!', 12, 0, 100, 0, 0, 0, 86480, 37000, 37000, 0, 'Филарх Вечнозеленый');
INSERT INTO `creature_text` VALUES (84871, 2, 0, 'Гул\'дан выдернет из тебя душу...', 12, 0, 100, 0, 0, 0, 91216, 0, 0, 0, 'Темнорожденная чародейка Скверны to Player');
INSERT INTO `creature_text` VALUES (84873, 4, 0, 'Гул\'дан выдернет из тебя душу...', 12, 0, 100, 0, 0, 0, 91216, 5000, 5000, 0, 'Провидец Скверны to Player');
INSERT INTO `creature_text` VALUES (84880, 3, 0, 'Еще! Еще, еще, еще!', 14, 0, 100, 0, 0, 0, 86587, 608, 608, 0, 'Канонир Скверны to Трофейная пушка Железной Орды');
INSERT INTO `creature_text` VALUES (84891, 3, 0, 'Вот мы и снова встретились, осквернитель! В этих диких землях тебе деваться некуда!', 14, 0, 100, 0, 0, 0, 86481, 0, 0, 0, 'Филарх Вечнозеленый to Player');
INSERT INTO `creature_text` VALUES (84891, 4, 0, 'Ты просто паразит! Жалкая тварь, пытающаяся высосать все соки из моего мира! Но мы этого не допустим!', 12, 0, 100, 0, 0, 0, 86615, 41000, 41000, 0, 'Филарх Вечнозеленый');
INSERT INTO `creature_text` VALUES (84892, 3, 0, 'I am going to your fortress, $ghost:mistress;. When I arrive there, all my knowledge is at your service.', 12, 0, 100, 0, 0, 0, 86485, 71000, 71000, 0, 'Филарх Вечнозеленый to Player');
INSERT INTO `creature_text` VALUES (84892, 3, 0, 'I lost twice. But this will not happen again!', 14, 0, 100, 0, 0, 0, 86484, 0, 0, 0, 'Филарх Вечнозеленый to Player');
