INSERT INTO `creature_text` VALUES (84927, 4, 0, '%s зовет мать на помощь.', 16, 0, 100, 35, 0, 0, 86771, 2000, 2000, 0, 'Детеныш темнокрыла to Player');
INSERT INTO `creature_text` VALUES (84928, 3, 0, 'Встретимся в Окрил\'лоне и вместе дадим бой Гар\'маку.', 12, 0, 100, 274, 0, 0, 84794, 487114, 487114, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (84929, 3, 0, 'Bring those battleplans back to me and we can begin to dismantle this Iron Horde.', 12, 0, 100, 0, 0, 0, 84785, 9750, 9750, 0, 'Vindicator Maraad to Player');
INSERT INTO `creature_text` VALUES (84929, 3, 0, 'Hmm... it appears the Iron Horde has formed an alliance with the Dreadmaul ogres. This is going to be more difficult than I thought.', 12, 0, 100, 0, 0, 0, 84787, 1047141, 1047141, 166313, 'Vindicator Maraad to Player');
INSERT INTO `creature_text` VALUES (84929, 3, 0, 'I will meet you at Okril\'lon. We will take down Gar\'mak together.', 12, 0, 100, 0, 0, 0, 84794, 1356375, 1356375, 0, 'Vindicator Maraad to Player');
INSERT INTO `creature_text` VALUES (84929, 3, 0, 'Plant the Alliance flags inside Nethergarde, our men will do the rest.', 12, 0, 100, 0, 0, 0, 84792, 15469, 15469, 0, 'Vindicator Maraad to Player');
INSERT INTO `creature_text` VALUES (84929, 3, 0, 'We must keep pushing back on the Iron Horde. They need to know we will not sit by and watch as they kill our brothers and sisters.', 12, 0, 100, 0, 0, 0, 84793, 1048906, 1048906, 166314, 'Vindicator Maraad to Player');
INSERT INTO `creature_text` VALUES (84929, 3, 0, 'With Gar\'mak dead the Iron Horde will be in disarray until they appoint a new leader here on Azeroth. Although we have won this battle, I fear the war has just begun.', 12, 0, 100, 0, 0, 0, 84795, 1773828, 1773828, 167518, 'Vindicator Maraad to Player');
INSERT INTO `creature_text` VALUES (84929, 3, 0, 'Встретимся в Окрил\'лоне и вместе дадим бой Гар\'маку.', 12, 0, 100, 0, 0, 0, 84794, 627172, 627172, 0, 'Воздаятель Мараад to Player');
INSERT INTO `creature_text` VALUES (84929, 3, 0, 'Мы ни на минуту не должны ослаблять давление на Железную Орду. Пусть знают, что мы не собираемся сидеть сложа руки, пока они убивают наших братьев и сестер.', 12, 0, 100, 0, 0, 0, 84793, 447984, 447984, 166314, 'Воздаятель Мараад to Player');
INSERT INTO `creature_text` VALUES (84929, 3, 0, 'Принеси мне эти планы, и тогда мы подумаем, как нам справиться с Железной Ордой.', 12, 0, 100, 0, 0, 0, 84785, 8562, 8562, 0, 'Воздаятель Мараад to Player');
INSERT INTO `creature_text` VALUES (84929, 3, 0, 'Со смертью Гар\'мака Железная Орда будет пребывать в смятении, пока они не выберут здесь, в Азероте, нового военачальника. Но хоть эта битва и осталась за нами, я боюсь, что настоящая война только начинается.', 12, 0, 100, 0, 0, 0, 84795, 887422, 887422, 167518, 'Воздаятель Мараад to Player');
INSERT INTO `creature_text` VALUES (84929, 3, 0, 'Установи знамя Альянса в крепости Стражей Пустоты, а об остальном позаботятся наши войска.', 12, 0, 100, 0, 0, 0, 84792, 6890, 6890, 0, 'Воздаятель Мараад to Player');
INSERT INTO `creature_text` VALUES (84929, 3, 0, 'Хм... Похоже, что Железная Орда заключила союз с ограми клана Молота Ужаса. Это несколько осложняет нашу задачу.', 12, 0, 100, 0, 0, 0, 84787, 446172, 446172, 166313, 'Воздаятель Мараад to Player');
INSERT INTO `creature_text` VALUES (84933, 3, 0, 'Спасибо, |3-6($r). Я думал, что застрял тут навечно.', 12, 0, 100, 0, 0, 0, 86653, 152000, 152000, 0, 'Рангари-разведчик to Player');
INSERT INTO `creature_text` VALUES (84962, 3, 0, 'Злоумышленник! Держи свои грязные лапы подальше от апекситовых реликвий!', 12, 0, 100, 0, 0, 0, 88519, 62464, 62464, 0, 'Солнечный ваятель Валарик to Player');
