INSERT INTO `creature_text` VALUES (85081, 3, 0, 'Свет приведет нас к победе.', 12, 0, 100, 0, 0, 0, 142398, 129000, 129000, 0, 'Адмирал Тейлор to Player');
INSERT INTO `creature_text` VALUES (85081, 3, 0, 'Я всегда буду на твоей стороне.', 12, 0, 100, 0, 0, 0, 87797, 167000, 167000, 0, 'Адмирал Тейлор to Player');
INSERT INTO `creature_text` VALUES (85091, 3, 0, 'I\'ll be at your garrison if you need me. Be careful out there.', 12, 0, 100, 396, 0, 0, 86613, 6547, 6547, 0, 'Admiral Taylor to Player');
INSERT INTO `creature_text` VALUES (85091, 3, 0, 'I\'m with you, $p. We will fight shoulder to shoulder.', 12, 0, 100, 396, 0, 0, 86612, 1000, 1000, 0, 'Адмирал Тейлор to Player');
INSERT INTO `creature_text` VALUES (85091, 3, 0, 'We\'re in this together, $p. Side by side.', 12, 0, 100, 396, 0, 0, 86612, 719, 719, 0, 'Admiral Taylor to Player');
INSERT INTO `creature_text` VALUES (85091, 3, 0, 'Если понадоблюсь, я в твоем гарнизоне. Будь $gосторожен:осторожна;!', 12, 0, 100, 396, 0, 0, 86613, 7000, 7000, 0, 'Адмирал Тейлор to Player');
INSERT INTO `creature_text` VALUES (85093, 0, 0, 'Какой могучий зверь! Ты просто молодчина! Мы немедленно доставим его на базу.', 12, 0, 100, 4, 0, 0, 86603, 0, 0, 0, 'Фермер Лок\'луб to Железная ловушка');
INSERT INTO `creature_text` VALUES (85093, 0, 0, 'Шкура этой зверюги прочнее железа! Надо как следует наточить ножи.', 12, 0, 100, 1, 0, 0, 86606, 0, 0, 0, 'Фермер Лок\'луб to Железная ловушка');
INSERT INTO `creature_text` VALUES (85093, 3, 0, 'I smelled of blood - and my expectations were fulfilled!', 12, 0, 100, 1, 0, 0, 86608, 0, 0, 0, 'Фермер Лок\'луб to Железная ловушка');
INSERT INTO `creature_text` VALUES (85093, 3, 0, 'Воздух Награнда – все такой же, каким я его помню. Свежий, с едва уловимыми нотками крови!', 12, 0, 100, 5, 0, 0, 86607, 1000, 1000, 0, 'Фермер Лок\'луб to Железная ловушка');
INSERT INTO `creature_text` VALUES (85093, 3, 0, 'Его запах сразу напомнил мне о дуротарских кабаньих фермах.', 12, 0, 100, 1, 0, 0, 86605, 0, 0, 0, 'Фермер Лок\'луб to Железная ловушка');
INSERT INTO `creature_text` VALUES (85093, 3, 0, 'Замечательный экземпляр. Мы точно соберем с него кучу материалов.', 12, 0, 100, 25, 0, 0, 86604, 1000, 1000, 0, 'Фермер Лок\'луб to Улучшенная железная ловушка');
INSERT INTO `creature_text` VALUES (85093, 3, 0, 'Прекрасная добыча. Ты можешь по праву гордиться ей!', 12, 0, 100, 1, 0, 0, 86609, 1000, 1000, 0, 'Фермер Лок\'луб to Железная ловушка');
INSERT INTO `creature_text` VALUES (85093, 3, 0, 'Просто прекрасная добыча, командир. Я распоряжусь, чтобы ее немедленно доставили в крепость.', 12, 0, 100, 66, 0, 0, 86602, 1000, 1000, 0, 'Фермер Лок\'луб to Улучшенная железная ловушка');
INSERT INTO `creature_text` VALUES (85104, 0, 0, 'Спасибо, $r, но я не стану сражаться с тобой плечом к плечу.', 12, 0, 100, 0, 0, 0, 90133, 0, 0, 0, 'Отважный страж Верных Солнцу to Player');
INSERT INTO `creature_text` VALUES (85121, 4, 0, 'Они... сомнут... тебя...', 12, 0, 100, 0, 0, 0, 86725, 149000, 149000, 0, 'Владычица соблазнов to Player');
INSERT INTO `creature_text` VALUES (85122, 2, 0, 'Мы никогда не победим, если поддадимся страху.', 14, 0, 100, 0, 0, 0, 86704, 0, 0, 0, 'Мальда Хмелепуз to Мальда Хмелепуз');
INSERT INTO `creature_text` VALUES (85122, 3, 0, 'Мы не сдадимся!', 14, 0, 100, 0, 0, 49299, 126628, 1000, 1000, 178086, 'Мальда Хмелепуз to Мальда Хмелепуз');
INSERT INTO `creature_text` VALUES (85129, 2, 0, 'Н-е-ет!', 14, 0, 100, 0, 0, 0, 128399, 0, 0, 178076, 'Чани Малфлейм to Чани Малфлейм');
INSERT INTO `creature_text` VALUES (85129, 3, 0, 'I will not be silent for long.', 14, 0, 100, 0, 0, 0, 86706, 2666, 2666, 178000, 'Чани Малфлейм to Чани Малфлейм');
INSERT INTO `creature_text` VALUES (85129, 4, 0, 'УБИ-И-И-ИТЬ!', 14, 0, 100, 0, 0, 0, 91291, 31530, 31530, 178071, 'Чани Малфлейм to Рубака Копья Войны');
INSERT INTO `creature_text` VALUES (85130, 0, 0, 'Put yer back into it. There ya go!', 12, 0, 100, 603, 0, 0, 86622, 0, 0, 0, 'Glirin to Player');
INSERT INTO `creature_text` VALUES (85130, 1, 0, 'Commander, feel free to use the mole machine to return to yer outpost. It\'ll get ya there. Perhaps.', 12, 0, 100, 1, 0, 0, 86921, 0, 0, 0, 'Glirin to Player');
INSERT INTO `creature_text` VALUES (85131, 2, 0, 'I appreciated your skills.', 14, 0, 100, 0, 0, 0, 86707, 0, 0, 164392, 'Джаред В. Хеллстрайк to Джаред В. Хеллстрайк');
INSERT INTO `creature_text` VALUES (85131, 4, 0, 'Испытание провалено.', 14, 0, 100, 0, 0, 0, 91294, 27671, 27671, 162758, 'Джаред В. Хеллстрайк to 88053');
INSERT INTO `creature_text` VALUES (85131, 4, 0, 'Твои потуги смешны.', 14, 0, 100, 0, 0, 0, 91296, 103386, 103386, 164394, 'Джаред В. Хеллстрайк to Рыцарь Преграды Ветров');
INSERT INTO `creature_text` VALUES (85131, 4, 0, 'Ты и твои навыки – серая посредственность.', 14, 0, 100, 0, 0, 0, 91295, 20000, 20000, 164394, 'Джаред Удар Преисподней to Player');
INSERT INTO `creature_text` VALUES (85133, 3, 0, 'Ар-р-р-ргх, я чувствую кровь!', 14, 0, 100, 0, 0, 0, 91414, 2133, 2133, 178002, 'Мор\'риз to Мор\'риз');
INSERT INTO `creature_text` VALUES (85134, 0, 0, 'Попала! Ха-ха-ха!', 12, 0, 100, 25, 0, 3325, 87047, 0, 0, 0, 'Гарона Полуорчиха to Player');
INSERT INTO `creature_text` VALUES (85134, 3, 0, 'Is venom burning your veins? Don’t be afraid, soon everything will be over!', 12, 0, 100, 0, 0, 44466, 87048, 10000, 10000, 0, 'Гарона Полуорчиха to Player');
INSERT INTO `creature_text` VALUES (85134, 3, 0, 'В погоню за Гароной!', 41, 0, 100, 0, 0, 0, 87413, 4000, 4000, 0, 'Гарона Полуорчиха to Player');
INSERT INTO `creature_text` VALUES (85134, 3, 0, 'Еще держишься? Ты крепче, чем я думала!', 12, 0, 100, 0, 0, 44467, 87049, 12000, 12000, 0, 'Гарона Полуорчиха to Player');
INSERT INTO `creature_text` VALUES (85134, 3, 0, 'Ну же, убей меня. У тебя мало времени.', 12, 0, 100, 0, 0, 44468, 87050, 21000, 21000, 0, 'Гарона Полуорчиха');
INSERT INTO `creature_text` VALUES (85134, 4, 0, 'Гул\'дан берет, что хочет. Всегда. Я ничто.', 12, 0, 100, 0, 0, 44470, 87052, 28000, 28000, 0, 'Гарона Полуорчиха');
INSERT INTO `creature_text` VALUES (85134, 4, 0, 'Слишком поздно. Вашему верховному магу конец!', 12, 0, 100, 0, 0, 44469, 87051, 16000, 16000, 176122, 'Гарона Полуорчиха');
INSERT INTO `creature_text` VALUES (85137, 2, 0, 'Побежден, но не уничтожен.', 14, 0, 100, 0, 0, 0, 86717, 0, 0, 0, 'Матиас Зунн to Матиас Зунн');
INSERT INTO `creature_text` VALUES (85137, 4, 0, 'Ты для меня – ничто.', 14, 0, 100, 0, 0, 0, 91297, 20762, 20762, 177403, 'Матиас Зунн to Рубака Копья Войны');
INSERT INTO `creature_text` VALUES (85137, 4, 0, 'Удача отвернулась от тебя.', 14, 0, 100, 0, 0, 0, 91298, 20000, 22000, 0, 'Матиас Занн to Рубака Копья Войны');
INSERT INTO `creature_text` VALUES (85138, 4, 0, 'Идеально ровный надрез.', 14, 0, 100, 0, 0, 0, 91408, 13710, 13710, 178052, 'Рэзор Герра to Player');
INSERT INTO `creature_text` VALUES (85138, 4, 0, 'Именно поэтому я здесь.', 14, 0, 100, 0, 0, 0, 91409, 62364, 62364, 0, 'Рэзор Герра to 69946');
INSERT INTO `creature_text` VALUES (85138, 4, 0, 'Отрежем лишнее.', 14, 0, 100, 0, 0, 0, 91407, 33017, 33017, 178052, 'Рэзор Герра to Передовой боец Преграды Ветров');
INSERT INTO `creature_text` VALUES (85140, 3, 0, 'Чума, смерть и поражение – вот что ждет вас, ордынская погань!', 14, 0, 100, 0, 0, 0, 86720, 1123, 1123, 178087, 'Анни Оттер to Анни Оттер');
INSERT INTO `creature_text` VALUES (85140, 4, 0, 'Вас сюда не звали.', 14, 0, 100, 0, 0, 0, 91410, 16000, 16000, 164330, 'Анни Оттер to Статуя Черного Быка');
INSERT INTO `creature_text` VALUES (85141, 0, 0, 'Освободи нас!', 14, 0, 100, 0, 0, 43877, 81227, 0, 0, 0, 'Раб из клана Северного Волка to Player');
INSERT INTO `creature_text` VALUES (85142, 0, 0, 'Освободи нас!', 14, 0, 100, 0, 0, 43877, 81227, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (85143, 0, 0, 'See ya at Tangleheart.', 12, 0, 100, 603, 0, 0, 86640, 0, 0, 0, 'Glirin to Player');
INSERT INTO `creature_text` VALUES (85164, 0, 0, 'Miss Kimzee, before we crashed we saw one survivor. I think it might be your uncle.', 12, 0, 100, 1, 0, 0, 86654, 0, 0, 0, 'Mother Lode Crewman to Player');
INSERT INTO `creature_text` VALUES (85173, 0, 0, 'Uncle Krixel!? Get outta the way, I got to rescue him!', 12, 0, 100, 603, 0, 0, 86656, 0, 0, 0, 'Kimzee Pinchwhistle to Player');
INSERT INTO `creature_text` VALUES (85199, 0, 0, 'Кто-о-о... Смеет... Буди-и-ить... МЕНЯ?!', 14, 0, 100, 0, 0, 0, 86669, 0, 0, 0, 'Окаменевший древний to Player');
INSERT INTO `creature_text` VALUES (85213, 0, 0, 'Если Пазерп не вернется в ближайшее время, мне придется послать еще одного разведчика, чтобы найти эту разведчицу. Мы все окажемся в неловком положении.', 12, 0, 100, 0, 0, 0, 86697, 0, 0, 0, 'Боудрик Грей to Player');
INSERT INTO `creature_text` VALUES (85213, 3, 0, 'Hopefully the prisoners you saved will have an opportunity to rebuild Nethergarde to its former glory.', 12, 0, 100, 0, 0, 0, 84781, 235766, 235766, 159388, 'Bodrick Grey to Player');
INSERT INTO `creature_text` VALUES (85213, 3, 0, 'If Pazerp doesn\'t return soon I\'m going to have to send a scout to find my scout. That will be awkward for everybody involved.', 12, 0, 100, 0, 0, 0, 86697, 30750, 30750, 0, 'Bodrick Grey to Player');
INSERT INTO `creature_text` VALUES (85213, 3, 0, 'Nethergarde has lost so much already, I can\'t stand to see them lose more.', 12, 0, 100, 0, 0, 0, 84778, 736922, 736922, 0, 'Bodrick Grey to Player');
INSERT INTO `creature_text` VALUES (85213, 3, 0, 'We have lost too many lives already. This battle is getting out of hand.', 12, 0, 100, 0, 0, 0, 86700, 734250, 734250, 0, 'Bodrick Grey to Player');
INSERT INTO `creature_text` VALUES (85213, 3, 0, 'Будем надеяться, что спасенные тобою пленники со временем вернут крепости Стражей Пустоты ее былое величие.', 12, 0, 100, 0, 0, 0, 84781, 156625, 156625, 159388, 'Боудрик Грей to Player');
INSERT INTO `creature_text` VALUES (85213, 3, 0, 'Крепость Стражей Пустоты уже потеряла стольких защитников... Я не могу допустить больших потерь.', 12, 0, 100, 0, 0, 0, 84778, 73688, 73688, 0, 'Боудрик Грей to Player');
INSERT INTO `creature_text` VALUES (85213, 3, 0, 'Мы уже потеряли слишком многих. Победа ускользает из наших рук.', 12, 0, 100, 0, 0, 0, 86700, 7141, 7141, 0, 'Боудрик Грей to Player');
INSERT INTO `creature_text` VALUES (85214, 4, 0, 'Мне это наскучило. Твое время истекло.', 12, 0, 100, 0, 0, 0, 88471, 10000, 10000, 175936, 'Рассекатель плоти из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (85214, 4, 0, 'Пустая трата времени – а я-то рассчитывал на хорошую драку.', 12, 0, 100, 0, 0, 0, 88472, 18000, 18000, 0, 'Рассекатель плоти из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (85219, 1, 0, 'За клан Изувеченной Длани!', 12, 0, 100, 0, 0, 0, 86937, 0, 0, 0, 'Надсмотрщик Кровавая Погибель to Player');
INSERT INTO `creature_text` VALUES (85219, 1, 0, 'К закату я буду носить твое лицо как маску!', 12, 0, 100, 0, 0, 0, 86936, 0, 0, 0, 'Надсмотрщик Кровавая Погибель to Player');
INSERT INTO `creature_text` VALUES (85219, 1, 0, 'Ты пожалеешь о своем выборе!', 12, 0, 100, 0, 0, 28232, 86938, 0, 0, 0, 'Надсмотрщик Кровавая Погибель to Player');
INSERT INTO `creature_text` VALUES (85221, 3, 0, 'Мой народ. Что они сделали?', 12, 0, 100, 66, 0, 0, 87481, 21000, 21000, 0, 'Страдающая душа to Тотем Бездны');
INSERT INTO `creature_text` VALUES (85223, 3, 0, 'Командир! Я знаю, что меня трудно назвать умником, но мне кажется, что это не самая лучшая идея. Ты действительно этого хочешь?', 12, 0, 100, 1, 0, 0, 86722, 6000, 6000, 0, 'Дровосек to Player');
INSERT INTO `creature_text` VALUES (85223, 3, 0, 'Ну хорошо! Молчание – знак согласия.', 12, 0, 100, 273, 0, 0, 86723, 12000, 12000, 0, 'Дровосек to Player');
INSERT INTO `creature_text` VALUES (85224, 3, 0, 'Босс, я, конечно, не шибко умный, но это дерево какое-то не такое. Может, нам не стоит его рубить?', 12, 0, 100, 6, 0, 0, 86742, 5000, 5000, 0, 'Батрак to Player');
INSERT INTO `creature_text` VALUES (85224, 3, 0, 'Ой! Простите! Не подумал! ВОТ Я ДУРАК! ДУРАК! ДУРАК! Сейчас срубим это дерево, босс!', 12, 0, 100, 273, 0, 0, 86743, 11000, 11000, 0, 'Батрак to Player');
INSERT INTO `creature_text` VALUES (85226, 3, 0, 'My, my, my... another delivery from Goggles\'. What\'s next? Flyin\' Alarm-o-Bots?', 12, 0, 100, 0, 0, 0, 86733, 2327812, 2327812, 0, 'Grendella Cogswitch to Player');
INSERT INTO `creature_text` VALUES (85247, 3, 0, 'Железная Орда – это очень плохо. Нам нужно ее остановить.', 12, 0, 100, 0, 0, 0, 86746, 550194, 550194, 0, 'Рокхан to Player');
INSERT INTO `creature_text` VALUES (85271, 0, 0, 'Вечная тьма ждет!', 14, 0, 100, 0, 0, 0, 86782, 0, 0, 0, 'Курук Древний to Player');
INSERT INTO `creature_text` VALUES (85272, 0, 0, 'Твои кости станут хорошим удобрением!', 14, 0, 100, 0, 0, 0, 86780, 0, 0, 0, 'Лорук Древний to Player');
INSERT INTO `creature_text` VALUES (85273, 0, 0, 'Мощь леса сокрушит тебя!', 14, 0, 100, 0, 0, 0, 86781, 0, 0, 0, 'Ханук Древний to Player');
INSERT INTO `creature_text` VALUES (85274, 0, 0, 'I am the protector of these forests. Get ready for death!', 14, 0, 100, 0, 0, 0, 86783, 0, 0, 0, 'Отук Древний to Player');
INSERT INTO `creature_text` VALUES (85276, 0, 0, 'Великие стихии... что же наделал Чо\'галл?', 12, 0, 100, 0, 0, 0, 86762, 0, 0, 0, 'Предсказатель Дрек\'Тар to Player');
INSERT INTO `creature_text` VALUES (85276, 1, 0, 'Держись, $n! Наару превращается в божество Бездны... у нас нет выбора – мы должны сражаться!', 12, 0, 100, 0, 0, 0, 86763, 0, 0, 0, 'Предсказатель Дрек\'Тар to Player');
INSERT INTO `creature_text` VALUES (85276, 3, 0, 'Великие фурии должны узнать обо всем, что здесь произошло. Скорее за мной!', 12, 0, 100, 0, 0, 0, 86765, 0, 0, 0, 'Предсказатель Дрек\'Тар to Зайка');
INSERT INTO `creature_text` VALUES (85276, 4, 0, 'I feel a chill ... I smell the earth and foliage ... somewhere nearby there should be a way out!', 12, 0, 100, 0, 0, 0, 86766, 0, 0, 0, 'Предсказатель Дрек\'Тар');
INSERT INTO `creature_text` VALUES (85276, 5, 0, 'За мной, $p! Будем биться как волки!', 12, 0, 100, 0, 0, 0, 86792, 0, 0, 0, 'Предсказатель Дрек\'Тар to Истребитий');
INSERT INTO `creature_text` VALUES (85276, 6, 0, 'Проход совсем рядом!', 12, 0, 100, 0, 0, 0, 86823, 0, 0, 0, 'Предсказатель Дрек\'Тар');
INSERT INTO `creature_text` VALUES (85276, 7, 0, 'Я буду ждать тебя у Трона Стихий. Береги себя.', 12, 0, 100, 0, 0, 0, 86822, 0, 0, 0, 'Предсказатель Дрек\'Тар');
INSERT INTO `creature_text` VALUES (85277, 3, 0, 'I will show what I am capable of! I will prove that they are all wrong!', 12, 0, 100, 0, 0, 0, 87910, 0, 0, 172524, 'Железный работник');
INSERT INTO `creature_text` VALUES (85277, 3, 0, 'О, вот как нужно делать подсечку!', 12, 0, 100, 0, 0, 0, 87903, 0, 0, 172524, 'Железный работник');
INSERT INTO `creature_text` VALUES (85310, 3, 0, 'Good, let\'s get in there!', 12, 0, 100, 0, 0, 0, 86770, 2766, 2766, 0, 'Rakthoth to Player');
INSERT INTO `creature_text` VALUES (85310, 3, 0, 'Отлично! Давай туда!', 12, 0, 100, 0, 0, 0, 0, 2528, 2528, 0, 'Рактот to Player');
INSERT INTO `creature_text` VALUES (85310, 3, 0, 'Отлично! Идем туда!', 12, 0, 100, 0, 0, 0, 86770, 2297, 2297, 0, 'Рактот to Player');
INSERT INTO `creature_text` VALUES (85312, 0, 0, 'Их атака захлебывается.', 12, 0, 100, 0, 0, 0, 83929, 0, 0, 0, 'Ружейница Зашедшей Луны to Ружейница Зашедшей Луны');
INSERT INTO `creature_text` VALUES (85312, 0, 0, 'Павший страж превращен в скелет!', 41, 0, 100, 0, 0, 0, 91949, 0, 0, 0, 'Ружейница Зашедшей Луны to Ружейник Зашедшей Луны');
INSERT INTO `creature_text` VALUES (85312, 0, 0, 'Устанешь тут считать. Сколько уже?', 12, 0, 100, 0, 0, 0, 85716, 0, 0, 0, 'Ружейница Зашедшей Луны to Ружейница Зашедшей Луны');
INSERT INTO `creature_text` VALUES (85312, 0, 0, 'Это живые орки или они – нежить?', 12, 0, 100, 0, 0, 0, 89671, 0, 0, 0, 'Ружейник Зашедшей Луны to Ружейник Зашедшей Луны');
INSERT INTO `creature_text` VALUES (85312, 2, 0, 'A fallen guard has been turned into a skeleton!', 41, 0, 100, 0, 0, 0, 91949, 0, 0, 0, 'Lunarfall Rifleman to Lunarfall Rifleman');
INSERT INTO `creature_text` VALUES (85312, 3, 0, 'Danger is close!', 12, 0, 100, 0, 0, 0, 87804, 213407, 213407, 0, 'Lunarfall Rifleman to Lunarfall Rifleman');
INSERT INTO `creature_text` VALUES (85312, 3, 0, 'Врассыпную! Врассыпную!', 12, 0, 100, 0, 0, 0, 83925, 467000, 467000, 0, 'Ружейник Зашедшей Луны to Ружейник Зашедшей Луны');
INSERT INTO `creature_text` VALUES (85312, 3, 0, 'Уводи горожан в безопасное место.', 12, 0, 100, 70, 0, 0, 87805, 330000, 330000, 0, 'Ружейник Зашедшей Луны to Ружейник Зашедшей Луны');
INSERT INTO `creature_text` VALUES (85314, 3, 0, '%s chirps excitedly and flutters away.', 16, 0, 100, 0, 0, 3605, 86773, 2109, 2109, 0, 'Ikky to Player');
INSERT INTO `creature_text` VALUES (85314, 3, 0, '%s возбужденно щебечет и улетает прочь.', 16, 0, 100, 0, 0, 3605, 86773, 2000, 2000, 0, 'Икки to Player');
INSERT INTO `creature_text` VALUES (85315, 0, 0, 'Наару... этого не может быть!', 12, 0, 100, 0, 0, 0, 86767, 0, 0, 0, 'Воздаятель Нобундо to Player');
INSERT INTO `creature_text` VALUES (85315, 1, 0, 'К\'уре трансформируется в божество Бездны... и, кроме нас, некому защитить Дренор от него!', 12, 0, 100, 0, 0, 0, 86768, 0, 0, 0, 'Воздаятель Нобундо to Player');
INSERT INTO `creature_text` VALUES (85315, 2, 0, 'I\'m with you, $p! We will fight this evil together!', 12, 0, 100, 0, 0, 0, 86791, 0, 0, 0, 'Воздаятель Нобундо to Истребитий');
INSERT INTO `creature_text` VALUES (85315, 3, 0, 'У меня разрывается сердце от мысли о потере К\'уре... но у нас нет времени предаваться скорби. Нам нужно выбираться отсюда. За мной!', 12, 0, 100, 0, 0, 0, 86769, 0, 0, 0, 'Воздаятель Нобундо to Зайка');
INSERT INTO `creature_text` VALUES (85315, 4, 0, 'I thought so! There is a way out of here!', 12, 0, 100, 0, 0, 0, 86772, 0, 0, 0, 'Воздаятель Нобундо to Зайка');
INSERT INTO `creature_text` VALUES (85315, 5, 0, 'Выход уже совсем близко!', 12, 0, 100, 0, 0, 0, 86826, 0, 0, 0, 'Воздаятель Нобундо to Зайка');
INSERT INTO `creature_text` VALUES (85315, 6, 0, 'Я буду дожидаться твоего возвращения у Трона Стихий.', 12, 0, 100, 0, 0, 0, 86830, 0, 0, 0, 'Воздаятель Нобундо to Зайка');
INSERT INTO `creature_text` VALUES (85325, 0, 0, 'Что я здесь делаю?! Нужно срочно найти безопасное место!', 12, 0, 100, 603, 0, 0, 86850, 0, 0, 0, 'Зараженный завсегдатай бара to Player');
INSERT INTO `creature_text` VALUES (85325, 0, 1, 'Такое ощущение, будто моим телом управлял кто-то другой...', 12, 0, 100, 603, 0, 0, 86849, 0, 0, 0, 'Зараженный завсегдатай бара to Player');
INSERT INTO `creature_text` VALUES (85325, 0, 2, 'Голова раскалывается...', 12, 0, 100, 603, 0, 0, 86848, 0, 0, 0, 'Зараженный завсегдатай бара to Player');
INSERT INTO `creature_text` VALUES (85325, 0, 3, 'Ух ты! Я снова могу двигать руками и ногами! Спасибо, дружище!', 12, 0, 100, 603, 0, 0, 86851, 0, 0, 0, 'Зараженный завсегдатай бара to Player');
INSERT INTO `creature_text` VALUES (85373, 3, 0, 'Мы узнали имя нового Чемпиона Крови: это $n. Склонитесь перед $gним:ней;, ибо $gон доказал:она доказала;, что $gдостоин:достойна; почестей.', 14, 0, 100, 1, 0, 0, 87853, 199000, 199000, 0, 'Могор to Player');
INSERT INTO `creature_text` VALUES (85378, 3, 0, 'Ah, thank you for the assistance, young peasant. Here is a token of my gratitude.', 12, 0, 100, 0, 0, 0, 86843, 1906, 1906, 0, 'Weldon Barov to Player');
INSERT INTO `creature_text` VALUES (85378, 3, 0, 'Should you be looking to make a few more coppers there will be work for you at your garrison. I will take my leave. Find me there if you wish.', 12, 0, 100, 1, 0, 0, 86845, 18296, 18296, 0, 'Weldon Barov to Player');
INSERT INTO `creature_text` VALUES (85378, 3, 0, 'Если захочешь подзаработать, в гарнизоне тебя будет ждать еще работа. А теперь мне пора. Я буду в гарнизоне на тот случай, если вдруг тебе понадоблюсь.', 12, 0, 100, 1, 0, 0, 86845, 15000, 15000, 0, 'Вэлдон Баров to Player');
INSERT INTO `creature_text` VALUES (85378, 3, 0, 'Спасибо тебе за помощь, $gпростолюдин:простолюдинка;. Прими это в знак моей признательности.', 12, 0, 100, 0, 0, 0, 86843, 2000, 2000, 0, 'Вэлдон Баров to Player');
INSERT INTO `creature_text` VALUES (85379, 3, 0, 'Если захочешь подзаработать, в гарнизоне тебя будет ждать еще работа. А теперь мне пора. Я буду в гарнизоне на тот случай, если вдруг тебе понадоблюсь.', 12, 0, 100, 1, 0, 0, 86845, 15000, 15000, 0, 'Алексий Баров to Player');
INSERT INTO `creature_text` VALUES (85379, 3, 0, 'Спасибо тебе за помощь, $gпростолюдин:простолюдинка;. Прими это в знак моей признательности.', 12, 0, 100, 0, 0, 0, 86843, 2000, 2000, 0, 'Алексий Баров to Player');
INSERT INTO `creature_text` VALUES (85394, 3, 0, 'Мусор! Я отправлю вас туда, где вам самое место!', 12, 0, 100, 0, 0, 0, 86852, 280000, 280000, 0, 'Морах Ледяной Шепот to Player');
INSERT INTO `creature_text` VALUES (85395, 3, 0, 'I\'ll get that scared little girl out from under the log, $g sir:ma\'am;!', 12, 0, 100, 1, 0, 0, 86833, 4937, 4937, 0, 'Lumberjack to Player');
INSERT INTO `creature_text` VALUES (85395, 3, 0, 'Я вытащу эту несчастную малышку из-под бревна, босс!', 12, 0, 100, 1, 0, 0, 86833, 5000, 5000, 0, 'Дровосек to Player');
INSERT INTO `creature_text` VALUES (85396, 3, 0, 'Кажется, тут какая-то женщина застряла, босс! Надо ей помочь!', 12, 0, 100, 1, 0, 0, 86835, 6000, 6000, 0, 'Батрак to Player');
INSERT INTO `creature_text` VALUES (85399, 3, 0, 'ОХ-Х-Х-Х! ПОМОГИТЕ!!! МЕНЯ СЕЙЧАС РАЗДАВИТ!', 12, 0, 100, 0, 0, 0, 86842, 7395, 7395, 0, 'Приглушенный голос to Player');
