INSERT INTO `creature_text` VALUES (85452, 3, 0, 'Прежде чем покинуть мир живых, дух Призрачной Луны благодарит вас.', 16, 0, 100, 0, 0, 0, 86892, 21000, 21000, 170979, 'Тотем Бездны to Player');
