INSERT INTO `creature_text` VALUES (85461, 4, 0, 'Моим мучениям пришел конец.', 12, 0, 100, 0, 0, 0, 88465, 20000, 20000, 0, 'Рубака из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (85483, 3, 0, 'Берегись! Альянс призвал ледяного змея! Уходим с главной дороги!', 14, 0, 100, 0, 0, 8332, 88881, 5820030, 13974928, 0, 'Дозорный на башне to Ледяной змей');
INSERT INTO `creature_text` VALUES (85483, 3, 0, 'Замечены сигнальные огни! Союзники запрашивают помощь. Местоположение отмечено на вашей карте!', 14, 0, 100, 0, 0, 15911, 86867, 7000, 7000, 0, 'Дозорный на башне to S.O.S. Flare Map Icon Vignette Bunny');
INSERT INTO `creature_text` VALUES (85483, 3, 0, 'Слышишь?.. БЕГИ! ЭТО ДИКОЕ СТАДО!', 12, 0, 100, 0, 0, 8332, 89358, 245000, 245000, 0, 'Дозорный на башне to Player');
INSERT INTO `creature_text` VALUES (85496, 3, 0, 'Если он войдет в портал, поросль расползется по всему Азероту!', 12, 0, 100, 25, 0, 0, 86943, 1159000, 1159000, 170741, 'Помощник мага Кесалон to Player');
INSERT INTO `creature_text` VALUES (85496, 3, 0, 'Ты! Может, время еще есть... Портал наверху ведет в наш мир, прямо в Штормград! Генезотавр...', 12, 0, 100, 0, 0, 0, 86942, 1128000, 1128000, 0, 'Помощник мага Кесалон to Player');
INSERT INTO `creature_text` VALUES (85519, 3, 0, 'Битвы питомцев! Прямо здесь – на Арене Славы!', 12, 0, 100, 70, 0, 0, 68897, 346421, 346421, 0, 'Кристоф фон Физель to Player');
INSERT INTO `creature_text` VALUES (85519, 3, 0, 'Испытайте своих питомцев в деле! Сразитесь с лучшими из лучших!', 12, 0, 100, 70, 0, 0, 68900, 50000, 50000, 0, 'Кристоф фон Физель to Player');
INSERT INTO `creature_text` VALUES (85542, 4, 0, 'Жалкий земляной червяк!', 12, 0, 100, 0, 0, 0, 91165, 23000, 23000, 0, 'Бригадир Небесного Пути to Player');
INSERT INTO `creature_text` VALUES (85546, 0, 0, 'Взлети в небеса, приняв вызов огнекрыла!', 12, 0, 100, 0, 0, 0, 87081, 0, 0, 0, 'Зигги Искрец to Player');
INSERT INTO `creature_text` VALUES (85546, 2, 0, 'Тебя ждут отличные призы – только запали шнур!', 12, 0, 100, 0, 0, 0, 87082, 0, 0, 0, 'Зигги Искрец to Player');
INSERT INTO `creature_text` VALUES (85546, 8, 0, 'У меня звезды в глазах? Нет, это лишь еще один соперник, готовый принять вызов огнекрыла!', 12, 0, 100, 0, 0, 0, 87083, 0, 0, 0, 'Зигги Искрец to Player');
INSERT INTO `creature_text` VALUES (85546, 9, 0, 'Эй, ты $gпохож:похожа; на того, кто может оценить прелесть дюжины фейерверков, привязанных к спине!', 12, 0, 100, 0, 0, 0, 87080, 0, 0, 0, 'Зигги Искрец to Player');
INSERT INTO `creature_text` VALUES (85571, 3, 0, 'Gronnlings, attack!', 14, 0, 100, 25, 0, 46590, 87453, 85094, 85094, 0, 'Commander Gar to 723');
