INSERT INTO `creature_text` VALUES (85589, 0, 0, 'Ты потерпел неудачу, Кил\'джеден. Орки не напились крови Маннорота, хотя ты им и обещал.', 12, 0, 100, 274, 0, 0, 88253, 0, 0, 0, 'Архимонд to Player');
INSERT INTO `creature_text` VALUES (85589, 1, 0, 'Меня не волнует твоя месть дренеям... Но их души еще могут оказаться полезными для нас.', 12, 0, 100, 15, 0, 0, 88313, 0, 0, 0, 'Архимонд to Player');
INSERT INTO `creature_text` VALUES (85589, 2, 0, 'Мы захватим этот мир, используя неиссякаемую силу Легиона. Время нежностей прошло.', 12, 0, 100, 274, 0, 0, 88254, 0, 0, 0, 'Архимонд to Player');
INSERT INTO `creature_text` VALUES (85589, 3, 0, 'Орки и дренеи присягнут на верность Легиону. Наши орды выжгут землю под ними. Кил\'джеден, вместе мы непобедимы.', 12, 0, 100, 15, 0, 0, 88327, 0, 0, 0, 'Архимонд to Player');
INSERT INTO `creature_text` VALUES (85591, 3, 0, 'Победа! У тебя получилось!', 12, 0, 100, 0, 0, 44982, 87012, 230000, 230000, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (85591, 3, 0, 'Хорошо, я отвлеку Кайроза, пока вы атакуете.', 12, 0, 100, 1, 0, 44973, 86988, 1000, 1000, 177630, 'Верховный маг Кадгар to Player');
