INSERT INTO `creature_text` VALUES (86296, 3, 0, 'Я буду раздирать тебя на части очень медленно.', 12, 0, 100, 0, 0, 0, 90974, 8000, 8000, 0, 'Забойщик из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (86296, 4, 0, 'Кровь и боль!', 12, 0, 100, 14, 0, 0, 90973, 10000, 10000, 0, 'Забойщик из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (86312, 3, 0, 'Everyone is a little on edge with the Iron Horde\'s presence looming. Your visit here is much appreciated.', 12, 0, 100, 0, 0, 0, 88050, 89360, 89360, 0, 'Lieutenant Kragil to Player');
INSERT INTO `creature_text` VALUES (86312, 3, 0, 'Vivianne is just down the hill. She\'s a bit of a pyro, but her passion for battle is palpable.', 12, 0, 100, 0, 0, 0, 88058, 501094, 501094, 0, 'Lieutenant Kragil to Player');
INSERT INTO `creature_text` VALUES (86312, 3, 0, 'Вивианна чуть ниже по склону. Она, конечно, пироманка, но сражается с огромной страстью.', 12, 0, 100, 0, 0, 0, 88058, 43000, 43000, 0, 'Лейтенант Крейгил to Player');
INSERT INTO `creature_text` VALUES (86312, 3, 0, 'Железная Орда держит в страхе весь Дренор. Мы так благодарны тебе за то, что ты $gприбыл:прибыла; сюда.', 12, 0, 100, 0, 0, 0, 88050, 57502, 57502, 0, 'Лейтенант Крейгил to Player');
INSERT INTO `creature_text` VALUES (86355, 0, 0, 'Встретимся у Надломленного пика.', 12, 0, 100, 0, 0, 0, 88534, 0, 0, 0, 'Анзу to Player');
INSERT INTO `creature_text` VALUES (86385, 3, 0, 'Lieutenant Kragil is just inside. It\'s been a pleasure serving you, commander.', 12, 0, 100, 25, 0, 0, 88049, 27594, 27594, 0, 'Stomphoof to Player');
INSERT INTO `creature_text` VALUES (86385, 3, 0, 'Once the mages opened up the portal from Orgrimmar this place really started to boom. We probably have the greatest assembly of vendors and trade goods this side of Draenor.', 12, 0, 100, 0, 0, 0, 88568, 18344, 18344, 0, 'Stomphoof to Player');
INSERT INTO `creature_text` VALUES (86385, 3, 0, 'The inn is this way, commander.', 12, 0, 100, 0, 0, 0, 87640, 891, 891, 0, 'Stomphoof to Player');
INSERT INTO `creature_text` VALUES (86385, 3, 0, 'Командир, лейтенант Крейгил ждет внутри. Служить тебе – честь для меня.', 12, 0, 100, 25, 0, 0, 88049, 28111, 28111, 0, 'Топокопыт to Player');
INSERT INTO `creature_text` VALUES (86385, 3, 0, 'После того, как маги открыли портал из Оргриммара, мы начали процветать. В этой части Дренора у нас, вероятно, самый широкий выбор товаров и лучшая компания всевозможных торговцев.', 12, 0, 100, 0, 0, 0, 88568, 18658, 18658, 0, 'Топокопыт to Player');
INSERT INTO `creature_text` VALUES (86385, 3, 0, 'Таверна здесь, командир.', 12, 0, 100, 0, 0, 0, 87640, 1373, 1373, 0, 'Топокопыт to Player');
INSERT INTO `creature_text` VALUES (86398, 1, 0, 'Умри, $gчужак:чужестранка;!', 12, 0, 100, 11, 0, 0, 104834, 0, 0, 0, 'Награндский боец to Player');
INSERT INTO `creature_text` VALUES (86401, 3, 0, 'The Iron Horde have no idea how much potential lies within the artifact. Let us give them a proper demonstration, shall we?', 12, 0, 100, 1, 0, 0, 87477, 15703, 15703, 0, 'Draka to Player');
INSERT INTO `creature_text` VALUES (86401, 3, 0, 'Железная Орда даже не представляет, какая сила скрывается в артефакте. Быть может, стоит провести для них демонстрацию, как считаешь?', 12, 0, 100, 1, 0, 0, 87477, 155923, 155923, 0, 'Дрека to Player');
INSERT INTO `creature_text` VALUES (86403, 4, 0, 'Спасибо, командир.', 12, 0, 100, 0, 0, 0, 87672, 101197, 101197, 0, 'Древорез to Player');
INSERT INTO `creature_text` VALUES (86405, 3, 0, 'Обожаю свою работу.', 12, 0, 100, 390, 0, 0, 88157, 2000, 2000, 0, 'Ужасный мучитель');
INSERT INTO `creature_text` VALUES (86406, 3, 0, 'Тебе конец.', 12, 0, 100, 0, 0, 0, 122854, 204000, 204000, 0, 'Налетчик из клана Песни Войны to Player');
INSERT INTO `creature_text` VALUES (86408, 0, 0, 'How long does he plan to take? We can\'t stay holed up here forever with the Horde breathing down our necks.', 12, 0, 100, 6, 0, 0, 89745, 0, 0, 0, 'Stormshield Guard to Stormshield Guard');
INSERT INTO `creature_text` VALUES (86408, 1, 0, 'Орда смелеет с каждым днем. Нынче утром они снова напали на одну из наших застав.', 12, 0, 100, 1, 0, 0, 89747, 0, 0, 0, 'Страж Преграды Ветров to Страж Преграды Ветров');
INSERT INTO `creature_text` VALUES (86408, 2, 0, 'Все к тому. Мне кажется, они боятся, что мы станем использовать артефакт против них, если найдем его.', 12, 0, 100, 1, 0, 0, 89749, 0, 0, 0, 'Страж Преграды Ветров to Страж Преграды Ветров');
INSERT INTO `creature_text` VALUES (86408, 3, 0, 'Сколько на это потребуется времени? Мы не можем отсиживаться тут вечно – Орда уже дышит нам в затылок.', 12, 0, 100, 6, 0, 0, 89745, 0, 0, 0, 'Страж Преграды Ветров to Страж Преграды Ветров');
INSERT INTO `creature_text` VALUES (86411, 3, 0, 'Иди. Я задержу всех, кто решит преследовать тебя.', 12, 0, 100, 0, 0, 46706, 83975, 1000, 1000, 0, 'Предсказатель Дрек\'Тар to Player');
INSERT INTO `creature_text` VALUES (86412, 3, 0, 'Ступай. Я защищу тебя от бледнокожих.', 12, 0, 100, 0, 0, 45917, 84162, 0, 0, 0, 'Воздаятель Нобундо to Player');
INSERT INTO `creature_text` VALUES (86413, 3, 0, 'Надеюсь, осталось недолго. Я так скучаю по своей семье в Штормграде...', 12, 0, 100, 1, 0, 0, 89746, 134023, 134023, 0, 'Страж Преграды Ветров');
INSERT INTO `creature_text` VALUES (86416, 1, 0, 'I want to see your blood.', 12, 0, 100, 14, 0, 0, 90972, 461000, 461000, 0, 'Поработитель из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (86416, 1, 0, 'Кричи... кричи для меня!', 12, 0, 100, 0, 0, 0, 90971, 0, 0, 0, 'Поработитель из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (86416, 1, 0, 'Кровь и боль!', 12, 0, 100, 0, 0, 0, 90973, 0, 0, 0, 'Поработитель из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (86416, 1, 0, 'Я буду раздирать тебя на части очень медленно.', 12, 0, 100, 0, 0, 0, 90974, 0, 0, 0, 'Поработитель из клана Изувеченной Длани to Player');
INSERT INTO `creature_text` VALUES (86419, 0, 0, 'Let us get to work.', 12, 0, 100, 0, 0, 0, 113786, 0, 0, 0, 'Talon Guard Kurekk to Player');
INSERT INTO `creature_text` VALUES (86419, 1, 0, 'Scaly vermin!', 12, 0, 100, 0, 0, 0, 88208, 0, 0, 0, 'Talon Guard Kurekk to Player');
INSERT INTO `creature_text` VALUES (86419, 2, 0, 'In killing Sethe, Anzu was the first to bear his dying curse.', 12, 0, 100, 0, 0, 0, 88162, 0, 0, 0, 'Talon Guard Kurekk to Player');
INSERT INTO `creature_text` VALUES (86419, 3, 0, 'Ra-aawk! For Anzu!', 12, 0, 100, 0, 0, 0, 88234, 0, 0, 0, 'Talon Guard Kurekk to Player');
INSERT INTO `creature_text` VALUES (86419, 4, 0, 'As he grappled with the curse, Anzu commanded his dread ravens to eradicate the brood of Sethe.', 12, 0, 100, 0, 0, 0, 88163, 0, 0, 0, 'Talon Guard Kurekk to Player');
INSERT INTO `creature_text` VALUES (86419, 5, 0, 'In one bloody night, the children of Sethe were eradicated.', 12, 0, 100, 0, 0, 0, 88186, 0, 0, 0, 'Talon Guard Kurekk to Player');
INSERT INTO `creature_text` VALUES (86419, 7, 0, 'Back, you beast!', 12, 0, 100, 0, 0, 0, 88233, 0, 0, 0, 'Talon Guard Kurekk to Player');
INSERT INTO `creature_text` VALUES (86419, 9, 0, 'It seems the wind serpents, like their master, refuse to stay dead.', 12, 0, 100, 0, 0, 0, 88190, 0, 0, 0, 'Talon Guard Kurekk to Player');
INSERT INTO `creature_text` VALUES (86419, 12, 0, 'You follow a false king!', 12, 0, 100, 0, 0, 0, 88229, 0, 0, 0, 'Talon Guard Kurekk to Player');
INSERT INTO `creature_text` VALUES (86419, 13, 0, 'Let us make for the Talon Altar.', 12, 0, 100, 0, 0, 0, 88239, 0, 0, 0, 'Talon Guard Kurekk to Player');
INSERT INTO `creature_text` VALUES (86419, 14, 0, 'Идем к Алтарю Когтя.', 12, 0, 100, 0, 0, 0, 88239, 0, 0, 0, 'Коготарь Курекк to Player');
INSERT INTO `creature_text` VALUES (86419, 17, 0, 'Во имя Терокка!', 12, 0, 100, 0, 0, 0, 88231, 0, 0, 0, 'Коготарь Курекк to Player');
INSERT INTO `creature_text` VALUES (86419, 18, 0, 'Ра-авк! За Анзу!', 12, 0, 100, 0, 0, 0, 88234, 0, 0, 0, 'Коготарь Курекк to Player');
INSERT INTO `creature_text` VALUES (86419, 20, 0, 'Мерзкий культист!', 12, 0, 100, 0, 0, 0, 88230, 0, 0, 0, 'Коготарь Курекк to Player');
INSERT INTO `creature_text` VALUES (86419, 22, 0, 'Кто додумался приделать змее крылья, а?!', 12, 0, 100, 0, 0, 0, 88235, 0, 0, 0, 'Коготарь Курекк to Player');
INSERT INTO `creature_text` VALUES (86419, 24, 0, 'Мерзкий вредитель!', 12, 0, 100, 0, 0, 0, 88208, 0, 0, 0, 'Коготарь Курекк to Player');
INSERT INTO `creature_text` VALUES (86460, 0, 0, 'Вот как? И почему это?', 12, 0, 100, 6, 0, 0, 90518, 0, 0, 0, 'Рубака Копья Войны to Рубака Копья Войны');
INSERT INTO `creature_text` VALUES (86460, 1, 0, 'Ха! Да кому нужно это пыльное старье? Я здесь затем, чтобы пробивать черепа воинам Альянса!', 12, 0, 100, 66, 0, 0, 90508, 0, 0, 0, 'Рубака Копья Войны to Рубака Копья Войны');
INSERT INTO `creature_text` VALUES (86460, 2, 0, 'Для меня важно только то, что мы снова воюем с людьми. Мечтаю как следует подраться!', 12, 0, 100, 1, 0, 0, 90510, 0, 0, 0, 'Рубака Копья Войны to Рубака Копья Войны');
INSERT INTO `creature_text` VALUES (86460, 4, 0, 'Души огров? Тогда я их понимаю. Нет, я не боюсь призраков – но мне нравятся враги, которых можно зарубить топором.', 12, 0, 100, 5, 0, 0, 90521, 0, 0, 0, 'Рубака Копья Войны to Рубака Копья Войны');
INSERT INTO `creature_text` VALUES (86460, 5, 0, 'Что, здоровенную секиру?', 12, 0, 100, 6, 0, 0, 90506, 0, 0, 0, 'Рубака Копья Войны to Рубака Копья Войны');
INSERT INTO `creature_text` VALUES (86461, 1, 0, 'I want to hear your screams as I strip your flesh!', 12, 0, 100, 0, 0, 0, 90970, 0, 0, 0, 'Багдот Кровопийца to Player');
INSERT INTO `creature_text` VALUES (86461, 3, 0, 'Какая красивая кожа. Пожалуй, я оставлю ее себе после того, как прикончу тебя.', 12, 0, 100, 0, 0, 0, 90975, 152000, 152000, 0, 'Багдот Кровопийца to Player');
INSERT INTO `creature_text` VALUES (86472, 0, 0, 'Знаешь, что случилось? Эльфы крови говорят, что обнаружили в шахтах нечто огромное.', 12, 0, 100, 1, 0, 0, 90505, 0, 0, 0, 'Рубака Копья Войны');
INSERT INTO `creature_text` VALUES (86472, 3, 0, 'Воины, которые там побывали, рассказывают про каких-то странных призраков. Они говорят, что некроманты порабощают души древних огров.', 12, 0, 100, 1, 0, 0, 90520, 51380, 180288, 0, 'Рубака Копья Войны');
INSERT INTO `creature_text` VALUES (86472, 3, 0, 'Не секиру, тупица! Они говорят, что это часть большого артефакта, который очень нужен Альянсу.', 12, 0, 100, 5, 0, 0, 90507, 18608, 18608, 0, 'Рубака Копья Войны');
INSERT INTO `creature_text` VALUES (86472, 3, 0, 'Сегодня я говорила с полководцем Волратом. Ему совсем не нравится то, что наши войска не хотят идти по кладбищу.', 12, 0, 100, 1, 0, 0, 90516, 17017, 17017, 0, 'Рубака Копья Войны');
INSERT INTO `creature_text` VALUES (86472, 3, 0, 'Ты хоть понимаешь, зачем мы здесь, на Ашране?', 12, 0, 100, 6, 0, 0, 90509, 65670, 1144929, 0, 'Рубака Копья Войны');
INSERT INTO `creature_text` VALUES (86482, 3, 0, 'Как ты сюда $gдобрался:добралась;?!', 12, 0, 100, 0, 0, 0, 90979, 82000, 82000, 0, 'Вал\'дун Плоторез to Player');
INSERT INTO `creature_text` VALUES (86482, 3, 0, 'Что ты здесь делаешь?!', 12, 0, 100, 0, 0, 0, 90980, 191000, 191000, 0, 'Вал\'дун Плоторез to Player');
INSERT INTO `creature_text` VALUES (86484, 3, 0, 'How are ya?', 12, 0, 100, 0, 0, 0, 88307, 251422, 251422, 0, 'Glirin to Player');
INSERT INTO `creature_text` VALUES (86484, 3, 0, 'Твое здоровье!', 12, 0, 100, 0, 0, 49521, 88311, 33803, 33803, 0, 'Глирин to Player');
INSERT INTO `creature_text` VALUES (86499, 3, 0, 'Черноруку так не помочь! Быстрее!', 12, 0, 100, 0, 0, 0, 88218, 92000, 92000, 0, 'Железный страж');
INSERT INTO `creature_text` VALUES (86501, 3, 0, 'I\'m going to your garrison, commander!', 12, 0, 100, 3, 0, 0, 88228, 889, 889, 0, 'Пенни Поппинстук to Player');
INSERT INTO `creature_text` VALUES (86502, 3, 0, 'I\'ll see ya at yer garrison, commander!', 12, 0, 100, 603, 0, 0, 88232, 2078, 2078, 0, 'Glirin to Player');
INSERT INTO `creature_text` VALUES (86502, 3, 0, 'Увидимся в гарнизоне, командир!', 12, 0, 100, 603, 0, 0, 88232, 2000, 2000, 0, 'Глирин to Player');
INSERT INTO `creature_text` VALUES (86503, 3, 0, 'Let us make haste to Khadgar and the others.', 12, 0, 100, 396, 0, 0, 88240, 1375, 1375, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (86503, 3, 0, 'Скорее к Кадгару и остальным.', 12, 0, 100, 396, 0, 0, 88240, 1000, 1000, 0, 'Ирель to Player');
INSERT INTO `creature_text` VALUES (86510, 3, 0, 'Commander, you have lovely bone structure.', 12, 0, 100, 0, 0, 0, 88336, 772500, 772500, 0, 'Spirit of Bony Xuk to Player');
INSERT INTO `creature_text` VALUES (86510, 3, 0, 'Good to see your bones are still intact, commander.', 12, 0, 100, 0, 0, 0, 88332, 893922, 893922, 0, 'Spirit of Bony Xuk to Player');
INSERT INTO `creature_text` VALUES (86510, 3, 0, 'Командир, у тебя просто идеальный скелет.', 12, 0, 100, 0, 0, 42439, 88336, 89000, 89000, 0, 'Дух Костлявого Зака to Player');
INSERT INTO `creature_text` VALUES (86514, 3, 0, 'Аркенон порос.', 12, 0, 100, 0, 0, 0, 142392, 58000, 58000, 0, 'Рангари Эрдани to Player');
INSERT INTO `creature_text` VALUES (86514, 3, 0, 'Большое спасибо, что $gпригласил:пригласила; меня сюда.', 12, 0, 100, 0, 0, 0, 88364, 803000, 803000, 0, 'Рангари Эрдани to Player');
INSERT INTO `creature_text` VALUES (86514, 3, 0, 'Да будет судьба милостива к тебе.', 12, 0, 100, 0, 0, 0, 88365, 78000, 82000, 0, 'Рангари Эрдани to Player');
INSERT INTO `creature_text` VALUES (86514, 3, 0, 'Командир.', 12, 0, 100, 0, 0, 0, 89429, 4139000, 4139000, 0, 'Рангари Эрдани to Player');
INSERT INTO `creature_text` VALUES (86514, 3, 0, 'Привет, командир.', 12, 0, 100, 0, 0, 0, 88361, 100000, 100000, 0, 'Рангари Эрдани to Player');
INSERT INTO `creature_text` VALUES (86514, 3, 0, 'Рада снова видеть тебя.', 12, 0, 100, 2, 0, 0, 88363, 17000, 17000, 0, 'Рангари Эрдани to Player');
INSERT INTO `creature_text` VALUES (86515, 3, 0, 'Этот город принадлежит Легиону!', 12, 0, 100, 0, 0, 0, 91212, 47000, 47000, 0, 'Наездница на террокрыле');
INSERT INTO `creature_text` VALUES (86515, 4, 0, 'Ты ничего не $gдобился:добилась;.', 12, 0, 100, 0, 0, 0, 91215, 30000, 30000, 0, 'Наездница на террокрыле to Player');
INSERT INTO `creature_text` VALUES (86516, 0, 0, 'Идем. Кадгар недалеко.', 12, 0, 100, 396, 0, 0, 88242, 0, 0, 0, 'Дуротан to Player');
INSERT INTO `creature_text` VALUES (86521, 3, 0, 'I want to get into your garrison soon, commander.', 12, 0, 100, 0, 0, 0, 88246, 2000, 2000, 0, 'Рангари Эрдани to Player');
INSERT INTO `creature_text` VALUES (86526, 0, 0, 'Вам не уйти отсюда живыми!', 14, 0, 100, 603, 0, 43903, 86418, 0, 0, 0, 'Гром\'карский мастер цепей to Player');
INSERT INTO `creature_text` VALUES (86533, 3, 0, 'Ты уже раз $gспас:спасла; мне жизнь – пора мне и вернуть должок!', 14, 0, 100, 0, 0, 0, 88389, 5000, 5000, 0, 'Ауби to Player');
INSERT INTO `creature_text` VALUES (86535, 1, 0, 'О, ты пришла освободить эту жалкую драконицу, охотница на демонов?', 12, 0, 100, 0, 0, 0, 93006, 0, 0, 0, 'Надзиратель Ликилл to Player');
INSERT INTO `creature_text` VALUES (86535, 2, 0, 'Я еще вернусь!', 12, 0, 100, 0, 0, 57400, 93273, 0, 0, 0, 'Надзиратель Ликилл to Кор\'вас Кровавый Шип');
INSERT INTO `creature_text` VALUES (86535, 3, 0, 'В моей клетке еще достаточно места для ваших душ.', 14, 0, 100, 0, 0, 0, 97407, 154314, 154314, 0, 'Надзиратель Ликилл');
