INSERT INTO `creature_text` VALUES (86763, 3, 0, 'I heard recently found something really interesting in the mines. Harrison, however, is still silent like a fish.', 12, 0, 100, 0, 0, 0, 89842, 9000, 9000, 0, 'Стивен Хиклин to Филипп Хилленбранд');
