INSERT INTO `creature_text` VALUES (86763, 3, 0, 'So I hear that they\'re uncovering some interesting stuff in the mines. Harrison is staying pretty hush hush about it though.', 12, 0, 100, 0, 0, 0, 89842, 277500, 428000, 0, 'Stephen Hicklin to Phillip Hillenbrand');
