INSERT INTO `creature_text` VALUES (88066, 3, 0, 'All right, all right! I\'ll talk! Just call off your goons!', 12, 0, 100, 0, 0, 0, 90209, 435953, 435953, 0, 'Captured Blackfuse Engineer to Bruiser Snaptoe');
INSERT INTO `creature_text` VALUES (88074, 3, 0, 'Ну так что, расскажешь о тех машинах, что ты со своими ведьмами делал для Железной Орды?', 12, 0, 100, 1, 0, 0, 90203, 126422, 126422, 0, 'Костоломша Ногохват');
INSERT INTO `creature_text` VALUES (88074, 3, 0, 'Ну что, этого достаточно, чтобы развязать тебе язык? Или тебя нужно \"убеждать\" дальше?', 12, 0, 100, 6, 0, 0, 90206, 9000, 9000, 0, 'Костоломша Ногохват');
INSERT INTO `creature_text` VALUES (88074, 3, 0, 'Ребята, займитесь \"убеждением\".', 12, 0, 100, 25, 0, 0, 90208, 958000, 958000, 0, 'Костоломша Ногохват');
INSERT INTO `creature_text` VALUES (88086, 1, 0, 'За Назжатар!', 12, 0, 100, 0, 0, 52685, 97411, 0, 0, 0, 'Хвостодер из клана Колец Ненависти to Player');
INSERT INTO `creature_text` VALUES (88086, 1, 0, 'Землеходы нам не ровня.', 12, 0, 100, 0, 0, 52686, 97408, 0, 0, 0, 'Хвостодер из клана Колец Ненависти to Player');
INSERT INTO `creature_text` VALUES (88086, 1, 0, 'Море поглотит твой труп.', 12, 0, 100, 0, 0, 52681, 97410, 0, 0, 0, 'Хвостодер из клана Колец Ненависти to Player');
INSERT INTO `creature_text` VALUES (88086, 1, 0, 'Смерть землеходам!', 12, 0, 100, 0, 0, 52684, 97409, 0, 0, 0, 'Хвостодер из клана Колец Ненависти to Player');
INSERT INTO `creature_text` VALUES (88086, 1, 0, 'Я заточу трезубец о твои кости.', 12, 0, 100, 38, 0, 52681, 92731, 0, 0, 0, 'Хвостодер из клана Колец Ненависти to Player');
INSERT INTO `creature_text` VALUES (88087, 1, 0, 'Гнев Азшары поглотит тебя!', 12, 0, 100, 0, 0, 52706, 92728, 0, 0, 0, 'Призывательница штормов из клана Колец Ненависти to Player');
INSERT INTO `creature_text` VALUES (88087, 1, 0, 'Прилив сметет тебя.', 12, 0, 100, 0, 0, 52702, 97416, 0, 0, 0, 'Призывательница штормов из клана Колец Ненависти to Player');
INSERT INTO `creature_text` VALUES (88087, 1, 0, 'Умри, землеход!', 12, 0, 100, 0, 0, 52704, 97415, 0, 0, 0, 'Призывательница штормов из клана Колец Ненависти to Player');
INSERT INTO `creature_text` VALUES (88089, 1, 0, 'Гнев Азшары поглотит тебя!', 12, 0, 100, 0, 0, 52706, 92728, 0, 0, 0, 'Чародейка из клана Колец Ненависти to Player');
INSERT INTO `creature_text` VALUES (88089, 1, 0, 'Хочешь погрузиться в пучину?', 12, 0, 100, 0, 0, 52700, 92729, 0, 0, 0, 'Чародейка из клана Колец Ненависти to Player');
INSERT INTO `creature_text` VALUES (88090, 0, 0, 'Твоя смелость мне нравится, $gмалыш:малышка,. Я с радостью выпущу тебе кишки на глазах у всего моего батальона.', 12, 0, 100, 0, 0, 0, 91649, 0, 0, 0, 'Повелитель пучин Заррин to Player');
INSERT INTO `creature_text` VALUES (88090, 1, 0, 'Госпожа Атисса! Я... подвел тебя...', 14, 0, 100, 0, 0, 0, 91650, 0, 0, 0, 'Повелитель пучин Заррин to Player');
INSERT INTO `creature_text` VALUES (88091, 0, 0, 'Довольно! Приливный камень будет нашим!', 14, 0, 100, 0, 0, 0, 92395, 0, 0, 0, 'Атисса to 0');
INSERT INTO `creature_text` VALUES (88091, 2, 0, 'Ничтожество…', 12, 0, 100, 0, 0, 0, 102162, 0, 0, 0, 'Атисса to Player');
INSERT INTO `creature_text` VALUES (88109, 2, 0, 'Мое тело повержено, но дух воспарит. Я всегда буду с тобой.', 14, 0, 100, 0, 0, 0, 90218, 0, 0, 0, 'Кимилин to Кимилин');
INSERT INTO `creature_text` VALUES (88109, 3, 0, 'Моя мощь, закаленная пламенем, безгранична, а мои товарищи не ведают страха!', 14, 0, 100, 0, 0, 0, 90216, 1908, 1908, 178002, 'Кимилин to Кимилин');
INSERT INTO `creature_text` VALUES (88115, 0, 0, 'Прощайте, стражи... хотя вы вряд ли будете по мне скучать.', 12, 0, 100, 0, 0, 52804, 91592, 0, 0, 0, 'Принц Фарондис to Player');
INSERT INTO `creature_text` VALUES (88117, 3, 0, 'Веселей, ночные стражи! Мы нужны принцу!', 14, 0, 100, 0, 0, 52778, 93579, 1354, 1354, 0, 'Ночной страж Талдрис');
INSERT INTO `creature_text` VALUES (88138, 3, 0, 'Наверное, Альянсу позарез нужен этот артефакт, раз он бросает против нас таких врагов.', 12, 0, 100, 1, 0, 0, 90500, 24944, 24944, 0, 'Темная охотница Аския to Рала Дикое Сердце');
INSERT INTO `creature_text` VALUES (88138, 3, 0, 'Наверное, это сильно осложнит тебе задачу на поле боя.', 12, 0, 100, 1, 0, 0, 90503, 15967, 15967, 0, 'Темная охотница Аския to Рала Дикое Сердце');
INSERT INTO `creature_text` VALUES (88165, 3, 0, 'Чего желаешь, командир?', 12, 0, 100, 0, 0, 0, 90613, 734034, 734034, 0, 'Темный следопыт Велонара to Player');
INSERT INTO `creature_text` VALUES (88173, 0, 0, 'Спасибо, командир. Увидимся в твоем гарнизоне.', 12, 0, 100, 1, 0, 0, 90336, 0, 0, 0, 'Темный следопыт Велонара to Player');
INSERT INTO `creature_text` VALUES (88181, 3, 0, 'Возможно, они боятся, что мы используем его против них. Именно поэтому они сражаются столь отчаянно.', 12, 0, 100, 1, 0, 0, 90333, 505000, 505000, 0, 'Жрица Лления');
INSERT INTO `creature_text` VALUES (88181, 3, 0, 'Надеюсь, этого не произойдет, Мишал.', 12, 0, 100, 1, 0, 0, 90329, 13549, 13549, 0, 'Жрица Лления');
INSERT INTO `creature_text` VALUES (88187, 0, 0, 'Сделай так, чтобы эти огроны вернулись к работе!', 14, 0, 100, 0, 0, 0, 90318, 0, 0, 0, 'Убийца рабов');
INSERT INTO `creature_text` VALUES (88193, 3, 0, 'Командир, тебя на минутку можно?', 12, 0, 100, 6, 0, 0, 90346, 20000, 20000, 0, 'Гульда Теневой Клинок to Player');
INSERT INTO `creature_text` VALUES (88198, 3, 0, 'Спасибо, командир. Встретимся в твоем гарнизоне.', 12, 0, 100, 1, 0, 0, 90349, 0, 0, 0, 'Гульда Теневой Клинок to Player');
INSERT INTO `creature_text` VALUES (88207, 4, 0, 'На колени, ничтожество!', 14, 0, 100, 0, 0, 0, 112925, 8000, 67000, 176552, 'Работорговец Кольца Крови to Player');
INSERT INTO `creature_text` VALUES (88207, 4, 0, 'Сто золотых огру, который принесет мне голову этого недоразумения!', 14, 0, 100, 0, 0, 0, 90648, 10000, 23000, 0, 'Работорговец Кольца Крови');
INSERT INTO `creature_text` VALUES (88210, 0, 0, 'Круд здесь! Любой тошнотик, который подумает, что может убить Круда, пусть копит золото. Такая честь дорого стоит.', 14, 0, 100, 0, 0, 0, 90683, 0, 0, 0, 'Круд Потрошитель');
INSERT INTO `creature_text` VALUES (88210, 3, 0, 'Неплохо для кучки бестолковых слабаков! Сразитесь с Крудом, когда решите помереть!', 14, 0, 100, 0, 0, 0, 91641, 46000, 46000, 0, 'Круд Потрошитель to Player');
INSERT INTO `creature_text` VALUES (88211, 0, 0, 'Да где эта Колебрямс? Сколько можно чинить проклятый механотанк!', 12, 0, 100, 6, 0, 0, 90367, 0, 0, 0, 'Шестер Короткомер');
INSERT INTO `creature_text` VALUES (88211, 0, 0, 'Долго еще, Пружинавс? Нам этот танк полдня назад был нужен!', 12, 0, 100, 5, 0, 0, 90366, 0, 0, 0, 'Шестер Короткомер');
INSERT INTO `creature_text` VALUES (88211, 0, 0, 'Искрюль, нужно удвоить производительность булыжно-долбежного робота. Или ты думаешь, что Харрисон Джонс будет расчищать шахту с киркой в руках?', 12, 0, 100, 22, 0, 0, 90365, 0, 0, 0, 'Шестер Короткомер');
INSERT INTO `creature_text` VALUES (88211, 0, 0, 'Ускорямс, чини уже скорее этот танк. Он нужен нашим ребятам на передовой!', 12, 0, 100, 25, 0, 0, 90364, 0, 0, 0, 'Шестер Короткомер');
INSERT INTO `creature_text` VALUES (88223, 3, 0, '$GSir:Madam;, we\'ll assemble the troops at your command.', 12, 0, 100, 0, 0, 0, 84138, 80718, 80718, 0, 'Sergeant Crowler to Player');
INSERT INTO `creature_text` VALUES (88223, 3, 0, 'Do you feel that chill? Like something stomping on your grave...', 12, 0, 100, 0, 0, 0, 86882, 188922, 188922, 164060, 'Sergeant Crowler');
INSERT INTO `creature_text` VALUES (88223, 3, 0, 'Don\'t fear, brothers and sisters. We\'ve fought orcs for decades.', 12, 0, 100, 0, 0, 0, 86881, 179593, 179593, 0, 'Sergeant Crowler');
INSERT INTO `creature_text` VALUES (88223, 3, 0, 'It is here. Charge! For the Alliance!', 14, 0, 100, 0, 0, 0, 86888, 707656, 707656, 0, 'Sergeant Crowler');
INSERT INTO `creature_text` VALUES (88223, 3, 0, 'It must be the Shadowmoon Clan. Sound the alarm!', 12, 0, 100, 0, 0, 8333, 86879, 148828, 148828, 0, 'Sergeant Crowler to Player');
INSERT INTO `creature_text` VALUES (88223, 3, 0, 'Spread out! Protect the structures!', 14, 0, 100, 0, 0, 0, 90434, 295750, 295750, 0, 'Sergeant Crowler');
INSERT INTO `creature_text` VALUES (88223, 3, 0, 'Take heart. It is always darkest before dawn.', 14, 0, 100, 0, 0, 0, 86887, 700265, 700265, 166541, 'Sergeant Crowler');
INSERT INTO `creature_text` VALUES (88223, 3, 0, 'There\'s a chill in the air...', 12, 0, 100, 0, 0, 0, 86878, 131797, 131797, 0, 'Sergeant Crowler to Player');
INSERT INTO `creature_text` VALUES (88223, 3, 0, 'To arms! To your posts! The Shadowmoon Clan has a score to settle.', 14, 0, 100, 22, 0, 0, 86880, 157312, 157312, 0, 'Sergeant Crowler to Player');
INSERT INTO `creature_text` VALUES (88223, 3, 0, 'Должно быть, это клан Призрачной Луны. Трубите тревогу!', 12, 0, 100, 0, 0, 0, 86879, 294000, 294000, 0, 'Сержант Краулер to Player');
INSERT INTO `creature_text` VALUES (88223, 3, 0, 'К оружию! По местам! За кланом Призрачной Луны должок.', 14, 0, 100, 22, 0, 8333, 86880, 299000, 299000, 0, 'Сержант Краулер to Player');
INSERT INTO `creature_text` VALUES (88223, 3, 0, 'Командир, по твоей команде мы соберем войска.', 12, 0, 100, 0, 0, 0, 84138, 114000, 114000, 0, 'Сержант Краулер to Player');
INSERT INTO `creature_text` VALUES (88223, 3, 0, 'Не падайте духом. Самый темный час – перед рассветом.', 14, 0, 100, 0, 0, 0, 86887, 794000, 794000, 166541, 'Сержант Краулер');
INSERT INTO `creature_text` VALUES (88223, 3, 0, 'Нечего бояться, братья и сестры. Мы уже десятки лет сражаемся с орками.', 12, 0, 100, 0, 0, 0, 86881, 324000, 324000, 0, 'Сержант Краулер');
INSERT INTO `creature_text` VALUES (88223, 3, 0, 'Оно уже здесь. Вперед! За Альянс!', 14, 0, 100, 0, 0, 0, 86888, 802000, 802000, 0, 'Сержант Краулер');
INSERT INTO `creature_text` VALUES (88223, 3, 0, 'Повеяло холодом...', 12, 0, 100, 0, 0, 0, 86878, 283000, 283000, 0, 'Сержант Краулер to Player');
INSERT INTO `creature_text` VALUES (88223, 3, 0, 'Чувствуешь этот холодок? Как-то мне не по себе... Аж мурашки по коже.', 12, 0, 100, 0, 0, 0, 86882, 334000, 334000, 164060, 'Сержант Краулер');
INSERT INTO `creature_text` VALUES (88223, 4, 0, 'That was just a test.', 14, 0, 100, 0, 0, 0, 90431, 33344, 33344, 0, 'Sergeant Crowler');
INSERT INTO `creature_text` VALUES (88223, 4, 0, 'Рассредоточиться! Защищайте постройки!', 14, 0, 100, 0, 0, 0, 90434, 56000, 56000, 0, 'Сержант Краулер');
INSERT INTO `creature_text` VALUES (88223, 4, 0, 'Это была просто проверка.', 14, 0, 100, 0, 0, 0, 90431, 54000, 54000, 0, 'Сержант Краулер');
INSERT INTO `creature_text` VALUES (88224, 1, 0, 'Здесь становится жарко.', 12, 0, 100, 0, 0, 0, 90440, 0, 0, 0, 'Рилая Бушующая Волна');
INSERT INTO `creature_text` VALUES (88224, 4, 0, 'А-а… приятный свежий ветерок.', 12, 0, 100, 0, 0, 0, 90462, 3000, 3000, 177599, 'Рилая Бушующая Волна');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'A revenge assault, eh?  Scout, sound the Call to Arms! Get the peons inside!', 12, 0, 100, 0, 0, 42439, 83383, 250187, 250187, 0, 'Sergeant Grimjaw to Player');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'Remember, keep your mouth clenched, so if you\'re struck by lightning then you won\'t bite off your tongue.', 12, 0, 100, 0, 0, 42439, 90558, 280578, 280578, 0, 'Sergeant Grimjaw');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'Snow? Our shamans said there would be mild weather today... I don\'t trust these clouds.', 12, 0, 100, 0, 0, 42439, 81781, 235640, 235640, 0, 'Sergeant Grimjaw to Player');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'Spread out! Don\'t let them flank us!', 14, 0, 100, 0, 0, 42439, 90554, 351031, 351031, 0, 'Sergeant Grimjaw');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'Stubborn, I see. This is it, warriors. For the Horde!', 14, 0, 100, 0, 0, 42439, 91636, 734500, 734500, 0, 'Sergeant Grimjaw');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'The first charge is broken.', 14, 0, 100, 0, 0, 42439, 90553, 348390, 348390, 0, 'Sergeant Grimjaw');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'The Thunderlords are here to give us a lesson in revenge. Instead, let\'s teach them humility.', 12, 0, 100, 0, 0, 42439, 90552, 273625, 273625, 0, 'Sergeant Grimjaw');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'To arms! To your posts! The Thunderlords want to play.', 14, 0, 100, 22, 0, 42439, 90517, 255031, 255031, 0, 'Sergeant Grimjaw to Player');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'We\'ve almost got them!', 14, 0, 100, 0, 0, 42439, 90555, 727703, 727703, 166541, 'Sergeant Grimjaw');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'Громоборцы пришли преподать нам урок мести. А мы вместо этого научим их смирению.', 12, 0, 100, 0, 0, 0, 90552, 345621, 345621, 0, 'Сержант Зловещая Утроба');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'Запомни: зубы нужно стиснуть. Тогда если в тебя попадет молния, ты не откусишь себе язык.', 12, 0, 100, 0, 0, 0, 90558, 352906, 352906, 0, 'Сержант Зловещая Утроба');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'К оружию! По местам! Громоборцы желают немного поиграть.', 14, 0, 100, 22, 0, 0, 90517, 311534, 311534, 0, 'Сержант Зловещая Утроба to Player');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'Какое упорство. Ну, бойцы, наш час пробил. За Орду!', 14, 0, 100, 0, 0, 0, 91636, 915414, 915414, 0, 'Сержант Зловещая Утроба');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'Командир, мы построим солдат, как только прикажешь.', 12, 0, 100, 0, 0, 0, 90388, 248385, 248385, 0, 'Сержант Зловещая Утроба to Player');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'Они дрогнули!', 14, 0, 100, 0, 0, 0, 90555, 908410, 908410, 166541, 'Сержант Зловещая Утроба');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'Первая атака отбита.', 14, 0, 100, 0, 0, 0, 90553, 454743, 454743, 0, 'Сержант Зловещая Утроба');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'Рассыпаться! Не дайте им нас окружить!', 14, 0, 100, 0, 0, 0, 90554, 457427, 457427, 0, 'Сержант Зловещая Утроба');
INSERT INTO `creature_text` VALUES (88228, 3, 0, 'Снег? Наши шаманы сказали, что сегодня будет тепло... Не нравятся мне эти тучи.', 12, 0, 100, 0, 0, 0, 81781, 298259, 298259, 0, 'Сержант Зловещая Утроба to Player');
INSERT INTO `creature_text` VALUES (88229, 3, 0, 'Тебе меня никогда не взять живьем! Ик…', 12, 0, 100, 1, 0, 0, 90421, 31000, 31000, 0, 'Джейкоб Андерс to Player');
INSERT INTO `creature_text` VALUES (88234, 1, 0, 'Тебе меня никогда не взять живьем! Ик…', 12, 0, 100, 1, 0, 0, 90421, 0, 0, 0, 'Рорка to Player');
INSERT INTO `creature_text` VALUES (88242, 3, 0, 'О, а я прямо воспрял духом. Спасиб, $gбратюня:сестрюня;, я этого не забуду.', 12, 0, 100, 0, 0, 0, 90448, 3000, 3000, 0, 'Темный охотник Мок\'и to Player');
INSERT INTO `creature_text` VALUES (88263, 1, 0, 'Вам не сравниться с Громоборцами!', 12, 0, 100, 0, 0, 0, 79289, 0, 0, 177426, 'Копьеносец из клана Громоборцев to Player');
INSERT INTO `creature_text` VALUES (88263, 1, 0, 'И где теперь твоя драгоценная стая?', 12, 0, 100, 0, 0, 0, 79296, 0, 0, 0, 'Копьеносец из клана Громоборцев to Player');
INSERT INTO `creature_text` VALUES (88263, 3, 0, 'За Железного Волка!', 12, 0, 100, 0, 0, 0, 80941, 3000, 3000, 0, 'Копьеносец из клана Громоборцев to Метатель топоров Ледяных скал');
INSERT INTO `creature_text` VALUES (88272, 1, 0, 'Положим этому конец!', 12, 0, 100, 0, 0, 0, 81131, 0, 0, 0, 'Убийца великанов из клана Громоборцев to Player');
INSERT INTO `creature_text` VALUES (88362, 0, 0, 'Прикасайтесь к часовым бомбам, чтобы отправить их обратно в небо!', 41, 0, 100, 0, 0, 0, 90557, 0, 0, 0, 'Часовая бомба to Player');
INSERT INTO `creature_text` VALUES (88416, 3, 0, 'Greetings, Commander $p.', 12, 0, 100, 1, 0, 45832, 90656, 42078, 134437, 0, 'Sergeant Gurmrok to Player');
INSERT INTO `creature_text` VALUES (88424, 3, 0, 'Командир, у тебя случайно нет доступа к ТЕРМОЯДу?', 12, 0, 100, 0, 0, 0, 90675, 612233, 612233, 0, 'Миллхаус Манашторм to Player');
INSERT INTO `creature_text` VALUES (88429, 3, 0, 'Еще одно заклинание...', 12, 0, 100, 0, 0, 0, 0, 2000, 121000, 0, 'Джерр Зеленая Река');
INSERT INTO `creature_text` VALUES (88429, 3, 0, 'Слишком много рыбачишь? В этом нет ничего плохого.', 12, 0, 100, 0, 0, 0, 90654, 37000, 37000, 0, 'Джерр Зеленая Река');
INSERT INTO `creature_text` VALUES (88429, 3, 0, 'Эй, рыбка-рыбка...', 12, 0, 100, 0, 0, 0, 90653, 340103, 340103, 0, 'Джерр Зеленая Река');
INSERT INTO `creature_text` VALUES (88435, 0, 0, 'You shall not harm this $c.', 12, 0, 100, 53, 0, 0, 90658, 0, 0, 0, 'Guardian of the Forge to Player');
INSERT INTO `creature_text` VALUES (88435, 0, 0, 'Стражи горна охраняют $gэтого:эту:r; |3-3($r).', 12, 0, 100, 53, 0, 0, 90659, 0, 0, 0, 'Страж горна  to Player');
INSERT INTO `creature_text` VALUES (88435, 3, 0, '$N под нашей защитой.', 12, 0, 100, 53, 0, 0, 90657, 0, 0, 177196, 'Страж горна  to Player');
INSERT INTO `creature_text` VALUES (88435, 3, 0, 'Не смей вредить $gэтому:этой:c; |3-2($c).', 12, 0, 100, 53, 0, 0, 90658, 0, 0, 177196, 'Страж горна  to Player');
INSERT INTO `creature_text` VALUES (88467, 3, 0, 'Приветствую, командир $p.', 12, 0, 100, 1, 0, 0, 90656, 810000, 810000, 0, 'Сержант Витасайп to Player');
INSERT INTO `creature_text` VALUES (88487, 0, 0, 'Спасибо, что $gпощадил:пощадила; Леораджа.', 12, 0, 100, 0, 0, 0, 90688, 0, 0, 0, 'Леорадж to Player');
INSERT INTO `creature_text` VALUES (88487, 3, 0, 'Леорадж и Солнечный Камень служат тебе. Приносят славу клану!', 12, 0, 100, 0, 0, 0, 90689, 2000, 2000, 0, 'Леорадж to Player');
INSERT INTO `creature_text` VALUES (88497, 3, 0, 'Хватит пялиться! Заводите зверя в клетку!', 12, 0, 100, 603, 0, 0, 90699, 15000, 15000, 0, 'Торговец экзотическими питомцами');
INSERT INTO `creature_text` VALUES (88500, 3, 0, 'Ты $gпочтил:почтила; память моих сородичей, |3-6($c). Прими же мое благословение, и пусть твоя поступь всегда будет легкой, а рука разит без промаха.', 12, 0, 100, 1, 0, 0, 90798, 1072000, 1072000, 0, 'Мургок to Player');
INSERT INTO `creature_text` VALUES (88500, 3, 0, 'Я видел, как саблероны сдирали мясо с костей невинных жертв. Я видел, как они варили в своем омерзительном котле то, что осталось от моих братьев по клану. Хорошо же... Теперь пришел черед саблеронов умирать и вариться в котле!', 12, 0, 100, 1, 0, 0, 90797, 16672, 16672, 0, 'Мургок to Player');
INSERT INTO `creature_text` VALUES (88513, 3, 0, 'Ты $Gвидел:видела;, какую мину он скорчил?!', 12, 0, 100, 6, 0, 0, 0, 0, 0, 0, 'Азереки Пепельный Клинок');
INSERT INTO `creature_text` VALUES (88515, 3, 0, 'Вот умора!', 12, 0, 100, 11, 0, 0, 90734, 2000, 2000, 0, 'Тео\'дрош Ослепленный');
INSERT INTO `creature_text` VALUES (88531, 3, 0, 'Командир, разведчики говорят, что Альянс бросает в бой огромного древнего!', 12, 0, 100, 1, 0, 0, 90542, 31056, 31056, 0, 'Разведчица Копья Войны to Разведчица Копья Войны');
INSERT INTO `creature_text` VALUES (88531, 3, 0, 'Нам сообщают, что у форпоста появилась группа враждебно настроенных араккоа-изгоев.', 12, 0, 100, 1, 0, 0, 90539, 31287, 31287, 0, 'Разведчица Копья Войны to Разведчица Копья Войны');
INSERT INTO `creature_text` VALUES (88531, 3, 0, 'Разведчики сообщают, что у той здоровой шахты огров творится что-то странное.', 12, 0, 100, 1, 0, 0, 90548, 22723, 22723, 0, 'Разведчица Копья Войны to Разведчица Копья Войны');
INSERT INTO `creature_text` VALUES (88531, 3, 0, 'Сейчас-сейчас, босс!', 12, 0, 100, 66, 0, 0, 90538, 64327, 64327, 0, 'Разведчица Копья Войны');
INSERT INTO `creature_text` VALUES (88567, 3, 0, 'А чего тебе не нравится-то? Раскопки идут очень даже неплохо.', 12, 0, 100, 0, 0, 0, 90800, 39547, 39547, 0, 'Археолог Крепкозубс to Археолог Ржавоножс');
INSERT INTO `creature_text` VALUES (88567, 3, 0, 'Кто-то, помнится, бахвалился, что всегда смеется в лицо опасности? Похоже, ты просто трусишь.', 12, 0, 100, 0, 0, 0, 90802, 87469, 1119088, 0, 'Археолог Крепкозубс to Археолог Ржавоножс');
INSERT INTO `creature_text` VALUES (88567, 3, 0, 'Ну, этот вкус напоминает мне о детстве. Не доводилось пить отработанную воду из систем охлаждения? Нам приходилось, другой-то не было.', 12, 0, 100, 0, 0, 0, 90807, 354, 354, 0, 'Археолог Крепкозубс to Археолог Ржавоножс');
INSERT INTO `creature_text` VALUES (88567, 3, 0, 'Хмель как хмель. Пить можно. Даже ностальгию навевает.', 12, 0, 100, 0, 0, 0, 90805, 3635, 777026, 0, 'Археолог Крепкозубс to Археолог Ржавоножс');
INSERT INTO `creature_text` VALUES (88568, 3, 0, 'I, naive, hoped that with the Society I would be able to rise here in life. How I miscalculated!', 12, 0, 100, 1, 0, 0, 90799, 61714, 1093567, 0, 'Археолог Ржавоножс');
INSERT INTO `creature_text` VALUES (88568, 3, 0, 'Да я не про то тебе толкую. Сама посуди: куда ни сунешься, то какой-нибудь огр норовит тебе размозжить башку палицей, то эти невменяемые из Альянса того и гляди подымут тебя на копье!', 12, 0, 100, 5, 0, 0, 90801, 12323, 12323, 0, 'Археолог Ржавоножс');
INSERT INTO `creature_text` VALUES (88568, 3, 0, 'Ностальгию? Вот это гнусное пойло? Ну-ка, расскажи!', 12, 0, 100, 6, 0, 0, 90806, 5756, 5756, 0, 'Археолог Ржавоножс');
INSERT INTO `creature_text` VALUES (88568, 3, 0, 'Трушу? Ха. Вот еще. Просто преждевременная смерть не входит в мои планы.', 12, 0, 100, 274, 0, 0, 90803, 6568, 6568, 0, 'Археолог Ржавоножс');
INSERT INTO `creature_text` VALUES (88568, 3, 0, 'Хм... Удивительно точное сравнение.', 12, 0, 100, 1, 0, 0, 90808, 363, 363, 0, 'Археолог Ржавоножс');
INSERT INTO `creature_text` VALUES (88568, 3, 0, 'Эти орки кроме хмеля вообще ничего не пьют, что ли? Похоже, приличному гоблину в этой глуши придется забыть о коктейлях...', 12, 0, 100, 6, 0, 0, 90804, 69476, 69476, 0, 'Археолог Ржавоножс');
INSERT INTO `creature_text` VALUES (88649, 3, 0, 'You must not do this! You are family!', 12, 0, 100, 5, 0, 44699, 74322, 99234, 99234, 0, 'Draka to Player');
INSERT INTO `creature_text` VALUES (88649, 3, 0, 'Остановитесь! Вы же одна семья!', 12, 0, 100, 5, 0, 44699, 74322, 96721, 96721, 0, 'Дрека to Player');
INSERT INTO `creature_text` VALUES (88663, 3, 0, 'Возвращайся назад, |3-6($c). Здесь тебя ждет только страдание.', 12, 0, 100, 1, 0, 0, 91076, 31000, 31000, 0, 'Темный головорез to Player');
INSERT INTO `creature_text` VALUES (88663, 3, 0, 'Возвращайся назад, $c. Здесь тебя ждет только страдание.', 12, 0, 100, 1, 0, 0, 0, 7000, 7000, 0, 'Темный головорез to Player');
INSERT INTO `creature_text` VALUES (88675, 3, 0, 'Беллоку удалось хотя бы примерно определить, где искать этот загадочный артефакт?', 12, 0, 100, 6, 0, 0, 91114, 23015659, 23015659, 0, 'Илландрия Беллоре');
INSERT INTO `creature_text` VALUES (88675, 3, 0, 'Нам бы поторопиться, а не то доктор Джонс прикарманит артефакт первым.', 12, 0, 100, 1, 0, 0, 91121, 49057, 3149539, 0, 'Илландрия Беллоре');
INSERT INTO `creature_text` VALUES (88675, 3, 0, 'Нет, ты это видел? Они нанизывают на веревочки фрагменты артефакта и продают это как украшения. Как у них только духу хватает так транжирить бесценные вещи.', 12, 0, 100, 274, 0, 0, 91112, 60000, 60000, 0, 'Илландрия Беллоре');
INSERT INTO `creature_text` VALUES (88675, 3, 0, 'Они начали продавать сувениры!', 12, 0, 100, 5, 0, 0, 91102, 102000, 1778000, 0, 'Илландрия Беллоре');
INSERT INTO `creature_text` VALUES (88675, 3, 0, 'Эти гоблины Хитрой Шестеренки до ужаса меркантильные. Они согласятся на любую сделку, будь она в их пользу.', 12, 0, 100, 1, 0, 0, 91095, 26000, 26000, 0, 'Илландрия Беллоре');
INSERT INTO `creature_text` VALUES (88676, 3, 0, 'Мы еще не успели оправиться после осады Темного портала, а они уже расставляют лавочки для туристов. Они издеваются?', 12, 0, 100, 6, 0, 0, 91107, 52000, 52000, 0, 'Исследователь Рам Пламенное Сердце to Илландрия Беллоре');
INSERT INTO `creature_text` VALUES (88676, 3, 0, 'Он нашел пару подсказок, но пока – никакой уверенности.', 12, 0, 100, 1, 0, 0, 91120, 40467, 3141028, 0, 'Исследователь Рам Пламенное Сердце to Илландрия Беллоре');
INSERT INTO `creature_text` VALUES (88676, 3, 0, 'Согласен, и так понятно, что может натворить Альянс с таким могущественным оружием. Нам нужна помощь, пока еще есть надежда.', 12, 0, 100, 1, 0, 0, 91122, 1717000, 1717000, 0, 'Исследователь Рам Пламенное Сердце to Илландрия Беллоре');
INSERT INTO `creature_text` VALUES (88676, 3, 0, 'Что они натворили на этот раз?', 12, 0, 100, 6, 0, 0, 91096, 88000, 1765000, 0, 'Исследователь Рам Пламенное Сердце to Илландрия Беллоре');
INSERT INTO `creature_text` VALUES (88679, 3, 0, 'По-моему, ты загнула. С чего ты решила, что тут замешаны титаны?', 12, 0, 100, 274, 0, 0, 91133, 21840, 21840, 0, 'Гримнир Суровый Молот to Generic Bunny - GJC');
INSERT INTO `creature_text` VALUES (88682, 3, 0, 'Бывало и лучше, но мне не впервой. Мне просто не верится, что у нашего новенького аванпоста такая невероятная история!', 12, 0, 100, 1, 0, 0, 91139, 784462, 784462, 0, 'Мизирин Пивохлеб to Generic Bunny - GJC');
INSERT INTO `creature_text` VALUES (88696, 3, 0, 'Мы должны сфокусироваться на шахтах. Я согласен с доктором Джонсом, артефакт где-то там. Иначе и быть не может!', 12, 0, 100, 1, 0, 4415, 91315, 21000, 158000, 0, 'Офицер Рамсфельд');
INSERT INTO `creature_text` VALUES (88696, 3, 0, 'Наши худшие опасения подтвердились: придется лезть в шахты. Кто знает, какие древние ужасы нас там ожидают...', 12, 0, 100, 1, 0, 0, 91316, 66000, 66000, 0, 'Офицер Рамсфельд');
INSERT INTO `creature_text` VALUES (88697, 3, 0, 'Ах, если бы у нас было воздушное судно... Мы бы задали жару Копью Войны с неба!', 12, 0, 100, 603, 0, 0, 91314, 88000, 88000, 0, 'Офицер Железноруд');
INSERT INTO `creature_text` VALUES (88697, 3, 0, 'Как мы, скажите на милость, выиграем этот бой у Орды без осадных машин?', 12, 0, 100, 603, 0, 0, 91313, 52000, 160000, 0, 'Офицер Железноруд');
INSERT INTO `creature_text` VALUES (88703, 5, 0, 'You do know the city has another auction house, don\'t you?', 12, 0, 100, 0, 0, 0, 131031, 0, 0, 0, 'Stormwind City Guard');
INSERT INTO `creature_text` VALUES (88717, 3, 0, 'Убить нарушителей! Ритуал нельзя прерывать! (Мы создаем бога Бездны!)', 14, 0, 100, 0, 0, 0, 90953, 35000, 497000, 0, 'Чо\'Галл to Player');
INSERT INTO `creature_text` VALUES (88773, 1, 0, 'Жалкие Северные Волки!', 12, 0, 100, 0, 0, 0, 79288, 0, 0, 0, 'Копьеносец из клана Громоборцев to Player');
INSERT INTO `creature_text` VALUES (88773, 1, 0, 'За Железного Волка!', 12, 0, 100, 0, 0, 0, 80941, 0, 0, 0, 'Копьеносец из клана Громоборцев to Player');
INSERT INTO `creature_text` VALUES (88773, 1, 0, 'И где теперь твоя драгоценная стая?', 12, 0, 100, 0, 0, 0, 79296, 0, 0, 0, 'Копьеносец из клана Громоборцев to Player');
INSERT INTO `creature_text` VALUES (88773, 3, 0, 'Из твоей головы выйдет хороший трофей.', 12, 0, 100, 0, 0, 0, 79292, 3425, 3425, 0, 'Копьеносец из клана Громоборцев to Метательница топоров Ледяных скал');
INSERT INTO `creature_text` VALUES (88773, 4, 0, 'Thunderlords, to arms!', 12, 0, 100, 0, 0, 42439, 79287, 2454, 2454, 0, 'Thunderlord Spearhand to Player');
INSERT INTO `creature_text` VALUES (88773, 4, 0, 'Вам не сравниться с Громоборцами!', 12, 0, 100, 0, 0, 0, 79289, 3000, 3000, 0, 'Копьеносец из клана Громоборцев to Метатель топоров Ледяных скал');
INSERT INTO `creature_text` VALUES (88773, 4, 0, 'Громоборцы, к оружию!', 12, 0, 100, 0, 0, 0, 79287, 2000, 2000, 0, 'Копьеносец из клана Громоборцев to Рубака Ледяных скал');
INSERT INTO `creature_text` VALUES (88782, 0, 0, 'Приливный камень зовет их...', 12, 0, 100, 0, 0, 0, 92358, 0, 0, 0, 'Ночная стражница Нар\'таласа to Player');
INSERT INTO `creature_text` VALUES (88782, 0, 1, 'Сейчас ты познаешь гнев ночных стражей!', 12, 0, 100, 0, 0, 0, 109264, 0, 0, 0, 'Ночная стражница Нар\'таласа to Player');
INSERT INTO `creature_text` VALUES (88782, 0, 2, 'За Нар\'талас!', 12, 0, 100, 0, 0, 0, 130216, 0, 0, 0, 'Ночная стражница Нар\'таласа to Player');
