INSERT INTO `creature_text` VALUES (88798, 0, 0, 'Великанов ты найдешь к юго-востоку отсюда. Есть несколько способов попасть на их арену. Удачи.', 12, 0, 100, 1, 0, 0, 93874, 0, 0, 0, 'Леди Ирисс to Player');
