INSERT INTO `creature_text` VALUES (88855, 1, 0, 'Собирай войска, Паржеш, а я пока займусь нашей маленькой проблемой.', 12, 0, 100, 0, 0, 51877, 91800, 0, 0, 0, 'Атисса to Player');
INSERT INTO `creature_text` VALUES (88855, 4, 0, 'Схватить землеходов!', 14, 0, 100, 0, 0, 52620, 93578, 14328, 14328, 0, 'Атисса to Player');
INSERT INTO `creature_text` VALUES (88859, 2, 0, 'Не-е-ет! Я должна... защитить... Приливный камень...', 12, 0, 100, 0, 0, 0, 91716, 0, 0, 0, 'Эля Лазурная Луна to Player');
INSERT INTO `creature_text` VALUES (88859, 3, 0, 'Вас предупреждали миллион раз. Пускай твой пример послужит уроком другим!', 14, 0, 100, 0, 0, 0, 115079, 2541, 2541, 0, 'Эля Лазурная Луна');
INSERT INTO `creature_text` VALUES (88859, 4, 0, 'А-А-А!', 12, 0, 100, 0, 0, 0, 135125, 43600, 43600, 0, 'Эля Лазурная Луна');
INSERT INTO `creature_text` VALUES (88859, 4, 0, 'Ах ты $gмелкий:мелкая; $gнаглый:наглая;...', 12, 0, 100, 0, 0, 0, 0, 26901, 26901, 0, 'Эля Лазурная Луна');
INSERT INTO `creature_text` VALUES (88864, 0, 0, 'Detonating the explosives... now!', 12, 0, 100, 0, 0, 45648, 83189, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (88864, 1, 0, 'Now\'s our chance. Let\'s go!', 12, 0, 100, 0, 0, 45647, 77072, 0, 0, 0, 'Yrel to Player');
INSERT INTO `creature_text` VALUES (88869, 3, 0, 'Прощайте, стражи... хотя вы вряд ли будете по мне скучать.', 12, 0, 100, 0, 0, 52804, 91592, 2559, 2559, 0, 'Принц Фарондис to Player');
INSERT INTO `creature_text` VALUES (88869, 3, 0, 'Талдрис, верный капитан.', 12, 0, 100, 0, 0, 52538, 91596, 101777, 101777, 0, 'Принц Фарондис to Player');
INSERT INTO `creature_text` VALUES (88869, 3, 0, 'Хм, и давно эту дорогу затопило? Прикажу рабочим восстановить ее.', 12, 0, 100, 0, 0, 52794, 91593, 14879, 14879, 0, 'Принц Фарондис to Player');
INSERT INTO `creature_text` VALUES (88869, 4, 0, 'I can’t do it myself. Stay close!', 12, 0, 100, 0, 0, 52529, 92446, 7641, 22101, 0, 'Принц Фарондис to Player');
INSERT INTO `creature_text` VALUES (88869, 4, 0, 'Не отходи, $p.', 12, 0, 100, 0, 0, 52532, 92445, 8685, 8685, 0, 'Принц Фарондис to Player');
INSERT INTO `creature_text` VALUES (88873, 0, 0, '$gНезнакомец:Незнакомка;, сюда. Скорее.', 12, 0, 100, 1, 0, 0, 92161, 0, 0, 0, 'Окуна Большой Бивень to Player');
INSERT INTO `creature_text` VALUES (88873, 1, 0, 'Китопуз может серьезно осложнить нам задачу. Постарайся его не раздражать.', 12, 0, 100, 25, 0, 0, 91621, 0, 0, 0, 'Окуна Большой Бивень to Player');
INSERT INTO `creature_text` VALUES (88873, 2, 0, 'Будь $gосторожен:осторожна;, $n. У Дхоу очень чуткий сон.', 12, 0, 100, 1, 0, 0, 91732, 0, 0, 0, 'Окуна Большой Бивень to Player');
INSERT INTO `creature_text` VALUES (88874, 3, 0, 'ХА-ХА-ХА-ХА-ХА!', 12, 0, 100, 0, 0, 45940, 128242, 3000, 3000, 177815, 'Чистильщик из клана Верховного Молота to Каргат Острорук');
INSERT INTO `creature_text` VALUES (88888, 3, 0, 'Пам-парам-хоп-хоп-хоп! А ну за работу – не то сразу в гроб!', 14, 0, 100, 0, 0, 0, 91620, 16046, 228370, 0, 'Страж-громила to Player');
INSERT INTO `creature_text` VALUES (88889, 0, 0, 'Идем со мной.', 12, 0, 100, 0, 0, 52534, 91612, 0, 0, 0, 'Принц Фарондис to Player');
INSERT INTO `creature_text` VALUES (88889, 1, 0, 'Мне очень жаль.', 12, 0, 100, 2, 0, 52802, 92448, 0, 0, 0, 'Принц Фарондис to Player');
