INSERT INTO `creature_text` VALUES (88889, 3, 0, 'Именно здесь, в академии Нар\'таласа, все и произошло.', 12, 0, 100, 0, 0, 52791, 91616, 0, 0, 0, 'Принц Фарондис to Player');
INSERT INTO `creature_text` VALUES (88911, 3, 0, 'Делай, что хочешь, главное – победа. Используй грязные приемы, песок противнику в глаза бросай – мне все равно. Только победа!', 12, 0, 100, 0, 0, 0, 91657, 724786, 724786, 0, 'Рулевод to Врайкул-гарпунщик');
INSERT INTO `creature_text` VALUES (88911, 3, 0, 'Я вам всем глотки перережу, если не дождусь от вас побед!', 12, 0, 100, 53, 0, 3684, 91669, 258891, 258891, 0, 'Рулевод to Страж бухты Кул-Тираса');
INSERT INTO `creature_text` VALUES (88913, 0, 0, 'Вот тут ты ошибаешься. Очень сильно ошибаешься.', 12, 0, 100, 397, 0, 0, 91628, 0, 0, 0, 'Босс Китопуз to Player');
INSERT INTO `creature_text` VALUES (88913, 3, 0, 'Видишь этот клинок? Я его по самую рукоять засуну тебе в...', 12, 0, 100, 397, 0, 0, 91629, 6321, 6321, 0, 'Босс Китопуз to Player');
INSERT INTO `creature_text` VALUES (88914, 3, 0, 'Китопуз, что все это значит?', 12, 0, 100, 0, 0, 0, 91632, 4483, 4483, 0, 'Рулевод to Босс Китопуз');
INSERT INTO `creature_text` VALUES (88914, 3, 0, 'Тебя предупреждали. С гладиаторами противника – никаких разговоров!', 12, 0, 100, 0, 0, 0, 91633, 13606, 13606, 0, 'Рулевод to Босс Китопуз');
INSERT INTO `creature_text` VALUES (88923, 3, 0, 'Зрелище было отличное. Может, в следующий раз оставим противнику жизнь?', 12, 0, 100, 463, 0, 0, 91697, 261309, 261309, 0, 'Мрачновод to Страж бухты Кул-Тираса');
INSERT INTO `creature_text` VALUES (88932, 0, 0, 'Да пребудет с тобой благословение Нефритовой Змеи!', 12, 0, 100, 2, 0, 0, 91776, 67324, 67324, 0, 'Золотой Сюй to Рулевод');
INSERT INTO `creature_text` VALUES (88932, 1, 0, 'Ты все равно проиграешь.', 12, 0, 100, 0, 0, 0, 91777, 0, 0, 0, 'Золотой Сюй to Рулевод');
INSERT INTO `creature_text` VALUES (88972, 0, 0, 'You may have claimed his body, but his immortal soul is mine to protect.', 12, 0, 100, 0, 0, 44608, 84461, 0, 0, 0, 'Exarch Maladaar to Player');
INSERT INTO `creature_text` VALUES (88972, 1, 0, 'Golad, maloch, valsheva! Golad, maloch, valsheva - Light give me strength...', 12, 0, 100, 0, 0, 44609, 84462, 0, 0, 0, 'Exarch Maladaar to Player');
INSERT INTO `creature_text` VALUES (88972, 2, 0, 'Not until we know. We must know who did this to him!', 12, 0, 100, 0, 0, 44610, 84486, 0, 0, 0, 'Exarch Maladaar to Player');
INSERT INTO `creature_text` VALUES (88972, 3, 0, 'Exoch, maloch, talomai! Hataaru, come to the light...', 12, 0, 100, 0, 0, 44611, 84487, 0, 0, 0, 'Exarch Maladaar to Player');
INSERT INTO `creature_text` VALUES (88972, 4, 0, '...wanted what, Hataaru? The codes to Auchindoun? Your security wards?', 12, 0, 100, 0, 0, 44612, 84491, 0, 0, 0, 'Exarch Maladaar to Player');
INSERT INTO `creature_text` VALUES (88972, 5, 0, 'Who, Hataaru? Who had you killed?', 12, 0, 100, 0, 0, 44613, 84494, 0, 0, 0, 'Exarch Maladaar to Player');
INSERT INTO `creature_text` VALUES (88972, 6, 0, 'What happened? Why did you stop it?', 12, 0, 100, 1, 0, 44614, 84499, 0, 0, 0, 'Exarch Maladaar to Player');
INSERT INTO `creature_text` VALUES (88972, 7, 0, 'He mentioned an Exarch. A betrayal from within the council itself?', 12, 0, 100, 1, 0, 44615, 84500, 0, 0, 0, 'Exarch Maladaar to Player');
INSERT INTO `creature_text` VALUES (88972, 8, 0, 'Что? Почему ты прервала ритуал?', 12, 0, 100, 1, 0, 44614, 84499, 0, 0, 0, 'Экзарх Маладаар to Player');
INSERT INTO `creature_text` VALUES (88972, 9, 0, 'Он упомянул экзарха. Неужели кто-то из совета предал нас?', 12, 0, 100, 1, 0, 44615, 84500, 0, 0, 0, 'Экзарх Маладаар to Player');
INSERT INTO `creature_text` VALUES (88972, 10, 0, 'Сосредоточьтесь. Я не смогу помочь вам, когда ритуал начнется.', 12, 0, 100, 1, 0, 44606, 84455, 0, 0, 0, 'Экзарх Маладаар to Player');
INSERT INTO `creature_text` VALUES (88972, 11, 0, 'Голад, малох, валшева. Следуй за моим голосом, дух, открой глаза живым. Голад, малох, валшева!', 12, 0, 100, 1, 0, 44607, 84468, 0, 0, 0, 'Экзарх Маладаар to Player');
INSERT INTO `creature_text` VALUES (88972, 12, 0, 'Голад, малох, валшева! Голад, малох, валшева – Свет, дай мне силы…', 12, 0, 100, 0, 0, 44609, 84462, 0, 0, 0, 'Экзарх Маладаар to Player');
INSERT INTO `creature_text` VALUES (88972, 13, 0, 'Ниами, отнеси тело в Аукиндон для погребения.', 12, 0, 100, 25, 0, 44616, 84501, 0, 0, 0, 'Экзарх Маладаар to Player');
