INSERT INTO `creature_text` VALUES (89134, 1, 0, 'Я даже не знаю, что произошло. Я услышал грохот падения чего-то тяжелого и... оказался здесь в этом стесненном положении. Вот с тех пор сижу тут и жду.', 12, 0, 100, 0, 0, 0, 91891, 0, 0, 0, 'Старейшина Алдрит to Player');
