INSERT INTO `creature_text` VALUES (89190, 0, 0, 'Отвали. Сколько раз тебе уже говорили – нас это не интересует.', 12, 0, 100, 1, 0, 0, 85104, 0, 0, 0, 'Вор\'варская стражница to Player');
