INSERT INTO `creature_text` VALUES (89199, 3, 0, 'Вот же ты сонный ленивый увалень. Я не стану здесь дожидаться весь день. Я требую аудиенции у принца!', 14, 0, 100, 15, 0, 0, 91984, 262733, 262733, 0, 'Леди Сссюрин');
INSERT INTO `creature_text` VALUES (89199, 3, 0, 'Если твоего принца не интересуют сокровища, может, ему нужна другая мотивация?', 14, 0, 100, 15, 0, 0, 91991, 26549, 26549, 0, 'Леди Сссюрин');
INSERT INTO `creature_text` VALUES (89199, 3, 0, 'Если твоему принцу не нужны эти сокровища, я их заберу и уйду.', 14, 0, 100, 15, 0, 0, 91987, 56191, 56191, 0, 'Леди Сссюрин');
