INSERT INTO `creature_text` VALUES (89283, 0, 1, 'Новая жертва для королевы и ее союзника-демона.', 12, 0, 100, 0, 0, 0, 93232, 0, 0, 0, 'Отравитель из племени Горькой Воды to Player');
INSERT INTO `creature_text` VALUES (89283, 0, 2, 'Не бойся. Яд сделает тебя податливее. Сговорчивее.', 12, 0, 100, 0, 0, 0, 93234, 0, 0, 0, 'Отравитель из племени Горькой Воды to Player');
INSERT INTO `creature_text` VALUES (89283, 0, 3, 'Немного яда – и ты никуда не убежишь.', 12, 0, 100, 0, 0, 0, 93231, 0, 0, 0, 'Отравитель из племени Горькой Воды to Player');
