INSERT INTO `creature_text` VALUES (89283, 0, 5, 'I\'ll let you feed the fish!', 12, 0, 100, 0, 0, 0, 97238, 0, 0, 0, 'Отравитель из племени Горькой Воды to Player');
INSERT INTO `creature_text` VALUES (89283, 0, 6, 'Тревога! Все сюда!', 12, 0, 100, 0, 0, 0, 97239, 0, 0, 0, 'Отравитель из племени Горькой Воды to Player');
INSERT INTO `creature_text` VALUES (89284, 0, 0, 'Брызги и песок!', 12, 0, 100, 0, 0, 52624, 97232, 0, 0, 0, 'Солемаг из племени Горькой Воды to Player');
INSERT INTO `creature_text` VALUES (89284, 0, 1, 'Свежие рабы для королевы и ее нового повелителя.', 12, 0, 100, 0, 0, 0, 93227, 0, 0, 0, 'Солемаг из племени Горькой Воды to Player');
INSERT INTO `creature_text` VALUES (89284, 0, 2, 'Ты, конечно, не моряк, но тоже сойдешь.', 12, 0, 100, 0, 0, 0, 93228, 0, 0, 0, 'Солемаг из племени Горькой Воды to Player');
INSERT INTO `creature_text` VALUES (89284, 0, 3, 'Тебе здесь не место. Умри во имя королевы!', 12, 0, 100, 0, 0, 0, 93230, 0, 0, 0, 'Солемаг из племени Горькой Воды to Player');
INSERT INTO `creature_text` VALUES (89284, 0, 4, 'Что это у тебя? Блестящее оружие?', 12, 0, 100, 0, 0, 0, 97233, 0, 0, 0, 'Солемаг из племени Горькой Воды to Player');
INSERT INTO `creature_text` VALUES (89284, 0, 5, 'Из тебя получится сытный обед!', 12, 0, 100, 0, 0, 0, 97234, 0, 0, 0, 'Солемаг из племени Горькой Воды to Player');
INSERT INTO `creature_text` VALUES (89289, 0, 0, 'Makrura queen... demon...', 12, 1, 100, 5, 0, 0, 92148, 0, 0, 0, 'Матрос \"Мести королевы\" to Player');
INSERT INTO `creature_text` VALUES (89289, 1, 0, 'We were... going to Stormheim... after the Broken Shore.', 12, 1, 100, 5, 0, 0, 92151, 0, 0, 0, 'Матрос \"Мести королевы\" to Player');
INSERT INTO `creature_text` VALUES (89289, 2, 0, 'Demon... here...', 12, 1, 100, 5, 0, 0, 92152, 0, 0, 0, 'Матрос \"Мести королевы\" to Player');
INSERT INTO `creature_text` VALUES (89289, 4, 0, 'We were headed for... Stormheim.', 12, 1, 100, 5, 0, 0, 92146, 0, 0, 0, 'Матрос \"Мести королевы\" to Player');
INSERT INTO `creature_text` VALUES (89290, 0, 0, 'Storm... ran aground.', 12, 1, 100, 5, 0, 0, 92149, 0, 0, 0, 'Матрос \"Мести королевы\" to Player');
INSERT INTO `creature_text` VALUES (89290, 1, 0, 'Captain... taken...', 12, 1, 100, 5, 0, 0, 92150, 0, 0, 0, 'Матрос \"Мести королевы\" to Player');
