INSERT INTO `creature_text` VALUES (89326, 3, 0, 'Помнишь историю, как королева Азшара заключила сделку с демонами? Оказывается, когда принц Фарондис прознал об этом, он сказал...', 12, 0, 100, 6, 0, 0, 113995, 5117, 5117, 0, 'Каллистия Звездное Копье to Player');
INSERT INTO `creature_text` VALUES (89326, 3, 0, 'Слушай-ка, братец!', 12, 0, 100, 5, 0, 0, 113993, 1340, 1340, 0, 'Каллистия Звездное Копье to Player');
INSERT INTO `creature_text` VALUES (89341, 3, 0, '\"Прогуляться\" он пошел... Как же! Наверняка опять отправился на свидание с этой простолюдинкой, в которой он души не чает.', 12, 0, 100, 0, 0, 0, 92195, 2605025, 2605025, 0, 'Магистр Гаруход to Player');
INSERT INTO `creature_text` VALUES (89341, 3, 0, 'Эти яйца помогут добиться идеального баланса между текстурой коктейля и его чародейской силой.', 12, 0, 100, 396, 0, 0, 92592, 7801, 7801, 0, 'Магистр Гаруход to Player');
INSERT INTO `creature_text` VALUES (89344, 3, 0, 'А?', 12, 0, 100, 0, 0, 0, 124594, 20429, 20429, 0, 'Мирмидон из клана Колец Ненависти to Принц Фарондис');
INSERT INTO `creature_text` VALUES (89344, 3, 0, 'Запытаем? Отправим на каторгу? Убьем?', 12, 0, 100, 0, 0, 0, 92201, 5360, 5360, 0, 'Мирмидон из клана Колец Ненависти to Принц Фарондис');
INSERT INTO `creature_text` VALUES (89344, 3, 0, 'Ну и что мы с $gним:ней; сделаем?', 12, 0, 100, 0, 0, 0, 92200, 3404, 3404, 0, 'Мирмидон из клана Колец Ненависти to Принц Фарондис');
INSERT INTO `creature_text` VALUES (89350, 1, 0, 'Легион поработит всех!', 12, 0, 100, 0, 0, 0, 90256, 0, 0, 0, 'Забвенон to Player');
INSERT INTO `creature_text` VALUES (89350, 3, 0, 'Ах, |3-6($r), как замечательно. Подойди поближе ко мне, дитя мое.', 12, 0, 100, 1, 0, 0, 92155, 14276, 14276, 0, 'Забвенон to Player');
INSERT INTO `creature_text` VALUES (89350, 4, 0, 'О чем я только думал? Это твоя битва, моя королева.', 12, 0, 100, 6, 0, 68513, 92156, 12282, 12282, 0, 'Забвенон');
INSERT INTO `creature_text` VALUES (89362, 3, 0, 'В твоих руках артефакт невиданной силы. Хочешь помочь – поговори с Джейсом и Аллари.', 12, 0, 100, 396, 0, 52715, 104259, 37975, 37975, 0, 'Кайн Ярость Солнца to Player');
INSERT INTO `creature_text` VALUES (89362, 3, 0, 'Верховный маг Кадгар? И ты с ним? Вот так сюрприз.', 12, 0, 100, 6, 0, 52717, 92347, 11707, 11707, 0, 'Кайн Ярость Солнца to Player');
INSERT INTO `creature_text` VALUES (89398, 0, 0, 'Как только убьешь демона, не забудь извлечь из него душу с помощью самоцвета.', 12, 0, 100, 0, 0, 55192, 104265, 0, 0, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (89402, 0, 0, 'I will lead you to Prince Farondis, as you wish.', 12, 0, 100, 0, 0, 52767, 93405, 0, 0, 0, 'Ночная стражница Идри to Player');
INSERT INTO `creature_text` VALUES (89402, 1, 0, 'Плюнь в него за меня при встрече.', 12, 0, 100, 0, 0, 52771, 93406, 0, 0, 0, 'Ночная стражница Идри to Player');
