INSERT INTO `creature_text` VALUES (89634, 3, 0, 'Они повсюду...', 12, 0, 100, 0, 0, 0, 114954, 434180, 434180, 0, 'Житель Нар\'таласа to Player');
INSERT INTO `creature_text` VALUES (89640, 3, 0, 'Это... Проклятый Пожиратель? Мы обратим оружие демонов Легиона против них же, да? Мне нравится ход твоих мыслей, $n!', 12, 0, 100, 6, 0, 0, 122054, 1513821, 1938660, 0, 'Фалара Песня Ночи to Player');
