INSERT INTO `creature_text` VALUES (89661, 3, 0, 'Отлично! Ну и напоследок руна льда. Только не забывай про пятый круг! Все постоянно забывают начертать пятый круг.', 12, 0, 100, 0, 0, 0, 92601, 3066318, 3066318, 0, 'Наставница Нидриэль to Player');
INSERT INTO `creature_text` VALUES (89661, 3, 0, 'Отлично. Теперь перейдем к огненной руне. Только осторожно: того, кто начертает эту руну неправильно, ждет скорая, но очень болезненная смерть.', 12, 0, 100, 0, 0, 0, 92600, 226308, 226308, 0, 'Наставница Нидриэль to Player');
