INSERT INTO `creature_text` VALUES (89943, 3, 0, 'I felt the breath of winter ... and here is its source. Staff of the first Guardian that you hold.', 12, 0, 100, 463, 0, 0, 123348, 76862, 76862, 0, 'Хранитель Покоя Лазурного Крыла to Player');
INSERT INTO `creature_text` VALUES (89943, 3, 0, 'От твоего клинка веет чумой и мором, рыцарь смерти. Держись подальше от Сенегоса.', 12, 0, 100, 0, 0, 0, 122057, 386094, 766728, 0, 'Хранитель Покоя Лазурного Крыла to Player');
INSERT INTO `creature_text` VALUES (89952, 1, 0, 'Всех нас вам не сдержать.', 12, 0, 100, 0, 0, 0, 95000, 0, 0, 162665, 'Железный боец арьергарда to Player');
INSERT INTO `creature_text` VALUES (89952, 1, 0, 'Я задержу их. Отходите к кораблям!', 12, 0, 100, 0, 0, 0, 94999, 0, 0, 162665, 'Железный боец арьергарда to Player');
INSERT INTO `creature_text` VALUES (89952, 3, 0, 'Мы нужны Гул\'дану в Танаане. Это наш последний шанс. Давайте, тащите эти ящики!', 12, 0, 100, 25, 0, 0, 93789, 4672, 4672, 0, 'Железный боец арьергарда');
INSERT INTO `creature_text` VALUES (89952, 3, 0, 'Обороняйте берег. Они хотят остановить нас!', 12, 0, 100, 0, 0, 0, 95002, 101544, 101544, 0, 'Железный боец арьергарда to 78116');
INSERT INTO `creature_text` VALUES (89952, 3, 0, 'Шевелитесь! Эти припасы нужно доставить в Танаан.', 12, 0, 100, 25, 0, 0, 93786, 171511, 171511, 0, 'Железный боец арьергарда');
INSERT INTO `creature_text` VALUES (89975, 0, 0, 'Ох...', 12, 0, 100, 0, 0, 52851, 131545, 0, 0, 0, 'Сенегос to Player');
INSERT INTO `creature_text` VALUES (89975, 1, 0, 'Снова помраченные? Принеси малыша ко мне, дитя.', 12, 0, 100, 0, 0, 52857, 93151, 0, 0, 0, 'Сенегос to Player');
INSERT INTO `creature_text` VALUES (89975, 2, 0, 'Стеллагоса, окажи мне последнюю услугу. Помоги своим братьям и сестрам.', 12, 0, 100, 0, 0, 52855, 93123, 0, 0, 0, 'Сенегос to Player');
INSERT INTO `creature_text` VALUES (89975, 3, 0, 'У тебя доброе сердце, $n.', 12, 0, 100, 0, 0, 52847, 93002, 0, 0, 0, 'Сенегос to Player');
INSERT INTO `creature_text` VALUES (89975, 4, 0, 'До встречи, звезда моя.', 12, 0, 100, 0, 0, 52853, 93130, 0, 0, 0, 'Сенегос to Player');
INSERT INTO `creature_text` VALUES (90018, 0, 0, '|TInterface\\Icons\\ability_vehicle_siegeenginecannon.blp:20|t %s получает незначительные повреждения!', 41, 0, 100, 0, 0, 0, 96814, 0, 0, 0, 'Пушка Адского Пламени to 0');
