INSERT INTO `creature_text` VALUES (90024, 0, 0, 'Ты смеешь вторгаться в мой порт и убивать моих воинов? Ну так сразись со мной и узнай, что такое Железная Орда!', 14, 0, 100, 0, 0, 0, 92697, 0, 0, 0, 'Сержант Мор\'грак');
INSERT INTO `creature_text` VALUES (90065, 0, 0, 'I overestimated my strength. All dragons, with the exception of Aspects, weakened significantly after the fall of Deathwing.', 12, 0, 100, 0, 0, 52876, 93114, 0, 0, 0, 'Стеллагоса to Player');
INSERT INTO `creature_text` VALUES (90066, 1, 0, 'Я выпью твою кровь!', 12, 0, 100, 0, 0, 0, 94933, 0, 0, 0, 'Сквернокровный воин to Player');
INSERT INTO `creature_text` VALUES (90068, 3, 0, 'Убирайся отсюда. И чтобы я тебя здесь больше не видел!', 14, 0, 100, 0, 0, 0, 92556, 9551, 9551, 0, 'Сонный Дхоу to Player');
INSERT INTO `creature_text` VALUES (90086, 0, 0, 'Твой взяла, шнуропс-фрумопс. На жратву. $gЗаслужил:Заслужила;.', 12, 0, 100, 1, 0, 0, 88934, 0, 0, 0, 'Ука-дукер to Player');
