INSERT INTO `creature_text` VALUES (90109, 1, 0, 'Землеходы нам не ровня,.', 12, 0, 100, 0, 0, 52683, 97410, 0, 0, 0, 'Поработитель из клана Колец Ненависти to Player');
INSERT INTO `creature_text` VALUES (90109, 1, 0, 'Смерть землеходам!', 12, 0, 100, 0, 0, 52684, 97409, 0, 0, 0, 'Поработитель из клана Колец Ненависти to Player');
INSERT INTO `creature_text` VALUES (90109, 1, 0, 'Я заточу трезубец о твои кости.', 12, 0, 100, 0, 0, 52687, 92731, 0, 0, 0, 'Поработитель из клана Колец Ненависти to Player');
INSERT INTO `creature_text` VALUES (90109, 3, 0, 'А ну, за работу!', 12, 0, 100, 1, 0, 52698, 82611, 11761, 11761, 0, 'Поработитель из клана Колец Ненависти to Груда оружия');
INSERT INTO `creature_text` VALUES (90109, 3, 0, 'Если б не великаны и их арена, я б тебя уже прибил.', 12, 0, 100, 0, 0, 52689, 91598, 340009, 340009, 0, 'Поработитель из клана Колец Ненависти');
INSERT INTO `creature_text` VALUES (90109, 3, 0, 'Скажи спасибо великанам, а то б мы тебя мигом прибили.', 12, 0, 100, 0, 0, 52695, 88936, 14774, 14774, 0, 'Поработитель из клана Колец Ненависти');
INSERT INTO `creature_text` VALUES (90109, 3, 0, 'Хочешь на арену? Нет? Тогда шевелись!', 12, 0, 100, 0, 0, 52699, 91587, 2969, 2969, 0, 'Поработитель из клана Колец Ненависти');
INSERT INTO `creature_text` VALUES (90109, 3, 0, 'Эй ты! Стоять!', 12, 0, 100, 1, 0, 52692, 92217, 7052, 7052, 0, 'Поработитель из клана Колец Ненависти to Груда оружия');
INSERT INTO `creature_text` VALUES (90109, 4, 0, 'Узники убегают!', 12, 0, 100, 1, 0, 52688, 92742, 22128, 22128, 0, 'Поработитель из клана Колец Ненависти to Груда оружия');
INSERT INTO `creature_text` VALUES (90138, 1, 0, 'Что это было?', 12, 0, 100, 0, 0, 49629, 132211, 0, 0, 0, 'Гарона Полуорчиха to Player');
INSERT INTO `creature_text` VALUES (90138, 3, 0, 'Гррр! Мы почти прошли.', 12, 0, 100, 0, 0, 49630, 92841, 33000, 33000, 0, 'Гарона Полуорчиха to Player');
INSERT INTO `creature_text` VALUES (90138, 3, 0, 'Никакой дисциплины. Ваши атаки подкосили Железную Орду.', 12, 0, 100, 0, 0, 49632, 92838, 14000, 14000, 179794, 'Гарона Полуорчиха to Гарона Полуорчиха');
INSERT INTO `creature_text` VALUES (90138, 3, 0, 'Эх! Мы можем лучше.', 12, 0, 100, 0, 0, 49631, 92842, 109000, 109000, 179798, 'Гарона Полуорчиха to Player');
INSERT INTO `creature_text` VALUES (90194, 3, 0, '%s поднимает тревогу!', 41, 0, 100, 0, 0, 1346, 92782, 19000, 19000, 0, 'Гром\'карский мастер клинка');
INSERT INTO `creature_text` VALUES (90199, 0, 0, 'I swallowed thousands of souls. Yours will be next!', 14, 0, 100, 0, 0, 50367, 94373, 0, 0, 0, 'Кровожад to Связанная кровью ведьма');
INSERT INTO `creature_text` VALUES (90199, 1, 0, 'С каждой смертью моя сила растет! Сейчас вы ее ощутите!', 14, 0, 100, 0, 0, 50365, 94371, 0, 0, 0, 'Кровожад to 0');
INSERT INTO `creature_text` VALUES (90199, 2, 0, 'Смерть не так уж страшна...', 15, 0, 100, 0, 0, 0, 96981, 0, 0, 0, 'Кровожад to Player');
INSERT INTO `creature_text` VALUES (90199, 3, 0, 'Кровожад начинает |TInterface\\Icons\\spell_misc_zandalari_council_soulswap:20|t |cFFFF0404|Hspell:181973|h[\"Пиршество душ\"]|h|r!', 42, 0, 100, 0, 0, 50376, 96832, 0, 0, 0, 'Кровожад to Player');
INSERT INTO `creature_text` VALUES (90199, 4, 0, 'Я голоден!', 14, 0, 100, 0, 0, 50376, 94380, 0, 0, 0, 'Кровожад to 0');
INSERT INTO `creature_text` VALUES (90199, 5, 0, 'Чего вы боитесь?', 15, 0, 100, 0, 0, 0, 96982, 0, 0, 0, 'Кровожад to Player');
INSERT INTO `creature_text` VALUES (90199, 6, 0, '|cFFFF0000|Hspell:181295|h[\"Переваривание\"]|h|r убило вас. Успейте добежать до центра, чтобы спастись, пока не истекло отпущенное время!', 42, 0, 100, 0, 0, 0, 97056, 0, 0, 0, 'Кровожад to Player');
INSERT INTO `creature_text` VALUES (90199, 7, 0, 'Больше! Мне надо больше!', 14, 0, 100, 0, 0, 50375, 94379, 0, 0, 0, 'Кровожад to 0');
INSERT INTO `creature_text` VALUES (90199, 8, 0, 'Смерть!', 14, 0, 100, 0, 0, 50371, 133907, 0, 0, 0, 'Кровожад to 0');
INSERT INTO `creature_text` VALUES (90199, 9, 0, 'Но… моя сила…', 14, 0, 100, 0, 0, 50366, 94383, 0, 0, 0, 'Кровожад to Player');
INSERT INTO `creature_text` VALUES (90217, 1, 0, 'Нет! Дай мне прикончить этих тварей! Я должен отомстить!', 12, 0, 100, 0, 0, 0, 92777, 0, 0, 0, 'Нормантис Низложенный to Player');
INSERT INTO `creature_text` VALUES (90217, 3, 0, 'Я заставлю вас заплатить за все, что вы сделали в Нордсколе!', 12, 0, 100, 0, 0, 0, 92778, 2547, 2547, 0, 'Нормантис Низложенный');
INSERT INTO `creature_text` VALUES (90221, 0, 0, 'Тут теперь все наше! Убирайтесь обратно в море!', 12, 0, 100, 0, 0, 0, 94034, 0, 0, 0, 'Рулкрик Разоритель to 78116');
INSERT INTO `creature_text` VALUES (90224, 1, 0, 'Воля Совета – закон!', 12, 0, 100, 0, 0, 0, 76129, 0, 0, 0, 'Старший чернокнижник to Player');
INSERT INTO `creature_text` VALUES (90225, 1, 0, 'Лок-Нараш!', 12, 0, 100, 0, 0, 0, 82413, 0, 0, 0, 'Темный боец авангарда to Player');
INSERT INTO `creature_text` VALUES (90225, 1, 0, 'Пленных не брать!', 12, 0, 100, 0, 0, 0, 83377, 0, 0, 0, 'Темный боец авангарда to Player');
INSERT INTO `creature_text` VALUES (90225, 1, 0, 'Уничтожить |3-3($pc)!', 12, 0, 100, 0, 0, 0, 76123, 0, 0, 0, 'Темный боец авангарда to Player');
INSERT INTO `creature_text` VALUES (90225, 3, 0, 'К оружию!', 12, 0, 100, 0, 0, 0, 139429, 8694, 8694, 0, 'Темный боец авангарда to Player');
INSERT INTO `creature_text` VALUES (90226, 0, 0, 'Ну наконец-то! Эта \"подготовка\" уже успела мне изрядно наскучить!', 12, 0, 100, 0, 0, 0, 94044, 0, 0, 0, 'Магрил Провидец to Player');
INSERT INTO `creature_text` VALUES (90230, 0, 0, 'Мы очистим вселенную огнем!', 12, 0, 100, 0, 0, 55364, 98766, 0, 0, 0, 'Страж гнева - завоеватель to Player');
INSERT INTO `creature_text` VALUES (90233, 3, 0, 'Все. Вы в безопасности.', 12, 0, 100, 1, 0, 49609, 92788, 297000, 297000, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (90233, 3, 0, 'Полегче! Я ведь мог и не успеть.', 12, 0, 100, 6, 0, 49611, 92790, 56000, 56000, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (90233, 3, 0, 'С нашей последней встречи Гул\'дан стал намного сильнее. Должно быть, он до сих пор связан с Пылающим Легионом. Мы обязаны остановить его.', 12, 0, 100, 1, 0, 49612, 92860, 14000, 14000, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (90233, 3, 0, 'Чуть не попались. Будьте осторожнее.', 12, 0, 100, 1, 0, 49610, 92789, 174000, 174000, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (90237, 0, 0, 'Я готова на все, чтобы остановить Гул\'дана. Веди, я пойду за тобой… командир!', 12, 0, 100, 6, 0, 49639, 93961, 0, 0, 0, 'Гарона Полуорчиха to Player');
INSERT INTO `creature_text` VALUES (90247, 0, 0, 'It will be as you say.', 12, 0, 100, 1, 0, 55044, 98400, 0, 0, 0, 'Военачальник Гаардун to Player');
INSERT INTO `creature_text` VALUES (90247, 1, 0, 'Ashtongue with me!', 14, 0, 100, 15, 0, 55045, 98402, 0, 0, 0, 'Военачальник Гаардун to Player');
INSERT INTO `creature_text` VALUES (90251, 0, 0, 'Как прикажешь. Скажу прямо: путь Света верен и справедлив. Наши помыслы должны всегда оставаться чисты.', 12, 0, 100, 1, 0, 68308, 114977, 0, 0, 0, 'Апони Светлая Грива to Player');
INSERT INTO `creature_text` VALUES (90251, 1, 0, 'Портал у врат Пламени Скверны надо уничтожить. Позволь нам с Аратором этим заняться.', 12, 0, 100, 1, 0, 68326, 119266, 0, 0, 0, 'Апони Светлая Грива to Player');
INSERT INTO `creature_text` VALUES (90251, 2, 0, 'Да направит Свет мою руку!', 12, 0, 100, 2, 0, 69655, 120616, 0, 0, 0, 'Апони Светлая Грива to Player');
INSERT INTO `creature_text` VALUES (90255, 0, 0, 'Ты не демон. Кто ты: друг или враг?', 12, 0, 100, 0, 0, 56343, 93015, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (90256, 0, 0, 'Во славу Железной Орды!', 12, 0, 100, 0, 0, 7234, 91014, 0, 0, 0, 'Гром\'карский резервист to Гром\'карский мастер клинка');
INSERT INTO `creature_text` VALUES (90256, 0, 0, 'Вы смеете противостоять нашей мощи?!', 12, 0, 100, 0, 0, 7234, 91020, 0, 0, 0, 'Гром\'карский резервист to Гром\'карский инженер');
INSERT INTO `creature_text` VALUES (90256, 0, 0, 'Твоя смерть принесет мне славу!', 12, 0, 100, 0, 0, 7234, 91022, 0, 0, 0, 'Гром\'карский резервист to Гром\'карский рыскатель');
INSERT INTO `creature_text` VALUES (90256, 1, 0, 'Наша мощь сломит тебя!', 12, 0, 100, 0, 0, 0, 91016, 0, 0, 158082, 'Гром\'карский резервист to Player');
INSERT INTO `creature_text` VALUES (90256, 1, 0, 'Я выбью жизнь из твоих костей!', 12, 0, 100, 0, 0, 7234, 91035, 0, 0, 0, 'Гром\'карский резервист to Player');
INSERT INTO `creature_text` VALUES (90256, 3, 0, 'I will become your doom!', 12, 0, 100, 0, 0, 0, 91041, 47, 47, 158082, 'Гром\'карский резервист to Гром\'карский остроглаз');
INSERT INTO `creature_text` VALUES (90256, 3, 0, 'Давай же, умри скорее.', 12, 0, 100, 0, 0, 7234, 91028, 0, 0, 0, 'Гром\'карский резервист to Гром\'карский остроглаз');
INSERT INTO `creature_text` VALUES (90259, 0, 0, 'Опасность велика. Но такого шанса у нас больше может и не быть. Мы должны принять бой.', 12, 0, 100, 1, 0, 69002, 120189, 0, 0, 0, 'Лорд Максвелл Тиросс to Player');
INSERT INTO `creature_text` VALUES (90267, 1, 0, 'Что это? Смерды пришли на поклон к своему принцу?', 12, 0, 100, 0, 0, 52578, 93180, 0, 0, 0, 'Аэл\'Ит to Player');
INSERT INTO `creature_text` VALUES (90267, 4, 0, 'Больше... больше силы!', 12, 0, 100, 0, 0, 52575, 93182, 38823, 38823, 0, 'Аэл\'Ит');
