INSERT INTO `creature_text` VALUES (90267, 4, 0, 'О, да! Да!', 12, 0, 100, 0, 0, 52577, 93183, 45131, 45131, 0, 'Аэл\'Ит');
INSERT INTO `creature_text` VALUES (90269, 0, 0, 'Эредары знают толк в войне. Вы так сильно хотите умереть?', 14, 0, 100, 0, 0, 50585, 93884, 0, 0, 0, 'Деспотичная Велари to Player');
