INSERT INTO `creature_text` VALUES (90378, 6, 0, '%s выбирает вас целью для своей способности |cFFFF0404|Hspell:180372|h[\"Пронзатель сердец\"]|h|r!', 42, 0, 100, 0, 0, 50431, 94137, 0, 0, 0, 'Килрогг Мертвый Глаз to Player');
INSERT INTO `creature_text` VALUES (90378, 7, 0, 'Наконец… такую смерть я видел!', 14, 0, 100, 0, 0, 50420, 94099, 0, 0, 0, 'Килрогг Мертвый Глаз to Player');
INSERT INTO `creature_text` VALUES (90378, 8, 0, 'Посмотрим, скольких из вас я заберу с собой!', 14, 0, 100, 0, 0, 50429, 94092, 0, 0, 0, 'Килрогг Мертвый Глаз to 0');
INSERT INTO `creature_text` VALUES (90378, 9, 0, 'Умри!', 14, 0, 100, 0, 0, 50426, 129619, 0, 0, 0, 'Килрогг Мертвый Глаз to Player');
INSERT INTO `creature_text` VALUES (90382, 0, 0, 'Мародеры Кровавой Глазницы. Если их не перебить, они станут угрозой снабжению.', 12, 0, 100, 396, 0, 50342, 92839, 0, 0, 0, 'Дрека to Player');
INSERT INTO `creature_text` VALUES (90382, 1, 0, 'Воины Кровавой Глазницы бродят по округе. Где их командование?', 12, 0, 100, 396, 0, 50343, 92932, 0, 0, 0, 'Дрека to Player');
INSERT INTO `creature_text` VALUES (90382, 2, 0, 'Головорезы Гул\'дана. Не знаю, что они затеяли рядом с Темным Порталом, но они стоят у нас на пути.', 12, 0, 100, 396, 0, 50344, 92845, 0, 0, 0, 'Дрека to Player');
INSERT INTO `creature_text` VALUES (90382, 3, 0, 'Что замышлял этот чернокнижник? Я лучше выйду против пушек Железной Орды.', 12, 0, 100, 396, 0, 50345, 92933, 0, 0, 0, 'Дрека to Player');
INSERT INTO `creature_text` VALUES (90382, 4, 0, 'Насчет пушек я погорячилась. Придется прорываться с боем.', 12, 0, 100, 396, 0, 50346, 92934, 0, 0, 0, 'Дрека to Player');
INSERT INTO `creature_text` VALUES (90389, 1, 0, 'Нет... НЕТ! Я все еще голодна! Еще... ЕЩЕ!', 12, 0, 100, 0, 0, 0, 93176, 0, 0, 0, 'Ишканет to Player');
INSERT INTO `creature_text` VALUES (90389, 2, 0, 'Меня мучит... жажда...', 12, 0, 100, 0, 0, 0, 93177, 0, 0, 0, 'Ишканет to Player');
INSERT INTO `creature_text` VALUES (90389, 4, 0, 'I will destroy you!', 12, 0, 100, 0, 0, 0, 102008, 24909, 24909, 0, 'Ишканет');
INSERT INTO `creature_text` VALUES (90390, 1, 0, 'На колени, низшее создание!', 12, 0, 100, 0, 0, 0, 93178, 0, 0, 0, 'Тиндриссен to Player');
INSERT INTO `creature_text` VALUES (90390, 2, 0, 'Этого не может быть! У меня было все...', 12, 0, 100, 0, 0, 0, 93179, 0, 0, 0, 'Тиндриссен to Player');
INSERT INTO `creature_text` VALUES (90390, 4, 0, 'Нет! Защити меня!', 12, 0, 100, 0, 0, 0, 102019, 15083, 15083, 0, 'Тиндриссен');
INSERT INTO `creature_text` VALUES (90390, 4, 0, 'Посмотрим, как ты справишься вот с этим!', 12, 0, 100, 0, 0, 0, 102018, 8416, 8416, 0, 'Тиндриссен');
INSERT INTO `creature_text` VALUES (90393, 1, 0, 'Давай же, умри скорее.', 12, 0, 100, 0, 0, 0, 91028, 0, 0, 0, 'Гром\'карская защитница to Player');
INSERT INTO `creature_text` VALUES (90393, 3, 0, 'Вы смеете противостоять нашей мощи?!', 12, 0, 100, 0, 0, 0, 91020, 5760, 5760, 0, 'Гром\'карская защитница to Player');
INSERT INTO `creature_text` VALUES (90397, 1, 0, 'Железная Орда сломит тебя!', 12, 0, 100, 0, 0, 0, 91027, 0, 0, 0, 'Гром\'карский каратель to Player');
INSERT INTO `creature_text` VALUES (90398, 1, 0, 'I will become your doom!', 12, 0, 100, 0, 0, 0, 91041, 0, 0, 0, 'Гром\'карский защитник to Player');
INSERT INTO `creature_text` VALUES (90398, 1, 0, 'Во славу Железной Орды!', 12, 0, 100, 0, 0, 0, 91014, 0, 0, 0, 'Гром\'карский защитник to Player');
INSERT INTO `creature_text` VALUES (90398, 1, 0, 'Вы смеете противостоять нашей мощи?!', 12, 0, 100, 0, 0, 0, 91020, 0, 0, 0, 'Гром\'карский защитник to Player');
INSERT INTO `creature_text` VALUES (90398, 3, 0, 'Твоя смерть принесет мне славу!', 12, 0, 100, 0, 0, 0, 91022, 38592, 38592, 0, 'Гром\'карский защитник to Player');
INSERT INTO `creature_text` VALUES (90399, 1, 0, 'Твоя смерть принесет мне славу!', 12, 0, 100, 0, 0, 0, 91022, 0, 0, 162665, 'Гром\'карский остроглаз to Player');
INSERT INTO `creature_text` VALUES (90401, 0, 0, 'Скорее, $c, – мы должны заставить души демонов заговорить.', 12, 0, 100, 0, 0, 56847, 92416, 0, 0, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (90401, 1, 0, '$p, расколи первый самоцвет души и выпусти заточенную в нем душу демона.', 12, 0, 100, 396, 0, 0, 92552, 0, 0, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (90401, 2, 0, 'А ну выкладывай все, что тебе известно, гнусное создание!', 12, 0, 100, 0, 0, 0, 92415, 0, 0, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (90401, 3, 0, 'Скорее, $p, – расколи следующий самоцвет души.', 12, 0, 100, 397, 0, 56847, 92252, 0, 0, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (90401, 4, 0, 'Где Легион спрятал тело Иллидана? Говори – или обречешь себя на неописуемые страдания.', 12, 0, 100, 0, 0, 0, 92237, 0, 0, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (90401, 5, 0, 'Кажется, я понимаю, почему ничего не выходит.', 12, 0, 100, 6, 0, 0, 92244, 0, 0, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (90401, 6, 0, 'Могущественный демон, заточенный в этих самоцветах, помогает им сопротивляться.', 12, 0, 100, 273, 0, 0, 92246, 0, 0, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (90401, 7, 0, '$p, тебе нужно будет $gсамому:самой; войти в самоцвет души и заставить Арев\'наала раскрыть тебе местонахождение Иллидана.', 12, 0, 100, 397, 0, 55191, 92245, 0, 0, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (90402, 0, 0, 'Ха-ха-ха! Имя нам – Легион. Мы знаем тебя, Аллари, – и тебя, $p. Ваши души станут нашими.', 12, 0, 100, 25, 0, 56847, 92403, 0, 0, 0, 'Душа демона to Player');
INSERT INTO `creature_text` VALUES (90403, 3, 0, '$p, расколи первый самоцвет и выпусти заточенную в нем душу демона.', 12, 0, 100, 396, 0, 0, 92552, 3444, 3444, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (90403, 3, 0, 'А ну выкладывай все, что тебе известно, гнусное создание!', 12, 0, 100, 0, 0, 0, 92415, 23790, 23790, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (90403, 3, 0, 'Где Легион спрятал тело Иллидана? Говори – или обречешь себя на неописуемые страдания.', 12, 0, 100, 0, 0, 0, 92237, 44663, 44663, 0, 'Аллари Пожирательница Душ to Player');
INSERT INTO `creature_text` VALUES (90406, 0, 0, 'Very good! Off to Zarkhenar, then. Off to see all my friends and family and loved ones.', 12, 0, 100, 0, 0, 52832, 92883, 0, 0, 0, 'Рунас Постыдный to Player');
INSERT INTO `creature_text` VALUES (90406, 1, 0, '...to kill them, of course, for harming your dragon.', 12, 0, 100, 0, 0, 52845, 92884, 0, 0, 0, 'Рунас Постыдный to Player');
INSERT INTO `creature_text` VALUES (90406, 2, 0, 'Bunch of mana addicts, that lot, ha hah! Not me, though. My discipline is iron.', 12, 0, 100, 0, 0, 52818, 92885, 0, 0, 0, 'Рунас Постыдный to Player');
INSERT INTO `creature_text` VALUES (90406, 6, 0, 'Ooh! Mana crystal. Just a moment.', 12, 0, 100, 0, 0, 52839, 92886, 0, 0, 0, 'Рунас Постыдный to Player');
INSERT INTO `creature_text` VALUES (90406, 7, 0, 'So... why are you here in Azsuna, friend? Just visiting, or...?', 12, 0, 100, 0, 0, 52833, 92887, 0, 0, 0, 'Рунас Постыдный to Player');
INSERT INTO `creature_text` VALUES (90406, 8, 0, 'My apologies, good dragon. Ahh, look! We are here.', 12, 0, 100, 0, 0, 52837, 92889, 0, 0, 0, 'Рунас Постыдный to Player');
INSERT INTO `creature_text` VALUES (90409, 0, 0, 'Ай!', 14, 0, 100, 0, 0, 50828, 95608, 0, 0, 0, 'Связанная кровью чародейка Скверны to Связанная кровью чародейка Скверны');
INSERT INTO `creature_text` VALUES (90409, 1, 0, 'Вперед!', 14, 0, 100, 0, 0, 50826, 131943, 0, 0, 0, 'Связанная кровью чародейка Скверны to Связанная кровью чародейка Скверны');
INSERT INTO `creature_text` VALUES (90409, 9, 0, 'В атаку!', 14, 0, 100, 0, 0, 50827, 139449, 0, 0, 0, 'Связанная кровью чародейка Скверны to Связанная кровью чародейка Скверны');
