INSERT INTO `creature_text` VALUES (90411, 4, 0, 'В агонии вы испускаете дух у ног сразившего вас демона адского огня.', 42, 0, 100, 0, 0, 0, 96183, 7890, 7890, 180572, 'Демон адского огня to Player');
INSERT INTO `creature_text` VALUES (90414, 4, 0, 'Горите!  ', 14, 0, 100, 0, 0, 50404, 133419, 0, 5597, 0, 'Повелительница адского огня');
INSERT INTO `creature_text` VALUES (90417, 0, 0, 'У меня есть как раз то, что нужно. Иди за мной.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (90417, 1, 0, 'Подожди секундочку. Я, знаешь ли, такой барахольщик...', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (90417, 2, 0, 'Так-так, посмотрим... апекситовые кристаллы? Нет, они совсем не подойдут.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (90417, 3, 0, 'Наверное, туда засунул.', 12, 0, 100, 25, 0, 0, 0, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (90417, 4, 0, 'Порошок чар... осколки душ... нет, не то...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (90417, 5, 0, 'Ага! Вот он! Кристалл души. Вот это пойдет.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (90417, 6, 0, 'Возьмешь? Эти кристаллы имеют свойство отторгаться от существ недемонической природы.', 12, 0, 100, 6, 0, 0, 0, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (90417, 7, 0, 'OW!! What in the light was that?!', 14, 0, 100, 0, 0, 0, 108791, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (90417, 8, 0, 'Something... bit my head I thing.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (90417, 9, 0, 'Не трогайте! Дождитесь нашего гостя.', 12, 0, 100, 1, 0, 67263, 101006, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (90417, 10, 0, 'Если эту штуку потревожить, процесс пойдет. Э-э... ткни ее, что ли.', 12, 0, 100, 1, 0, 67264, 100058, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (90417, 11, 0, 'Будь осторожнее. Сурамар десять тысяч лет пребывал в запустении.', 12, 0, 100, 1, 0, 67271, 100062, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (90417, 12, 0, 'You\'re speaking of the Tri-Disc of Fire. Yes, he gave it to me, but that was a long time ago. Back when I was still studying at Karazhan.', 12, 0, 100, 1, 0, 0, 126056, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (90417, 13, 0, 'I was working with Archmage Alturus on it at the time. He may still know where it resides. You\'ll find him just outside of Karazhan.', 12, 0, 100, 1, 0, 0, 126057, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (90418, 0, 0, 'Khadgar, what\'s wrong? What happened?', 12, 0, 100, 1, 0, 0, 108795, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (90418, 1, 1, 'Something bit your head? That\'s all? Would you like me to kiss it for you?', 12, 0, 100, 1, 0, 0, 108797, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (90421, 1, 0, 'В Свете – лишь пустота.', 12, 0, 100, 0, 0, 0, 93940, 0, 0, 0, 'Саргерайская повелительница демонов to Player');
INSERT INTO `creature_text` VALUES (90421, 1, 0, 'Легион – это истина. Легион – это сила!', 12, 0, 100, 0, 0, 0, 93939, 0, 0, 0, 'Саргерайская повелительница демонов to Player');
INSERT INTO `creature_text` VALUES (90421, 1, 0, 'Мощь Легиона течет в моих жилах!', 12, 0, 100, 0, 0, 0, 93945, 0, 0, 0, 'Саргерайская повелительница демонов to Player');
INSERT INTO `creature_text` VALUES (90430, 3, 0, '%s nods approvingly.', 16, 0, 100, 273, 0, 0, 102056, 41679, 41679, 0, 'Верховный маг Варгот to Player');
