INSERT INTO `creature_text` VALUES (90452, 3, 0, 'Эти зверюги дерутся, наплевав на всякую честь! Мы никогда не были такими дикими!', 12, 0, 100, 6, 0, 0, 94821, 23365, 23365, 0, 'Раненый рубака to Раненый рубака');
INSERT INTO `creature_text` VALUES (90463, 0, 0, 'If he wished to use the blade for his own ends, he would have simply given it to one of his Sunreavers.', 12, 0, 100, 0, 0, 0, 101742, 0, 0, 0, 'Верховный маг Карлейн to Player');
