INSERT INTO `creature_text` VALUES (90463, 2, 0, 'Отправляйся на Расколотый берег, там тебя ждет Карам! Он прямо сейчас разведывает местность вокруг гробницы.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Верховный маг Карлейн to Player');
