INSERT INTO `creature_text` VALUES (90474, 0, 0, 'Вместе мы нанесем Легиону удар в спину.', 12, 0, 100, 0, 0, 56344, 92242, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (90474, 1, 0, 'Иллидари, что здесь произошло?', 12, 0, 100, 0, 0, 57181, 92248, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (90474, 1, 1, 'Их пытали.', 12, 0, 100, 0, 0, 57182, 92243, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
