INSERT INTO `creature_text` VALUES (90474, 7, 0, 'Где Циана? Мне нужно ее найти!', 12, 0, 100, 0, 0, 57186, 93003, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (90474, 8, 0, 'Стражница продалась Легиону? Я убью ее собственными руками!', 12, 0, 100, 0, 0, 57187, 93021, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (90474, 9, 0, 'Быстрее, $p. Нужно найти ключ от демонических замков. Бьюсь об заклад, он у того тюремщика на востоке.', 12, 0, 100, 0, 0, 57399, 92337, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (90474, 10, 0, 'I will set you free, dragon!', 12, 0, 100, 0, 0, 57397, 93025, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (90474, 11, 0, 'Забирай ключ. Нужно освободить драконицу.', 12, 0, 100, 0, 0, 57400, 92339, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (90474, 12, 0, 'Я должна найти Стеллагосу и Циану.', 12, 0, 100, 396, 0, 57401, 93018, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (90474, 13, 0, 'Встретимся на вершине и вместе завершим начатое.', 12, 0, 100, 396, 0, 57446, 93019, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
