INSERT INTO `creature_text` VALUES (90475, 3, 0, 'Никто нас не одолеет! Ни Гул\'дан, ни Совет Теней, ни весь Пылающий Легион.', 12, 0, 100, 5, 0, 0, 92903, 541760, 541760, 0, 'Гарона Полуорчиха to Player');
INSERT INTO `creature_text` VALUES (90476, 0, 0, 'I surrender. Please, my friend, forgive me. I am truly, truly sorry.', 12, 0, 100, 0, 0, 52835, 92869, 0, 0, 0, 'Рунас Постыдный to Player');
INSERT INTO `creature_text` VALUES (90476, 1, 0, 'Feeding on such raw energy... it clouds my judgment.', 12, 0, 100, 0, 0, 52824, 92870, 0, 0, 0, 'Рунас Постыдный to Player');
INSERT INTO `creature_text` VALUES (90476, 2, 0, 'I hope you can see beyond my tattered robes and desperate behavior. I am not a monster.', 12, 0, 100, 0, 0, 52827, 92871, 0, 0, 0, 'Рунас Постыдный to Player');
INSERT INTO `creature_text` VALUES (90476, 3, 0, 'Watch. I shall prove it to you. I am not just some mana-addled FREAK, like the rest of those wretches.', 12, 0, 100, 0, 0, 52828, 92874, 0, 0, 0, 'Рунас Постыдный to Player');
INSERT INTO `creature_text` VALUES (90481, 0, 0, 'Наши всадники только что вернулись, командир. Со всех сторон нас окружают вражьи полчища. Отличный день для славной битвы!', 12, 0, 100, 11, 0, 0, 95012, 0, 0, 0, 'Дрека to Player');
