INSERT INTO `creature_text` VALUES (90481, 2, 0, 'Джунгли таят множество опасностей. Думаю, сегодня отличный день для перехода в наступление.', 12, 0, 100, 66, 0, 0, 95014, 0, 0, 0, 'Дрека to Player');
