INSERT INTO `creature_text` VALUES (90708, 0, 0, 'Слушайте все! С этого берега у нас одна дорога – вперед, через толпы демонов. Но они не знают, с кем связались. Покажите им истинную силу Орды!', 14, 0, 100, 0, 0, 53528, 99315, 0, 0, 0, 'Вол’джин to Player');
