INSERT INTO `creature_text` VALUES (90713, 0, 0, 'Держитесь, воины Альянса! Он идет на нас!', 14, 0, 100, 0, 0, 53464, 99249, 0, 0, 0, 'Король Вариан Ринн to Крос');
