INSERT INTO `creature_text` VALUES (90717, 0, 0, 'Как раз вовремя! Теперь у нас появился шанс прорвать строй противника.', 14, 0, 100, 0, 0, 53346, 94241, 0, 0, 0, 'Генн Седогрив to Player');
INSERT INTO `creature_text` VALUES (90717, 1, 0, 'Воины Альянса, у нас лишь один путь: вперед, сквозь орды демонов! Прикройте нас пушечным огнем! Войска, В АТАКУ!', 14, 0, 100, 0, 0, 53348, 94585, 0, 0, 0, 'Генн Седогрив to Player');
