INSERT INTO `creature_text` VALUES (91222, 0, 0, 'Champion! It\'s good to see you. Vrykul aren\'t known for their hospitality.', 12, 0, 100, 0, 0, 53018, 98414, 0, 0, 0, 'Командир Лорна Краули to Player');
INSERT INTO `creature_text` VALUES (91223, 0, 0, 'I will clear the passage. Be on the alert - I feel the presence of strong magic.', 12, 0, 100, 0, 0, 52350, 93423, 0, 0, 0, 'Кода Стальной Коготь to Player');
INSERT INTO `creature_text` VALUES (91223, 3, 0, 'Дай мне собраться с силами. Долгие годы сна не прошли мне даром.', 12, 0, 100, 0, 0, 52180, 97128, 18742, 18742, 0, 'Кода Стальной Коготь to Player');
INSERT INTO `creature_text` VALUES (91223, 3, 0, 'Я вот-вот отправлюсь в рощу Кенария. Спасибо тебе за помощь.', 12, 0, 100, 0, 0, 52353, 93429, 18285, 18285, 0, 'Кода Стальной Коготь to Player');
INSERT INTO `creature_text` VALUES (91224, 3, 0, 'Изумрудный Сон был окутан таким мраком...', 12, 0, 100, 0, 0, 52349, 93360, 3410, 3410, 0, 'Кода Стальной Коготь to Player');
INSERT INTO `creature_text` VALUES (91230, 1, 0, '*кашляет* Ты за это заплатишь!', 12, 0, 100, 0, 0, 0, 95382, 0, 0, 0, 'Ночной клинок Совета to Player');
INSERT INTO `creature_text` VALUES (91230, 1, 0, '*кашляет* Уй! Мои глаза!', 12, 0, 100, 0, 0, 0, 95383, 0, 0, 0, 'Ночной клинок Совета to Player');
INSERT INTO `creature_text` VALUES (91240, 0, 0, 'Мы повелеваем бурей!', 12, 0, 100, 0, 0, 0, 94002, 0, 0, 0, 'Гуннлауг Чешуйчатое Сердце to Player');
INSERT INTO `creature_text` VALUES (91242, 0, 0, 'Здесь! Отличная естественная гавань.', 12, 0, 100, 25, 0, 17671, 96902, 0, 0, 0, 'Солог Рырк to Player');
INSERT INTO `creature_text` VALUES (91243, 1, 0, 'Вам нечего противопоставить магии Гул\'дана!', 12, 0, 100, 0, 0, 0, 94912, 0, 0, 0, 'То\'гар Кровавый Кулак to Player');
INSERT INTO `creature_text` VALUES (91244, 1, 0, 'Драконы служат Сковальду, червь!', 12, 0, 100, 0, 0, 51713, 94005, 0, 0, 0, 'Сквернскорнский зверолов to Player');
INSERT INTO `creature_text` VALUES (91244, 1, 0, 'Моли о пощаде!', 12, 0, 100, 0, 0, 52468, 94006, 0, 0, 0, 'Сквернскорнский зверолов to Player');
INSERT INTO `creature_text` VALUES (91244, 1, 0, 'Покорись Королю-богу!', 12, 0, 100, 0, 0, 51715, 94007, 0, 0, 0, 'Сквернскорнский зверолов to Player');
INSERT INTO `creature_text` VALUES (91251, 3, 0, 'You will be destroyed!', 12, 0, 100, 0, 0, 0, 94895, 11345, 11345, 0, 'Felsworn Bulwark to Player');
INSERT INTO `creature_text` VALUES (91256, 3, 0, 'Будьте вы прокляты! Ничего – Кровавая Луна еще взойдет!', 14, 0, 100, 0, 0, 0, 93378, 3394698, 3394698, 0, 'Вестник Кровавой Глазницы to Player');
INSERT INTO `creature_text` VALUES (91256, 3, 0, 'Встает Кровавая Луна, наделяя нашего вождя великой силой! Возрадуйся, Кровавая Глазница, ибо близок миг, когда нас будет уже не остановить!', 14, 0, 100, 0, 0, 51127, 93376, 2682538, 2682538, 0, 'Вестник Кровавой Глазницы to Генератор Кровавой Луны');
INSERT INTO `creature_text` VALUES (91256, 3, 0, 'Кровавая Луна иссякла! Скорее оживляйте останки, которые впитают ту силу, что еще осталась!', 14, 0, 100, 0, 0, 0, 93377, 3369421, 3369421, 0, 'Вестник Кровавой Глазницы to Player');
INSERT INTO `creature_text` VALUES (91256, 3, 0, 'Кровавый охотник Зулк! Слишком много ритуалов мы не смогли довести до конца! Уничтожь незваных гостей!', 14, 0, 100, 0, 0, 0, 93375, 1625020, 1625020, 0, 'Вестник Кровавой Глазницы to Кровавый охотник Зулк');
INSERT INTO `creature_text` VALUES (91262, 4, 0, 'Ха-ха! Назад в Пустоту!', 12, 0, 100, 0, 0, 0, 93441, 22014, 22014, 181286, 'Призванный бес');
INSERT INTO `creature_text` VALUES (91288, 1, 0, 'I smash yours!', 12, 0, 100, 0, 0, 0, 95421, 0, 0, 0, 'Воин из племени Дымящейся Шкуры to Player');
INSERT INTO `creature_text` VALUES (91288, 1, 0, 'Это наша магия, $gворюга:воровка;!', 12, 0, 100, 53, 0, 0, 95423, 0, 0, 0, 'Воин из племени Дымящейся Шкуры to Player');
INSERT INTO `creature_text` VALUES (91288, 3, 0, 'Коли $gего:ее;, пока не сдохнет!', 12, 0, 100, 0, 0, 0, 95422, 37411, 37411, 0, 'Воин из племени Дымящейся Шкуры to Player');
INSERT INTO `creature_text` VALUES (91296, 3, 0, 'Все время будь начеку. Я хочу знать обо всех планах этого дракона.', 12, 0, 100, 0, 0, 0, 93392, 1250, 1250, 0, 'Кордана Оскверненная Песнь to Player');
INSERT INTO `creature_text` VALUES (91305, 0, 0, 'Они здесь! Быстрее! Гул\'дан требует завершить это…', 14, 0, 100, 0, 0, 50352, 96071, 0, 0, 0, 'Железный призыватель Скверны to Железный призыватель Скверны');
INSERT INTO `creature_text` VALUES (91313, 1, 0, 'Думаешь, мы позволим тебе похитить бесценные реликвии Кровавой Глазницы?!', 12, 0, 100, 0, 0, 0, 94356, 0, 0, 0, 'Разъяренный кровавый дух to Ариок');
INSERT INTO `creature_text` VALUES (91313, 1, 0, 'Ты $gступил:ступила; на священную землю!', 12, 0, 100, 0, 0, 0, 94354, 0, 0, 0, 'Разъяренный кровавый дух to Ариок');
INSERT INTO `creature_text` VALUES (91313, 1, 0, 'Эти глаза охраняет моя бессмертная душа!', 12, 0, 100, 0, 0, 0, 94355, 0, 0, 0, 'Разъяренный кровавый дух to Player');
INSERT INTO `creature_text` VALUES (91313, 2, 0, 'I\'m not the only eye guard ... there are others ...', 12, 0, 100, 0, 0, 0, 94357, 0, 0, 0, 'Разъяренный кровавый дух to Player');
INSERT INTO `creature_text` VALUES (91313, 2, 0, 'Килрогг... они пришли за глазами...', 12, 0, 100, 0, 0, 0, 94359, 0, 0, 0, 'Разъяренный кровавый дух to Player');
INSERT INTO `creature_text` VALUES (91313, 3, 0, 'Когда-нибудь ты заплатишь за это...', 12, 0, 100, 15, 0, 0, 94358, 2106, 2106, 0, 'Разъяренный кровавый дух to Ариок');
INSERT INTO `creature_text` VALUES (91318, 1, 0, 'Сила тотема принадлежит мне. Я не позволю тебе забрать ее!', 12, 0, 100, 0, 0, 0, 95372, 0, 0, 0, 'Вождь Гроу to Player');
INSERT INTO `creature_text` VALUES (91325, 0, 0, 'Мы с Перси отправимся сейчас. Как подготовишься, поговори с распорядителем полетов.', 12, 0, 100, 1, 0, 50812, 93436, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91326, 0, 0, 'Появляется |cFFFF0000%s|r и начинает усиливать шпиль Скверны!', 41, 0, 100, 0, 0, 50947, 93975, 0, 0, 0, 'Гул\'дан to Player');
INSERT INTO `creature_text` VALUES (91326, 1, 0, 'Глупцы. Воля Легиона будет исполнена.', 14, 0, 100, 0, 0, 50947, 96022, 0, 0, 0, 'Гул\'дан to Player');
INSERT INTO `creature_text` VALUES (91326, 2, 0, 'Восстань, Маннорот… Да, да! Твои повелители вновь призывают тебя!', 14, 0, 100, 0, 0, 50948, 96012, 0, 0, 0, 'Гул\'дан to Player');
INSERT INTO `creature_text` VALUES (91326, 3, 0, 'Не бойся, Маннорот. Дар скверны вернул твою силу. Заставь их страдать!', 14, 0, 100, 0, 0, 50949, 96015, 0, 0, 0, 'Гул\'дан to Маннорот');
INSERT INTO `creature_text` VALUES (91326, 4, 0, '|cFFFF0000%s|r начинает усиливать шпиль Скверны!', 41, 0, 100, 0, 0, 0, 93978, 0, 0, 0, 'Гул\'дан to Маннорот');
INSERT INTO `creature_text` VALUES (91326, 6, 0, 'Ощути поток силы. Ты - разрушитель, истребитель плоти!', 14, 0, 100, 0, 0, 50950, 96019, 0, 0, 0, 'Гул\'дан to Маннорот');
INSERT INTO `creature_text` VALUES (91326, 7, 0, 'Задержи их. Я почти закончил. Наш владыка скоро будет здесь!', 14, 0, 100, 0, 0, 50951, 93972, 0, 0, 0, 'Гул\'дан to Маннорот');
INSERT INTO `creature_text` VALUES (91329, 3, 0, 'Мы с Перси отправимся сейчас. Как подготовишься, поговори с распорядителем полетов.', 12, 0, 100, 1, 0, 50812, 93436, 1496, 1496, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91331, 0, 0, 'Трепещите, смертные! Этот мир обречен.', 14, 0, 100, 396, 0, 50662, 94741, 0, 0, 0, 'Архимонд to Player');
INSERT INTO `creature_text` VALUES (91331, 1, 0, 'Гул\'дан, готовь Черные Врата к вторжению. Этим червям не остановить меня. Свет померкнет в моих руках.', 14, 0, 100, 396, 0, 50663, 94742, 0, 0, 0, 'Архимонд to Player');
INSERT INTO `creature_text` VALUES (91331, 2, 0, 'Мой приход предвещает конец вашего убогого мира. Ваше скудное войско станет первой жертвой Пылающего Легиона!', 14, 0, 100, 0, 0, 50664, 94743, 0, 0, 0, 'Архимонд to Player');
INSERT INTO `creature_text` VALUES (91331, 3, 0, 'Кайрел-нарак!', 14, 0, 100, 0, 0, 50689, 94754, 0, 0, 0, 'Архимонд to Player');
INSERT INTO `creature_text` VALUES (91331, 4, 0, '|TINTERFACE\\ICONS\\spell_fire_felflamestrike.blp:20|t Архимонд begins to cast a spell |cFFF00000|Hspell:183254|h[\"Притяжение пламени\"]|h|r!', 41, 0, 100, 0, 0, 50687, 96693, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 5, 0, 'Прими свою плачевную судьбу.', 14, 0, 100, 0, 0, 50687, 94752, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 6, 0, 'Жалкие отродья!', 14, 0, 100, 0, 0, 50681, 94746, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 7, 0, 'Этот мир сгорит!', 14, 0, 100, 0, 0, 50684, 94749, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 8, 0, 'Вам не спасти этот мир!', 14, 0, 100, 0, 0, 50707, 94788, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 9, 0, 'Эхо вашей гибели разнесется по этому никчемному миру, повергая в отчаяние живущих!', 14, 0, 100, 0, 0, 50648, 94744, 0, 0, 0, 'Архимонд to Player');
INSERT INTO `creature_text` VALUES (91331, 10, 0, 'Вас так легко осквернить.', 14, 0, 100, 0, 0, 50690, 94755, 0, 0, 0, 'Архимонд to Player');
INSERT INTO `creature_text` VALUES (91331, 11, 0, 'I will destroy this miserable world! His remains will be scattered around the Twisting Nether!', 14, 0, 100, 0, 0, 50659, 94782, 0, 0, 0, 'Архимонд to Архимонд');
INSERT INTO `creature_text` VALUES (91331, 12, 0, 'Забвение уже близко, маг! Сдавайся!', 14, 0, 100, 0, 0, 50660, 94783, 0, 0, 0, 'Архимонд to Архимонд');
INSERT INTO `creature_text` VALUES (91331, 13, 0, 'Тебя поглотит вечная тьма!', 14, 0, 100, 0, 0, 50699, 94764, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 14, 0, 'Аким-курай!', 14, 0, 100, 0, 0, 50688, 94753, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 15, 0, 'Червь!', 14, 0, 100, 0, 0, 50683, 94748, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 16, 0, 'Узрите пламя, поглотившее сотни миров!', 14, 0, 100, 0, 0, 50685, 94750, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 17, 0, 'Этот презренный мирок уже пал жертвой моей скверны. Сама земля отмечена клеймом Легиона!', 14, 0, 100, 0, 0, 50650, 94773, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 18, 0, 'Слизняк.', 14, 0, 100, 0, 0, 50678, 94785, 0, 0, 0, 'Архимонд to Player');
INSERT INTO `creature_text` VALUES (91331, 19, 0, 'I will break your miserable will.', 14, 0, 100, 0, 0, 50693, 94758, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 20, 0, 'Твой жалкий свет бессилен против меня, дитя. Довольно этой глупости!', 14, 0, 100, 0, 0, 50651, 94774, 0, 0, 0, 'Архимонд to Архимонд');
INSERT INTO `creature_text` VALUES (91331, 21, 0, 'Ваша мышиная возня завершится здесь, смертные. Легион покончит с этим миром.', 14, 0, 100, 0, 0, 50649, 94745, 0, 0, 0, 'Архимонд to Player');
INSERT INTO `creature_text` VALUES (91331, 22, 0, 'Все сгорят в огне Легиона.', 14, 0, 100, 0, 0, 50686, 94751, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 23, 0, 'Анакх кри!', 14, 0, 100, 0, 0, 50682, 94747, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 24, 0, 'Жалкий червь.', 14, 0, 100, 0, 0, 50680, 94787, 0, 0, 0, 'Архимонд to Player');
INSERT INTO `creature_text` VALUES (91331, 25, 0, 'Ваши души будут моими!', 14, 0, 100, 0, 0, 50696, 94761, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 26, 0, 'Узрите несметные войска Пылающего Легиона и осознайте всю тщетность борьбы!', 14, 0, 100, 0, 0, 50655, 94778, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 27, 0, 'Мне надоела эта бесцельная забава. Пред вами — бессмертный Легион, бич тысячи миров.', 14, 0, 100, 0, 0, 50658, 94781, 0, 0, 0, 'Архимонд to Архимонд');
INSERT INTO `creature_text` VALUES (91331, 28, 0, 'Хватит! Вашим тщетным потугам — КОНЕЦ!', 14, 0, 100, 0, 0, 50654, 94777, 0, 0, 0, 'Архимонд to Архимонд');
INSERT INTO `creature_text` VALUES (91331, 29, 0, 'Ваши души познают вечные муки!', 14, 0, 100, 0, 0, 50697, 94762, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 30, 0, 'Легкая победа.', 14, 0, 100, 0, 0, 50689, 94786, 0, 0, 0, 'Архимонд to Player');
INSERT INTO `creature_text` VALUES (91331, 31, 0, 'Тебе не выбраться из бездны!', 14, 0, 100, 0, 0, 50698, 94763, 0, 0, 0, 'Архимонд to 0');
INSERT INTO `creature_text` VALUES (91331, 32, 0, '|TINTERFACE\\ICONS\\ability_warlock_moltencoregreen.blp:20|t $n $gотмечен:отмечена; для |cFFF00000|Hspell:186961|h[\"Изгнания в Пустоту\"]|h|r!', 41, 0, 100, 0, 0, 0, 96969, 0, 0, 0, 'Архимонд to Player');
INSERT INTO `creature_text` VALUES (91332, 4, 0, 'Костеглод сотрет вас в порошок!', 14, 0, 100, 0, 0, 0, 104640, 11125, 11125, 0, 'Камнерукий охотник to Камнеспинный щелкозуб');
INSERT INTO `creature_text` VALUES (91348, 0, 0, 'I can\'t wait - follow me!', 12, 0, 100, 15, 0, 50307, 93447, 0, 0, 0, 'Ариок to Player');
INSERT INTO `creature_text` VALUES (91349, 0, 0, 'Что… Что это? Гул\'дан, что ты наделал? Я… так слаб…', 14, 0, 100, 0, 0, 50459, 96013, 0, 0, 0, 'Маннорот to Маннорот');
INSERT INTO `creature_text` VALUES (91349, 1, 0, 'Ваш конец настал.', 14, 0, 100, 0, 0, 50494, 96034, 0, 0, 0, 'Маннорот to Маннорот');
INSERT INTO `creature_text` VALUES (91349, 2, 0, 'Умрите, шавки.', 14, 0, 100, 0, 0, 50486, 96028, 0, 0, 0, 'Маннорот to Маннорот');
INSERT INTO `creature_text` VALUES (91349, 3, 0, 'Бойся силы Легиона!', 14, 0, 100, 0, 0, 50482, 96014, 0, 0, 0, 'Маннорот to Маннорот');
INSERT INTO `creature_text` VALUES (91349, 4, 0, 'Я - Разрушитель!', 14, 0, 100, 0, 0, 50480, 96042, 0, 0, 0, 'Маннорот to Player');
INSERT INTO `creature_text` VALUES (91349, 5, 0, 'Мои силы вернулись! Я неудержим!', 14, 0, 100, 0, 0, 50460, 96016, 0, 0, 0, 'Маннорот to Портал демонов');
INSERT INTO `creature_text` VALUES (91349, 6, 0, 'Прочь!', 14, 0, 100, 0, 0, 50483, 135117, 0, 0, 0, 'Маннорот to Маннорот');
INSERT INTO `creature_text` VALUES (91349, 7, 0, 'Ваша плоть сгорит.', 14, 0, 100, 0, 0, 50492, 96033, 0, 0, 0, 'Маннорот to Маннорот');
INSERT INTO `creature_text` VALUES (91349, 8, 0, 'Смертные не могут быть так сильны. Гул\'дан, сделай что-нибудь!', 14, 0, 100, 0, 0, 50461, 96018, 0, 0, 0, 'Маннорот to 0');
INSERT INTO `creature_text` VALUES (91349, 9, 0, 'МЕНЯ… НЕ… ОСТАНОВИТЬ…', 14, 0, 100, 0, 0, 50462, 96020, 0, 0, 0, 'Маннорот to Маннорот');
INSERT INTO `creature_text` VALUES (91349, 10, 0, 'На колени!', 14, 0, 100, 0, 0, 50489, 96030, 0, 0, 0, 'Маннорот to Маннорот');
INSERT INTO `creature_text` VALUES (91349, 11, 0, 'Легион испепелит ваш мир, как и все миры до этого!', 14, 0, 100, 0, 0, 50491, 96032, 0, 0, 0, 'Маннорот to Маннорот');
INSERT INTO `creature_text` VALUES (91349, 12, 0, 'Трепещи перед мощью Легиона!', 14, 0, 100, 0, 0, 50484, 96023, 0, 0, 0, 'Маннорот to Маннорот');
INSERT INTO `creature_text` VALUES (91349, 13, 0, 'Кричите! Корчитесь в муках!', 14, 0, 100, 0, 0, 50478, 96038, 0, 0, 0, 'Маннорот to Player');
INSERT INTO `creature_text` VALUES (91349, 14, 0, 'Достойная попытка... но тщетная.', 14, 0, 100, 0, 0, 50475, 96036, 0, 0, 0, 'Маннорот to Player');
