INSERT INTO `creature_text` VALUES (91353, 2, 0, 'Больно!', 12, 0, 100, 0, 0, 70950, 128126, 0, 0, 199219, 'Боевой маг Кирин-Тора to Командир повелителей ужаса Арганот');
INSERT INTO `creature_text` VALUES (91354, 1, 0, 'Заколю! Заколю! Ха-ха-ха!', 12, 0, 100, 0, 0, 61491, 93801, 0, 0, 0, 'Головорез из племени Листа Чертополоха to Player');
INSERT INTO `creature_text` VALUES (91354, 1, 0, 'О да! Я до тебя доберусь!', 12, 0, 100, 0, 0, 61499, 93800, 0, 0, 0, 'Головорез из племени Листа Чертополоха to Player');
INSERT INTO `creature_text` VALUES (91354, 1, 0, 'Это наши драконы!', 12, 0, 100, 0, 0, 61502, 93802, 0, 0, 0, 'Головорез из племени Листа Чертополоха to Player');
INSERT INTO `creature_text` VALUES (91354, 2, 0, 'I just wanted to ... have fun ...', 12, 0, 100, 0, 0, 61504, 93807, 0, 0, 0, 'Головорез из племени Листа Чертополоха to Player');
INSERT INTO `creature_text` VALUES (91354, 2, 0, 'I\'m ... landing ...', 12, 0, 100, 0, 0, 61505, 108745, 0, 0, 0, 'Головорез из племени Листа Чертополоха to Верховный маг Модера');
INSERT INTO `creature_text` VALUES (91354, 2, 0, 'А-а-а!', 12, 0, 100, 0, 0, 61500, 135125, 0, 0, 0, 'Головорез из племени Листа Чертополоха to Player');
INSERT INTO `creature_text` VALUES (91354, 2, 0, 'На этот раз... твоя взяла.', 12, 0, 100, 0, 0, 61493, 93805, 0, 0, 0, 'Головорез из племени Листа Чертополоха to Player');
INSERT INTO `creature_text` VALUES (91354, 3, 0, 'Какой медлительный дракон! Дайте-ка мне кинжальчик!', 12, 0, 100, 0, 0, 0, 95505, 68, 68, 0, 'Головорез из племени Листа Чертополоха to Лунокрыл');
INSERT INTO `creature_text` VALUES (91354, 3, 0, 'От меня так просто не избавишься!', 12, 0, 100, 0, 0, 61496, 95506, 1374166, 1374166, 0, 'Головорез из племени Листа Чертополоха to Лунокрыл');
INSERT INTO `creature_text` VALUES (91374, 1, 0, 'Мм-м, ты прям вкусняшка!', 12, 0, 100, 0, 0, 0, 93493, 0, 0, 0, 'Повелитель стручлингов Ваккавам to Player');
INSERT INTO `creature_text` VALUES (91375, 0, 0, 'Ну, вперед.', 12, 0, 100, 1, 0, 50813, 93494, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91375, 1, 0, 'Интересно… \"Ступай в Танаан. Помоги мне, и я верну тебе небо\".', 12, 0, 100, 1, 0, 50815, 93490, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91375, 2, 0, 'Кто это написал? Идем, продолжим поиски.', 12, 0, 100, 1, 0, 50816, 93496, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91375, 3, 0, 'У тебя все так легко выходит.', 12, 0, 100, 603, 0, 50819, 93987, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91375, 4, 0, 'Святилище Терокка! Но сеттеки почитают Короля воронов. Зачем им уродовать свое святилище? Продолжим поиски.', 12, 0, 100, 1, 0, 50817, 93543, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91375, 5, 0, 'Ах, это на языке апекситов. \"…зло… погребено…запретное...\". Без второй половины это все, что я могу расшифровать. Идем дальше.', 12, 0, 100, 1, 0, 50818, 93551, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91375, 6, 0, 'Жестоко… но неизбежно.', 12, 0, 100, 1, 0, 50820, 93988, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91375, 7, 0, 'Возвращайтесь в тень!', 12, 0, 100, 603, 0, 50821, 93989, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91375, 8, 0, 'Не ожидал, что это кончится так быстро.', 12, 0, 100, 1, 0, 50822, 93990, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91375, 9, 0, 'Хе-хех. Чудесно, чудесно.', 12, 0, 100, 574, 0, 50823, 94421, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91375, 10, 0, 'Что это? Книга кажется... знакомой.', 12, 0, 100, 1, 0, 50814, 93585, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91375, 11, 0, 'Этот свиток адресован Искару. Вы показываете его Решаду.', 42, 0, 100, 1, 0, 0, 93480, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91375, 12, 0, 'Символ исписан мелким текстом на неизвестном языке. Судя по всему, большого куска текста не хватает.', 42, 0, 100, 1, 0, 0, 93477, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91375, 13, 0, 'Это святилище осквернено с особой жестокостью.', 42, 0, 100, 1, 0, 0, 93450, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91380, 2, 0, 'Килрогг победит все равно...', 12, 0, 100, 0, 0, 0, 94303, 0, 0, 0, 'Налетчик из клана Кровавой Глазницы to Player');
INSERT INTO `creature_text` VALUES (91386, 0, 0, 'Гарм что-то чует.', 42, 0, 100, 0, 0, 0, 95312, 0, 0, 0, 'Гарм to Player');
INSERT INTO `creature_text` VALUES (91402, 3, 0, 'Лучше посмотри, что стало с тобой! Бледная тень прошлого, которой не дано обрести покой.', 12, 0, 100, 0, 0, 62548, 114140, 53919, 53919, 0, 'Королева Азшара to Player');
INSERT INTO `creature_text` VALUES (91402, 3, 0, 'Фарондис, верный слуга. Какая встреча!', 12, 0, 100, 1, 0, 52897, 93453, 10529, 10529, 0, 'Видение королевы Азшары to Player');
INSERT INTO `creature_text` VALUES (91419, 0, 0, 'Добро пожаловать в нашу скромную контору, $gпутешественник:путешественница;.', 12, 0, 100, 2, 0, 40019, 92192, 0, 0, 0, 'Мистер Шекл to Player');
INSERT INTO `creature_text` VALUES (91419, 1, 0, 'Жаль, конечно, что дошло до такого – ну а что еще оставалось делать? Дожидаться, пока тебя прирежут в твоей собственной постели... уходить из бизнеса?', 12, 0, 100, 6, 0, 0, 111167, 0, 0, 0, 'Мистер Шекл to Player');
INSERT INTO `creature_text` VALUES (91421, 0, 0, 'Идем. Надо найти Килрогга.', 12, 0, 100, 0, 0, 50308, 93619, 0, 0, 0, 'Ариок to Player');
INSERT INTO `creature_text` VALUES (91421, 1, 0, 'Килрогг и другие калечили себя здесь. Это было... жертвой?', 12, 0, 100, 0, 0, 50309, 93669, 0, 0, 0, 'Ариок to Player');
INSERT INTO `creature_text` VALUES (91421, 2, 0, 'Используй глаз! Я не дам духам атаковать тебя.', 12, 0, 100, 0, 0, 50311, 93649, 0, 0, 0, 'Ариок to Player');
INSERT INTO `creature_text` VALUES (91430, 1, 0, 'Останься с нами... навечно...', 12, 0, 100, 0, 0, 52903, 100972, 0, 0, 0, 'Рыдающая банши to Player');
INSERT INTO `creature_text` VALUES (91431, 1, 0, 'Тебе здесь не место.', 12, 0, 100, 0, 0, 52727, 129799, 0, 0, 0, 'Призрачная студентка to Player');
INSERT INTO `creature_text` VALUES (91431, 1, 0, 'Этот сад не для таких, как ты.', 12, 0, 100, 0, 0, 52728, 93048, 0, 0, 0, 'Призрачная студентка to Player');
INSERT INTO `creature_text` VALUES (91460, 1, 0, 'I am revealed. Die!', 12, 0, 100, 0, 0, 0, 94144, 0, 0, 0, 'Шпион Швейдер');
INSERT INTO `creature_text` VALUES (91462, 3, 0, 'Душа нашего мира бьется в агонии...', 12, 0, 100, 1, 0, 90799, 139280, 3081, 3081, 0, 'Малфурион Ярость Бури to Player');
INSERT INTO `creature_text` VALUES (91465, 0, 0, 'Follow me.', 12, 0, 100, 0, 0, 52741, 93565, 0, 0, 0, 'Малфурион Ярость Бури to Player');
INSERT INTO `creature_text` VALUES (91465, 1, 0, 'Ах, Валь\'шара...', 12, 0, 100, 1, 0, 52735, 95552, 0, 0, 0, 'Малфурион Ярость Бури to Малфурион Ярость Бури');
INSERT INTO `creature_text` VALUES (91465, 2, 0, 'С этим лесом связано столько воспоминаний...', 12, 0, 100, 0, 0, 52358, 93557, 0, 0, 0, 'Малфурион Ярость Бури to Малфурион Ярость Бури');
INSERT INTO `creature_text` VALUES (91465, 3, 0, 'Друиды некогда создали Валь\'шару как отражение Изумрудного Сна.', 12, 0, 100, 0, 0, 52359, 93558, 0, 0, 0, 'Малфурион Ярость Бури to Малфурион Ярость Бури');
INSERT INTO `creature_text` VALUES (91465, 4, 0, 'Этот край – настоящий отзвук Изумрудного Сна в нашем мире.', 12, 0, 100, 0, 0, 52360, 93560, 0, 0, 0, 'Малфурион Ярость Бури to Малфурион Ярость Бури');
INSERT INTO `creature_text` VALUES (91465, 5, 0, 'Приготовься, вскоре мы предстанем перед Владыкой Леса.', 12, 0, 100, 0, 0, 52277, 95284, 0, 0, 0, 'Малфурион Ярость Бури to Малфурион Ярость Бури');
