INSERT INTO `creature_text` VALUES (91481, 1, 0, 'The grapple point is on the top of the wall over there. Follow me!', 12, 0, 100, 25, 0, 0, 113879, 0, 0, 0, 'Командир Лорна Краули to Player');
INSERT INTO `creature_text` VALUES (91517, 1, 0, 'Мы повелеваем штормом!', 12, 0, 100, 0, 0, 51618, 94002, 0, 0, 0, 'Рассекатель туч Рейкир to Player');
INSERT INTO `creature_text` VALUES (91519, 0, 0, 'Сюда, скорее! Пока драконы не учуяли твой запах!', 12, 0, 100, 0, 0, 53020, 98416, 0, 0, 0, 'Командир Лорна Краули to Player');
INSERT INTO `creature_text` VALUES (91520, 0, 0, 'Послушники! Ваша работа еще не выполнена!', 14, 0, 100, 0, 0, 0, 93876, 0, 0, 0, 'Адъюнкт Куро to Player');
INSERT INTO `creature_text` VALUES (91520, 1, 0, 'Я же должна была стать... бессмертной...', 12, 0, 100, 0, 0, 0, 93881, 0, 0, 0, 'Адъюнкт Куро to Player');
INSERT INTO `creature_text` VALUES (91521, 0, 0, 'I am the will of the Punisher ... and your doom.', 12, 0, 100, 0, 0, 0, 93879, 0, 0, 0, 'Воздаятель Браму to Player');
INSERT INTO `creature_text` VALUES (91522, 0, 0, 'Дальше ты не пройдешь!', 14, 0, 100, 0, 0, 0, 103319, 0, 0, 0, 'Заступник Баджунт to Player');
INSERT INTO `creature_text` VALUES (91531, 0, 0, 'Спасибо, $gиноземец:чужестранка;. Следуй за мной, и я посвящу тебя в свой план!', 12, 0, 100, 0, 0, 0, 99145, 0, 0, 0, 'Кольборн Недостойный to Player');
INSERT INTO `creature_text` VALUES (91538, 0, 0, 'I will not let you take this!', 12, 0, 100, 0, 0, 0, 93610, 0, 0, 0, 'Призрачный хранитель глаз to Player');
INSERT INTO `creature_text` VALUES (91538, 0, 1, 'Я должен защитить нашу священную землю...', 12, 0, 100, 0, 0, 0, 93609, 0, 0, 0, 'Призрачный хранитель глаз to Player');
INSERT INTO `creature_text` VALUES (91538, 0, 2, 'Умри, $gосквернитель:осквернительница;!', 12, 0, 100, 0, 0, 0, 93611, 0, 0, 0, 'Призрачный хранитель глаз to Player');
INSERT INTO `creature_text` VALUES (91538, 1, 1, 'Ты заплатишь за это, $gосквернитель:осквернительница;...', 12, 0, 100, 0, 0, 0, 94361, 0, 0, 0, 'Призрачный хранитель глаз to Player');
INSERT INTO `creature_text` VALUES (91538, 1, 2, 'Я еще вернусь...', 12, 0, 100, 0, 0, 0, 94362, 0, 0, 0, 'Призрачный хранитель глаз to Player');
INSERT INTO `creature_text` VALUES (91538, 1, 3, 'I let my leader down ...', 12, 0, 100, 0, 0, 0, 94360, 0, 0, 0, 'Призрачный хранитель глаз to Player');
INSERT INTO `creature_text` VALUES (91542, 0, 0, 'Ваша священная земля осквернена, Килрогг. Но не волнуйся, я принял меры.', 12, 0, 100, 0, 0, 50943, 93634, 0, 0, 0, 'Гул\'дан to Килрогг Мертвый Глаз');
INSERT INTO `creature_text` VALUES (91544, 0, 0, 'Вот это другое дело! Дайте-ка еще залп этой твари прямо в морду.', 14, 0, 100, 0, 0, 0, 93616, 0, 0, 0, 'Капитан Слепой Глаз');
INSERT INTO `creature_text` VALUES (91544, 0, 0, 'Отлично, парни! Вы спасли вашего любимого капитана и... эй, вы что творите?!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Капитан Слепой Глаз to Флог Пожиратель Капитанов');
INSERT INTO `creature_text` VALUES (91544, 0, 0, 'Соберитесь, вы, шелудивые псы! Вы что, позволите этому громиле-великану утащить ваши честно награбленные сокровища на дно морское?!', 14, 0, 100, 0, 0, 0, 93612, 0, 0, 0, 'Капитан Слепой Глаз to Флог Пожиратель Капитанов');
INSERT INTO `creature_text` VALUES (91544, 3, 0, 'Отлично, ребята! Вы спасли вашего любимого капитана и... эй, вы куда?!', 14, 0, 100, 0, 0, 0, 93620, 23469, 23469, 0, 'Капитан Слепой Глаз to Флог Пожиратель Капитанов');
INSERT INTO `creature_text` VALUES (91544, 3, 0, 'Стоять насмерть! Убейте эту тварь, не то мы все окажемся у нее в брюхе.', 14, 0, 100, 0, 0, 0, 93617, 33825, 33825, 0, 'Капитан Слепой Глаз');
INSERT INTO `creature_text` VALUES (91553, 0, 0, 'Сковальд пока не победил. Отберем у него драконов!', 12, 0, 100, 0, 0, 53021, 98417, 0, 0, 0, 'Командир Лорна Краули to Player');
INSERT INTO `creature_text` VALUES (91556, 3, 0, 'You are too late, weakling! Even now, we break the dragons to serve us!', 12, 0, 100, 0, 0, 51617, 94515, 6164, 6164, 0, 'Король-Бог Сковальд to Player');
INSERT INTO `creature_text` VALUES (91558, 0, 0, 'Ты иди дальше, а я вернусь к Седогриву. Удачи, $n!', 12, 0, 100, 25, 0, 53198, 98747, 0, 0, 0, 'Командир Лорна Краули to Player');
INSERT INTO `creature_text` VALUES (91565, 1, 0, '$gИзбранник:Избранница; Хранителя бурь $gприбыл:прибыла;. $n, стихии не потревожат тебя.', 12, 0, 100, 0, 0, 0, 113170, 0, 0, 0, 'Беснующаяся буря to Player');
INSERT INTO `creature_text` VALUES (91565, 4, 0, 'У тебя оружие нашего господина, $n. Буря не потревожит тебя.', 12, 0, 100, 0, 0, 0, 113173, 6905, 6905, 0, 'Беснующаяся буря to Player');
INSERT INTO `creature_text` VALUES (91566, 1, 0, 'Драконы служат Сковальду, червь!', 12, 0, 100, 0, 0, 51713, 94005, 0, 0, 0, 'Сквернскорнский палач to Player');
INSERT INTO `creature_text` VALUES (91566, 1, 0, 'Моли о пощаде!', 12, 0, 100, 0, 0, 52468, 94006, 0, 0, 0, 'Сквернскорнский палач to Player');
INSERT INTO `creature_text` VALUES (91566, 1, 0, 'Покорись Королю-богу!', 12, 0, 100, 0, 0, 51715, 94007, 0, 0, 0, 'Сквернскорнский палач to Player');
INSERT INTO `creature_text` VALUES (91570, 2, 0, 'Попроси Ветира отвезти тебя на вершину!', 12, 0, 100, 0, 0, 53202, 98752, 9446, 9446, 0, 'Тримьярис to Player');
INSERT INTO `creature_text` VALUES (91570, 3, 0, 'I am free! Thank you!', 12, 0, 100, 0, 0, 53201, 98750, 2148, 2148, 0, 'Тримьярис to Player');
INSERT INTO `creature_text` VALUES (91571, 1, 0, 'Ты меня не остановишь!', 12, 0, 100, 0, 0, 51732, 135483, 0, 0, 0, 'Тан Диконеб');
INSERT INTO `creature_text` VALUES (91571, 3, 0, 'Ешьте, детки! Нам еще ведьму искать!', 14, 0, 100, 0, 0, 51732, 94082, 9646, 9646, 0, 'Тан Диконеб to Player');
INSERT INTO `creature_text` VALUES (91571, 4, 0, 'Следопыты Натаноса выпускают |TInterface\\Icons\\inv_ammo_arrow_04:20|t |cFFFF0404|Hspell:182466|h[чумные стрелы]|h|r. Заманите под них тана Диконеба!', 42, 0, 100, 0, 0, 0, 95598, 20503, 20503, 0, 'Тан Диконеб to Player');
INSERT INTO `creature_text` VALUES (91573, 0, 0, 'Килрогг... в цитадели. Я должен убить его… пока еще… не поздно…', 12, 0, 100, 15, 0, 50312, 93652, 0, 0, 0, 'Ариок to Player');
INSERT INTO `creature_text` VALUES (91575, 1, 0, 'Туманы заберут тебя!', 12, 0, 100, 0, 0, 52495, 98104, 0, 0, 0, 'Квалдир - раздиратель душ to Player');
INSERT INTO `creature_text` VALUES (91575, 1, 0, 'Ты не уйдешь!', 12, 0, 100, 0, 0, 52491, 98095, 0, 0, 0, 'Квалдир - раздиратель душ to Недостойная душа');
INSERT INTO `creature_text` VALUES (91575, 1, 0, 'Умри в агонии!', 12, 0, 100, 0, 0, 52496, 98103, 0, 0, 0, 'Квалдир - раздиратель душ to Player');
INSERT INTO `creature_text` VALUES (91575, 2, 0, 'Моя война... вечна...', 12, 0, 100, 0, 0, 52492, 98108, 0, 0, 0, 'Квалдир - раздиратель душ to Player');
INSERT INTO `creature_text` VALUES (91575, 2, 0, 'Проклятие... спало?', 12, 0, 100, 0, 0, 52493, 98107, 0, 0, 0, 'Квалдир - раздиратель душ to Player');
INSERT INTO `creature_text` VALUES (91575, 2, 0, 'Я возвращаюсь в туманы...', 12, 0, 100, 0, 0, 52499, 98105, 0, 0, 0, 'Квалдир - раздиратель душ to Player');
INSERT INTO `creature_text` VALUES (91575, 3, 0, 'Я искупаюсь в твоей крови!', 12, 0, 100, 0, 0, 51704, 102890, 1411, 1411, 0, 'Квалдир - раздиратель душ to Player');
INSERT INTO `creature_text` VALUES (91575, 4, 0, 'Хелия, прими меня...', 12, 0, 100, 0, 0, 52489, 98106, 12223, 12223, 0, 'Квалдир - раздиратель душ to Player');
INSERT INTO `creature_text` VALUES (91580, 3, 0, 'I cannot ... long ... hold him back.', 12, 0, 100, 0, 0, 0, 93667, 6421, 6421, 0, 'Тейд Шумакер to Player');
INSERT INTO `creature_text` VALUES (91580, 4, 0, 'Мариус, твоя гордыня когда-нибудь сыграет с тобой злую шутку. Ты меня чуть не угробил.', 12, 0, 100, 0, 0, 0, 93666, 18453, 18453, 0, 'Тейд Шумакер to Владыка судеб Казрок');
INSERT INTO `creature_text` VALUES (91580, 4, 0, 'Шевелись давай! Долго мне его не удержать!', 12, 0, 100, 0, 0, 0, 96098, 34473, 34473, 0, 'Тейд Шумакер to Владыка судеб Казрок');
INSERT INTO `creature_text` VALUES (91590, 0, 0, 'Ну разумеется, сработало! Я оживляла и таких, кто был мертв втрое дольше и вдвое сильнее. Так что с $gэтим:этой; все было проще простого!', 12, 0, 100, 0, 0, 0, 98741, 0, 0, 0, 'Аптекарь Визерс to Player');
INSERT INTO `creature_text` VALUES (91593, 1, 0, 'Слишком поздно! Они уже здесь!', 12, 0, 100, 0, 0, 0, 94995, 0, 0, 0, 'Железный матрос to Player');
INSERT INTO `creature_text` VALUES (91593, 3, 0, 'Я должен был уплыть на следующем корабле!', 12, 0, 100, 0, 0, 0, 94996, 37793, 37793, 0, 'Железный матрос to Player');
INSERT INTO `creature_text` VALUES (91595, 1, 0, 'Обороняйте берег. Они хотят остановить нас!', 12, 0, 100, 0, 0, 0, 95002, 0, 0, 0, 'Резервист Железной Орды to Player');
INSERT INTO `creature_text` VALUES (91596, 3, 0, 'Влачить жалкое существование недостойно вас. Гул\'дан приведет нас к победе. Еще не поздно!', 14, 0, 100, 274, 0, 0, 93685, 153939, 153939, 0, 'Скверноподданный пророк');
INSERT INTO `creature_text` VALUES (91596, 3, 0, 'Мощь Легиона велика. Вы не будете знать страха. Вас не будут терзать сомнения. Все ясно, как день!', 14, 0, 100, 0, 0, 0, 93682, 475611, 475611, 0, 'Скверноподданный пророк');
INSERT INTO `creature_text` VALUES (91596, 3, 0, 'Ничто не остановит нас!', 12, 0, 100, 0, 0, 0, 93688, 495460, 495460, 0, 'Скверноподданный пророк');
INSERT INTO `creature_text` VALUES (91597, 1, 0, 'Не дайте им уничтожить корабли!', 12, 0, 100, 0, 0, 0, 95003, 0, 0, 0, 'Железная копейщица to Player');
INSERT INTO `creature_text` VALUES (91598, 0, 0, 'Ahh... curse you!', 12, 0, 100, 0, 0, 52880, 93819, 0, 0, 0, 'Искусительница из племени Каменной Крови to Player');
INSERT INTO `creature_text` VALUES (91598, 0, 1, 'Flay the flesh and scatter the bones!', 12, 0, 100, 0, 0, 52332, 93816, 0, 0, 0, 'Искусительница из племени Каменной Крови to Player');
INSERT INTO `creature_text` VALUES (91598, 0, 3, 'Fool! Die!', 12, 0, 100, 0, 0, 52879, 93814, 0, 0, 0, 'Искусительница из племени Каменной Крови to Player');
INSERT INTO `creature_text` VALUES (91598, 0, 4, 'Crrggghaaawww!', 12, 0, 100, 0, 0, 52336, 93822, 0, 0, 0, 'Искусительница из племени Каменной Крови to Player');
INSERT INTO `creature_text` VALUES (91598, 2, 2, 'We will not be stopped!', 12, 0, 100, 0, 0, 52878, 93820, 0, 0, 0, 'Искусительница из племени Каменной Крови to Player');
INSERT INTO `creature_text` VALUES (91601, 1, 0, 'Вам никогда не победить нас. Имя нам - Легион!', 12, 0, 100, 0, 0, 0, 93782, 0, 0, 0, 'Скверноподданный жрец душ to Player');
INSERT INTO `creature_text` VALUES (91601, 1, 0, 'Во славу Гул\'дана!', 12, 0, 100, 0, 0, 0, 95028, 0, 0, 0, 'Скверноподданный жрец душ to Player');
INSERT INTO `creature_text` VALUES (91601, 1, 0, 'За Гул\'дана!', 12, 0, 100, 0, 0, 0, 109589, 0, 0, 0, 'Скверноподданный жрец душ to Player');
INSERT INTO `creature_text` VALUES (91601, 3, 0, 'Вы даже не представляете, какая это мощь.', 12, 0, 100, 1, 0, 0, 93748, 114847, 114847, 0, 'Скверноподданный жрец душ');
INSERT INTO `creature_text` VALUES (91601, 3, 0, 'Доверьтесь Гул\'дану. Он с самого начала знал, что следовал единственно верным путем.', 12, 0, 100, 1, 0, 0, 93749, 106388, 106388, 0, 'Скверноподданный жрец душ');
INSERT INTO `creature_text` VALUES (91601, 3, 0, 'Скверна – не проклятие. Это дар. Чудесный и великий дар!', 12, 0, 100, 274, 0, 0, 93751, 94299, 94299, 0, 'Скверноподданный жрец душ');
INSERT INTO `creature_text` VALUES (91601, 3, 0, 'Твоя судьба предрешена!', 12, 0, 100, 5, 0, 0, 116703, 39462, 39462, 0, 'Скверноподданный жрец душ to Player');
INSERT INTO `creature_text` VALUES (91601, 3, 0, 'Теперь пришло наше время. Гул\'дан спасет нас. Наше могущество не будет иметь границ!', 12, 0, 100, 5, 0, 0, 93750, 190180, 190180, 0, 'Скверноподданный жрец душ');
INSERT INTO `creature_text` VALUES (91604, 0, 0, '|TInterface\\Icons\\spell_fel_elementaldevastation.blp:20|t|cFFFF0000|Hspell:181498|h[\"Волна разрушения\"]|h|r ударяет в |cFFFF0000|Hspell:179620|h[кристалл Скверны]|h|r!', 16, 0, 100, 0, 0, 0, 94031, 0, 0, 0, 'Кристалл Скверны to Кристалл Скверны');
INSERT INTO `creature_text` VALUES (91629, 3, 0, '$n! Вижу, тебе удалось получить клинки Варедиса Оскверненной Души. Будем надеяться, что ты лучше сможешь обуздать своего внутреннего демона.', 12, 0, 100, 1, 0, 0, 122083, 1318543, 2001566, 0, 'Иллидарская карательница to Player');
INSERT INTO `creature_text` VALUES (91650, 3, 0, 'Помоги мне выбраться из клетки! Нам грозит страшная опасность!', 12, 0, 100, 0, 0, 0, 102523, 5103, 5103, 0, 'Эвель Шепот Ночи to Player');
INSERT INTO `creature_text` VALUES (91651, 0, 0, 'Малфурион! Спасибо Элуне!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (91651, 1, 0, 'Поторопись! Что-то случилось!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (91651, 2, 0, 'Он отправился в Сон, но не может из него выйти.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (91651, 3, 0, 'Мы пока и сами не знаем.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (91657, 0, 0, 'Твоя душа теперь принадлежит Хелии, ничтожество!', 12, 0, 100, 0, 0, 0, 99685, 0, 0, 0, 'Кровобород to Player');
INSERT INTO `creature_text` VALUES (91657, 0, 1, 'I will cut you in two!', 12, 0, 100, 0, 0, 0, 99687, 0, 0, 0, 'Кровобород to 0');
INSERT INTO `creature_text` VALUES (91657, 2, 0, 'Захлебнись и отправляйся на дно!', 12, 0, 100, 0, 0, 0, 99686, 0, 0, 0, 'Кровобород to 0');
INSERT INTO `creature_text` VALUES (91657, 4, 0, 'Никто не ускользнет... из рук Хелии...', 12, 0, 100, 0, 0, 0, 99689, 0, 0, 0, 'Кровобород to Player');
INSERT INTO `creature_text` VALUES (91658, 0, 0, 'Не стоит нам здесь задерживаться. Я возвращаюсь в лагерь Актара.', 15, 0, 100, 1, 0, 0, 93791, 0, 0, 0, 'Решад to Player');
INSERT INTO `creature_text` VALUES (91695, 0, 0, 'Благодаря тебе я избавлен от необходимости вырезать слабаков – спасибо! Иди сюда, я тебе устрою хорошую драку!', 14, 0, 100, 0, 0, 0, 93921, 0, 0, 0, 'Главный чернокнижник Пустоклят');
INSERT INTO `creature_text` VALUES (91695, 1, 0, 'Скоро ты на коленях будешь молить о смерти!', 12, 0, 100, 0, 0, 0, 93922, 0, 0, 0, 'Главный чернокнижник Пустоклят to Player');
INSERT INTO `creature_text` VALUES (91701, 3, 0, 'I owe you my life!', 12, 0, 100, 5, 0, 0, 115681, 73603, 73603, 183352, 'Пленник из Львиного дозора to Player');
INSERT INTO `creature_text` VALUES (91710, 0, 0, 'ЗА АЗЕРОТ!', 14, 0, 100, 0, 0, 50632, 135754, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (91710, 0, 0, 'Мы так легко не сдадимся!', 14, 0, 100, 0, 0, 50404, 93605, 0, 0, 0, 'Верховный маг Кадгар to Бес адского огня');
INSERT INTO `creature_text` VALUES (91710, 0, 0, 'С последним вздохом Кадгар высвобождает мощнейший взрыв маны, убивая вокруг все живое.', 42, 0, 100, 0, 0, 0, 96187, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (91711, 1, 0, 'I\'m coming back...', 12, 0, 100, 2, 0, 0, 93877, 0, 0, 0, 'Воздаятель Браму to Адъюнкт Куро');
INSERT INTO `creature_text` VALUES (91712, 0, 0, 'И снова в атаку!', 14, 0, 100, 2, 0, 0, 93878, 0, 0, 0, 'Заступник Баджунт to Адъюнкт Куро');
INSERT INTO `creature_text` VALUES (91721, 0, 0, 'Мы уже идем!', 14, 0, 100, 0, 0, 0, 95102, 0, 0, 0, 'Инженер кузни to Палач Рилот');
INSERT INTO `creature_text` VALUES (91721, 1, 0, 'А? А что это ты здесь делаешь?', 12, 0, 100, 0, 0, 0, 94944, 0, 0, 183793, 'Инженер кузни to Player');
INSERT INTO `creature_text` VALUES (91721, 1, 0, 'Лазутчик в лагере!', 12, 0, 100, 0, 0, 0, 94943, 0, 0, 0, 'Инженер кузни to Player');
INSERT INTO `creature_text` VALUES (91721, 1, 0, 'Молот мой – а рожа твоя!', 12, 0, 100, 0, 0, 0, 94942, 0, 0, 0, 'Инженер кузни to Player');
INSERT INTO `creature_text` VALUES (91721, 1, 0, 'Не дергай меня, когда я работаю!', 12, 0, 100, 0, 0, 0, 94946, 0, 0, 0, 'Инженер кузни to Player');
INSERT INTO `creature_text` VALUES (91721, 1, 0, 'Не подпускайте их к оборудованию!', 12, 0, 100, 0, 0, 0, 94941, 0, 0, 158266, 'Инженер кузни to Player');
INSERT INTO `creature_text` VALUES (91721, 3, 0, 'Да, палач!', 14, 0, 100, 0, 0, 0, 95101, 44, 44, 0, 'Инженер кузни to Палач Рилот');
INSERT INTO `creature_text` VALUES (91727, 1, 0, 'Да! Сражайся за свою жалкую жизнь, $gсмертный:смертная;! Меня это позабавит!', 12, 0, 100, 0, 0, 0, 93900, 0, 0, 0, 'Палач Рилот to Player');
INSERT INTO `creature_text` VALUES (91727, 1, 0, 'За свое вмешательство ты дорого поплатишься!', 12, 0, 100, 0, 0, 0, 93902, 0, 0, 0, 'Палач Рилот to Player');
INSERT INTO `creature_text` VALUES (91727, 4, 0, 'I will enjoy your heart!', 12, 0, 100, 0, 0, 0, 93901, 0, 55651, 0, 'Палач Рилот to Player');
INSERT INTO `creature_text` VALUES (91727, 4, 0, 'Если Сквернобот не будет закончен к сроку, я из вас из всех душу выну!', 12, 0, 100, 0, 0, 0, 93899, 54773, 54773, 0, 'Палач Рилот');
INSERT INTO `creature_text` VALUES (91727, 4, 0, 'Инженеры! Атакуйте их! Живо!', 14, 0, 100, 0, 0, 0, 95103, 14746, 73778, 184896, 'Палач Рилот');
INSERT INTO `creature_text` VALUES (91727, 4, 0, 'Ничего еще не кончено!..', 12, 0, 100, 0, 0, 0, 93903, 48976, 48976, 0, 'Палач Рилот to Player');
INSERT INTO `creature_text` VALUES (91727, 4, 0, 'Я вернусь... Я... еще отомщу...', 12, 0, 100, 0, 0, 0, 93904, 86097, 86097, 0, 'Палач Рилот to Player');
INSERT INTO `creature_text` VALUES (91731, 3, 0, '$n! У меня срочное сообщение.', 12, 0, 100, 1, 0, 60553, 99095, 300937, 300937, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (91731, 3, 0, 'Шаман! У меня срочное сообщение.', 12, 0, 100, 1, 0, 60553, 99095, 8976, 8976, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (91737, 1, 0, 'Сковальд завладеет Эгидой. И тебе ему не помешать!', 12, 0, 100, 0, 0, 0, 94513, 0, 0, 0, 'Азария to Player');
INSERT INTO `creature_text` VALUES (91737, 2, 0, 'Жалкое ничтожество... тебе все равно не остановить Короля-бога...', 12, 0, 100, 0, 0, 0, 94514, 0, 0, 0, 'Азария to Player');
INSERT INTO `creature_text` VALUES (91740, 0, 0, 'Хочешь добраться до Кровожада? Сначала одолей меня!', 12, 0, 100, 0, 0, 0, 94702, 0, 0, 0, 'Адепт Ватруста to Player');
INSERT INTO `creature_text` VALUES (91740, 1, 0, 'Ты не остановишь ни его... ни Пылающий Легион...', 12, 0, 100, 0, 0, 0, 94703, 0, 0, 0, 'Адепт Ватруста to Player');
INSERT INTO `creature_text` VALUES (91755, 3, 0, 'No, please! No!', 12, 0, 100, 0, 0, 47208, 93910, 2037, 2037, 0, 'Оргриммарский солдат to Player');
INSERT INTO `creature_text` VALUES (91755, 3, 0, 'Нет, прошу! Нет!', 12, 0, 100, 0, 0, 47208, 93910, 6846, 6846, 0, 'Оргриммарский солдат to Player');
INSERT INTO `creature_text` VALUES (91756, 1, 0, 'За Азшару!', 12, 0, 100, 0, 0, 52729, 93050, 0, 0, 0, 'Вечный страж to Player');
INSERT INTO `creature_text` VALUES (91756, 1, 0, 'Тебе здесь не место.', 12, 0, 100, 0, 0, 52727, 129799, 0, 0, 0, 'Вечный страж to Player');
INSERT INTO `creature_text` VALUES (91756, 1, 0, 'Этот сад не для таких, как ты.', 12, 0, 100, 0, 0, 52728, 93048, 0, 0, 0, 'Вечный страж to Player');
INSERT INTO `creature_text` VALUES (91757, 1, 0, 'Этот сад не для таких, как ты.', 12, 0, 100, 0, 0, 52728, 93048, 0, 0, 0, 'Вечный жених to Player');
INSERT INTO `creature_text` VALUES (91759, 1, 0, 'Драконы покорятся или умрут!', 12, 0, 100, 0, 0, 52404, 94511, 0, 0, 0, 'Сквернскорнский усмиритель to Player');
INSERT INTO `creature_text` VALUES (91759, 1, 0, 'Торигниры сломлены. Теперь твой черед.', 12, 0, 100, 0, 0, 52525, 94512, 0, 0, 0, 'Сквернскорнский усмиритель to Player');
INSERT INTO `creature_text` VALUES (91760, 1, 0, 'Твоя голова станет отличным подарком Гул\'дану!', 12, 0, 100, 0, 0, 0, 94640, 0, 0, 0, 'Сквернокровный надсмотрщик to Player');
INSERT INTO `creature_text` VALUES (91764, 1, 0, 'А мне доводилось встречать тебя в Железных доках! Вот теперь тебе точно конец!', 12, 0, 100, 0, 0, 0, 93912, 0, 0, 0, 'Сквернокровный остроглаз to Player');
INSERT INTO `creature_text` VALUES (91764, 1, 0, 'Враг на прицеле!', 12, 0, 100, 0, 0, 0, 93914, 0, 0, 0, 'Сквернокровный остроглаз to Player');
INSERT INTO `creature_text` VALUES (91764, 1, 0, 'Еще одно ухо мне на ожерелье!', 12, 0, 100, 0, 0, 0, 93911, 0, 0, 0, 'Сквернокровный остроглаз to Player');
INSERT INTO `creature_text` VALUES (91764, 1, 0, 'Мочи их!', 12, 0, 100, 0, 0, 0, 93915, 0, 0, 0, 'Сквернокровный остроглаз to Player');
INSERT INTO `creature_text` VALUES (91764, 1, 0, 'Открыть огонь!', 12, 0, 100, 0, 0, 0, 146233, 0, 0, 0, 'Сквернокровный остроглаз to Player');
INSERT INTO `creature_text` VALUES (91764, 1, 0, 'Подавить эту цель!', 12, 0, 100, 0, 0, 0, 93917, 0, 0, 0, 'Сквернокровный остроглаз to Player');
INSERT INTO `creature_text` VALUES (91764, 1, 0, 'Прощайся с жизнью!', 12, 0, 100, 0, 0, 0, 112561, 0, 0, 0, 'Сквернокровный остроглаз to Player');
INSERT INTO `creature_text` VALUES (91784, 0, 0, 'Стражи, приготовьтесь! Ничто не может помешать ритуалу!', 14, 0, 100, 0, 0, 0, 101991, 0, 0, 0, 'Полководец Паржеш to Воин из клана Колец Ненависти');
