INSERT INTO `creature_text` VALUES (91808, 1, 0, '|TInterface\\Icons\\ability_rogue_venomouswounds:20|tВы страдаете от |cFFFF0000|Hspell:191855|h[отравленной раны]|h|r!', 42, 0, 100, 0, 0, 0, 98862, 0, 0, 0, 'Змеикс to Player');
INSERT INTO `creature_text` VALUES (91818, 1, 0, 'В этот раз меня не бросят на съедение псам!', 14, 0, 100, 0, 0, 51703, 94134, 0, 0, 0, 'Недостойная душа to Player');
INSERT INTO `creature_text` VALUES (91818, 1, 0, 'Хелира! Ты не можешь держать меня здесь вечно! Я обрету свободу!', 14, 0, 100, 0, 0, 0, 94135, 0, 0, 0, 'Недостойная душа to Player');
INSERT INTO `creature_text` VALUES (91818, 3, 0, 'Все пропало…', 12, 0, 100, 0, 0, 0, 94050, 8539, 8539, 0, 'Недостойная душа');
INSERT INTO `creature_text` VALUES (91818, 3, 0, 'Как только я выберусь отсюда, первого же квалдира в порошок сотру.', 12, 0, 100, 0, 0, 0, 95307, 17170, 17170, 0, 'Недостойная душа');
INSERT INTO `creature_text` VALUES (91830, 1, 0, '|TInterface\\Icons\\warlock_summon_doomguard.blp:20|t|cFFFF0000$n|r поражается |cFFFF0000|Hspell:181275|h[\"Проклятием Легиона\"]|h|r!', 41, 0, 100, 0, 0, 0, 93974, 0, 0, 0, 'Портал демонов to Player');
INSERT INTO `creature_text` VALUES (91832, 1, 0, 'Твоя голова станет отличным трофеем!', 12, 0, 100, 0, 0, 0, 92668, 0, 0, 0, 'Гром\'карский воин to Player');
INSERT INTO `creature_text` VALUES (91858, 0, 0, 'Демоны считают нас легкой добычей. Они узнают, что калдорай не склонятся ни перед кем.', 14, 0, 100, 0, 0, 52206, 96908, 0, 0, 0, 'Кур\'талос Гребень Ворона');
INSERT INTO `creature_text` VALUES (91858, 0, 0, 'Держитесь, защитники! Враг силен, но мы выстоим!', 14, 0, 100, 0, 0, 52209, 96911, 0, 0, 0, 'Кур\'талос Гребень Ворона');
INSERT INTO `creature_text` VALUES (91858, 0, 0, 'Наш народ взывает о помощи! Пусть демоны пожалеют о том, что вторглись в наш мир!', 14, 0, 100, 0, 0, 52205, 96907, 0, 0, 0, 'Кур\'талос Гребень Ворона');
INSERT INTO `creature_text` VALUES (91858, 3, 0, 'Воины, вы поклялись в верности роду Гребня Ворона. Сегодня нас ждет славная битва!', 14, 0, 100, 0, 0, 995, 96909, 136940, 136940, 0, 'Кур\'талос Гребень Ворона');
INSERT INTO `creature_text` VALUES (91858, 3, 0, 'Ко мне, воины! Мы одолеем любого врага и удержим крепость!', 14, 0, 100, 1, 0, 52208, 96910, 26994, 26994, 0, 'Кур\'талос Гребень Ворона');
INSERT INTO `creature_text` VALUES (91860, 1, 0, 'Возвращайся в свой мир, или я пошлю тебя туда по частям!', 12, 0, 100, 1, 0, 52226, 96925, 0, 0, 0, 'Оживленная лучница to Player');
INSERT INTO `creature_text` VALUES (91860, 1, 0, 'Вот и настал твой последний миг!', 12, 0, 100, 0, 0, 52223, 96926, 0, 0, 0, 'Оживленная лучница to Player');
INSERT INTO `creature_text` VALUES (91860, 1, 0, 'Мой лук быстр и точен!', 12, 0, 100, 0, 0, 52221, 96924, 0, 0, 0, 'Оживленная лучница to Player');
INSERT INTO `creature_text` VALUES (91860, 1, 0, 'Стоять! Сперва я пущу тебе кровь!', 12, 0, 100, 0, 0, 52220, 96923, 0, 0, 0, 'Оживленная лучница to Player');
INSERT INTO `creature_text` VALUES (91860, 2, 0, 'Лорд Гребень Ворона, я верно служила тебе.', 12, 0, 100, 1, 0, 52241, 96584, 0, 0, 0, 'Оживленная лучница to Player');
INSERT INTO `creature_text` VALUES (91860, 2, 0, 'Наконец-то покой.', 12, 0, 100, 1, 0, 52226, 96946, 0, 0, 0, 'Оживленная лучница to Player');
INSERT INTO `creature_text` VALUES (91860, 3, 0, 'I do not miss!', 12, 0, 100, 0, 0, 52219, 132435, 80135, 80135, 0, 'Оживленная лучница to Player');
INSERT INTO `creature_text` VALUES (91866, 0, 0, 'Вы, конечно, слышали о $gлегендарном:легендарной; |3-5($p), $gкоторый своими деяниями доказал, что достоин:которая своими деяниями доказала, что достойна; носить Испепелитель и занять в ордене место Тириона в качестве $gВерховного лорда:Верховной леди;. Теперь, когда нас возглавляет $p, мы сможем дать Легиону достойный отпор.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Лорд Максвелл Тиросс to Player');
INSERT INTO `creature_text` VALUES (91866, 1, 0, 'Мы должны все, как один, противостоять надвигающейся тьме. Мы все должны сражаться плечом к плечу, отбросив расовые предрассудки и забыв былые распри. Мы все должны объединиться!', 14, 0, 100, 0, 0, 0, 98559, 0, 0, 0, 'Лорд Максвелл Тиросс to Player');
INSERT INTO `creature_text` VALUES (91866, 2, 0, 'Сегодняшний день стал днем возрождения ордена Серебряной Длани! И вместе мы сокрушим даже самого Саргераса!', 14, 0, 100, 0, 0, 0, 94428, 0, 0, 0, 'Лорд Максвелл Тиросс to Player');
INSERT INTO `creature_text` VALUES (91866, 3, 0, 'Положи клинок на него, когда будешь $gготов:готова;. Сам Свет станет нашей кузней.', 12, 0, 100, 0, 0, 0, 98745, 0, 0, 0, 'Лорд Максвелл Тиросс to Player');
INSERT INTO `creature_text` VALUES (91892, 0, 0, 'Ну и ладно. Асгер, отправь это недоразумение в Хельхейм!', 14, 0, 100, 0, 0, 0, 94526, 0, 0, 0, 'Тан Ирглов Беспощадный to Эрлинг Громорожденный');
INSERT INTO `creature_text` VALUES (91892, 0, 0, 'Ха! Невелика потеря. Кайлер, твой черед. Отомсти за своих павших товарищей!', 14, 0, 100, 0, 0, 0, 94527, 0, 0, 0, 'Тан Ирглов Беспощадный to Асгер Дочь Ярла');
INSERT INTO `creature_text` VALUES (91892, 3, 0, 'Жалкое зрелище. Сперва докажи, что $gдостоин:достойна; привилегии сразиться со мной.', 12, 0, 100, 0, 0, 0, 94524, 6175, 6175, 0, 'Тан Ирглов Беспощадный to Player');
INSERT INTO `creature_text` VALUES (91892, 3, 0, 'Ну что ж, может быть, ты поможешь мне слегка размяться.', 14, 0, 100, 0, 0, 0, 94528, 24832, 24832, 0, 'Тан Ирглов Беспощадный to Скиталец Кайлер');
INSERT INTO `creature_text` VALUES (91894, 0, 0, 'Ты нанесешь первый удар – или так и будешь на меня таращиться? Тряпка!', 12, 0, 100, 0, 0, 0, 122457, 0, 0, 0, 'Скиталец Кайлер to Асгер Дочь Ярла');
INSERT INTO `creature_text` VALUES (91895, 0, 0, 'Come, outsider filth. I will even let you strike the first blow. Make it count.', 12, 0, 100, 0, 0, 0, 122456, 0, 0, 0, 'Асгер Дочь Ярла to Player');
INSERT INTO `creature_text` VALUES (91902, 3, 0, 'Завоевать этот жалкий мирок.', 14, 0, 100, 396, 0, 53517, 99304, 2733, 2733, 0, 'Бруталл to 786');
INSERT INTO `creature_text` VALUES (91904, 3, 0, 'Думаю, она причалила где-то рядом и пошла пешком.', 12, 0, 100, 1, 0, 51730, 94131, 8527, 8527, 0, 'Жуткий капитан Тэттерсейл to Player');
INSERT INTO `creature_text` VALUES (91911, 0, 0, 'Мародеры Кровавой Глазницы. Убьем их главаря, и они сами разбегутся по джунглям!', 12, 0, 100, 0, 0, 50579, 94001, 0, 0, 0, 'Экзарх Ирель to Player');
INSERT INTO `creature_text` VALUES (91911, 1, 0, 'Железная Орда оставила доки. Я думала, будет куда сложнее захватить их.', 12, 0, 100, 396, 0, 50578, 94000, 0, 0, 0, 'Экзарх Ирель to Player');
INSERT INTO `creature_text` VALUES (91911, 2, 0, 'Чернокнижники... Если мы не остановим Гул\'дана, он сожжет весь мир!', 12, 0, 100, 0, 0, 50582, 94025, 0, 0, 0, 'Экзарх Ирель to Player');
INSERT INTO `creature_text` VALUES (91911, 3, 0, 'Магия Гул\'дана оскверняет даже боевые машины Железной Орды! Придется прорываться…', 12, 0, 100, 396, 0, 50583, 94029, 0, 0, 0, 'Экзарх Ирель to Player');
INSERT INTO `creature_text` VALUES (91913, 0, 0, 'Добро пожаловать, командир! Кадгар рассказал о твоих подвигах…', 12, 0, 100, 1, 0, 50572, 95744, 0, 0, 0, 'Экзарх Ирель to Player');
INSERT INTO `creature_text` VALUES (91913, 1, 0, 'Преклони колено, герой. Немногие удостаиваются благословения наару.', 12, 0, 100, 2, 0, 50573, 95745, 0, 0, 0, 'Экзарх Ирель to Player');
INSERT INTO `creature_text` VALUES (91913, 2, 0, 'Что это было? Наару говорил с тобой?', 12, 0, 100, 6, 0, 50574, 95752, 0, 0, 0, 'Экзарх Ирель to Player');
INSERT INTO `creature_text` VALUES (91919, 1, 0, 'Воры!', 12, 0, 100, 0, 0, 0, 91152, 0, 0, 0, 'Отмеченный апекситовый призрак ветра to Player');
INSERT INTO `creature_text` VALUES (91919, 1, 0, 'Вы умрете как изгои!', 12, 0, 100, 0, 0, 0, 91161, 0, 0, 0, 'Отмеченный апекситовый призрак ветра to Player');
INSERT INTO `creature_text` VALUES (91919, 1, 0, 'Жалкий земляной червяк!', 12, 0, 100, 0, 0, 0, 91165, 0, 0, 0, 'Отмеченный апекситовый призрак ветра to Player');
INSERT INTO `creature_text` VALUES (91919, 1, 0, 'Изгнать нарушителей из наших владений!', 12, 0, 100, 0, 0, 0, 91160, 0, 0, 0, 'Отмеченный апекситовый призрак ветра to Player');
INSERT INTO `creature_text` VALUES (91919, 1, 0, 'Мерзкие грязнокровки!', 12, 0, 100, 0, 0, 0, 91155, 0, 0, 0, 'Отмеченный апекситовый призрак ветра to Player');
INSERT INTO `creature_text` VALUES (91919, 1, 0, 'Ты – язва на теле мира.', 12, 0, 100, 0, 0, 0, 91164, 0, 0, 0, 'Отмеченный апекситовый призрак ветра to Player');
INSERT INTO `creature_text` VALUES (91919, 1, 0, 'Ты сгоришь с изгоями!', 12, 0, 100, 0, 0, 0, 91166, 0, 0, 0, 'Отмеченный апекситовый призрак ветра to Player');
INSERT INTO `creature_text` VALUES (91919, 1, 0, 'Это не твоя битва, $gстранник:странница;.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Отмеченный апекситовый призрак ветра to Player');
INSERT INTO `creature_text` VALUES (91929, 0, 0, 'Ты только посмотри на это место!', 12, 0, 100, 0, 0, 0, 94047, 0, 0, 0, 'Экзарх Найель to Player');
INSERT INTO `creature_text` VALUES (91935, 0, 0, 'Иди, командир. Мы продолжим наше наступление.', 12, 0, 100, 0, 0, 50435, 96627, 0, 0, 0, 'Экзарх Маладаар to Player');
INSERT INTO `creature_text` VALUES (91949, 2, 0, '', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Гномреганская ремонтница to Крос');
INSERT INTO `creature_text` VALUES (91951, 0, 0, 'Свет... меня... аа-а! Аа-а-а-а-а!', 14, 0, 100, 0, 0, 53455, 99240, 0, 0, 0, 'Верховный лорд Тирион Фордринг to 786');
INSERT INTO `creature_text` VALUES (91982, 1, 0, '|cFFFF0000|Hspell:182582|h[\"Испепеление Скверной\"]|h|r преследует вас!', 42, 0, 100, 0, 0, 0, 95091, 0, 0, 185747, 'Цель луча Скверны to Player');
